        document.getElementById('slider-x-upper').addEventListener('input', (e) => {
            const upper = parseInt(e.target.value);
            const lower = parseInt(document.getElementById('slider-x-lower').value);

            if (upper <= lower) {
                document.getElementById('slider-x-lower').value = upper - 1;
                document.getElementById('span-x-lower-value').textContent = upper - 1;
            }

            document.getElementById('span-x-upper-value').textContent = upper;
            updatePlot();
        });

        function updateBranchSelector() {
            if (currentView === 'sub' && currentSubbandType !== 'full') {
                document.getElementById('branch-selector-group').classList.remove('disabled', 'hidden');
            } else {
                document.getElementById('branch-selector-group').classList.add('disabled', 'hidden');
            }
        }

        // Initial plot
        updatePlot();
    </script>
</body>
