    <script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <script type="importmap">
        {
            "imports": {
                "three": "https://cdn.jsdelivr.net/npm/three@0.163.0/build/three.module.js",
                "three/addons/": "https://cdn.jsdelivr.net/npm/three@0.163.0/examples/jsm/"
            }
        }
    </script>

    <script type="module">
        import * as THREE from 'three';
        import { OrbitControls } from 'three/addons/controls/OrbitControls.js';

        let scene, camera, renderer, controls;
        let radiationMesh, parentObject, boxHelper;
        let currentWaveType = 'P';
        let compassGroup;

        // Initialize Three.js scene
        function init() {
            const container = document.getElementById('canvas-container');

            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            // Camera
            camera = new THREE.PerspectiveCamera(75, container.clientWidth / container.clientHeight, 0.1, 1000);
            camera.position.set(5, 5, 5);
            camera.lookAt(0, 0, 0);

            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(container.clientWidth, container.clientHeight);
            container.appendChild(renderer.domElement);

            // Controls
            controls = new OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);

            const directionalLight1 = new THREE.DirectionalLight(0xffffff, 0.4);
            directionalLight1.position.set(5, 5, 5);
            scene.add(directionalLight1);

            const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.4);
            directionalLight2.position.set(-5, -5, -5);
            scene.add(directionalLight2);

            // Parent object for radiation pattern
            parentObject = new THREE.Object3D();
            scene.add(parentObject);

            // Create initial radiation pattern
            createRadiationPattern();

            // Create compass
            createCompass();

            // Up arrow
            const upArrow = new THREE.ArrowHelper(
                new THREE.Vector3(0, 0, 1),
                new THREE.Vector3(0, 0, 0),
                1.5,
                0x000000
            );
            scene.add(upArrow);

            // Create sprite for 'U' label
            const canvas = document.createElement('canvas');
            const context = canvas.getContext('2d');
            canvas.width = 64;
            canvas.height = 64;
            context.font = '48px Arial';
            context.fillStyle = 'black';
            context.textAlign = 'center';
            context.textBaseline = 'middle';
            context.fillText('U', 32, 32);

            const texture = new THREE.CanvasTexture(canvas);
            const spriteMaterial = new THREE.SpriteMaterial({ map: texture });
            const sprite = new THREE.Sprite(spriteMaterial);
            sprite.position.set(0, 0, 1.7);
            sprite.scale.set(0.5, 0.5, 1);
            scene.add(sprite);

            // Box helper
            boxHelper = new THREE.BoxHelper(parentObject, 0x000000);
            boxHelper.visible = false;
            scene.add(boxHelper);

            // Handle window resize
            window.addEventListener('resize', onWindowResize);
        }

        function createRadiationPattern() {
            if (radiationMesh) {
                parentObject.remove(radiationMesh);
                radiationMesh.geometry.dispose();
                radiationMesh.material.dispose();
            }

            const geometry = new THREE.ParametricGeometry((u, v, target) => {
                const theta = u * Math.PI;
                const phi = v * 2 * Math.PI;

                let r;
                switch (currentWaveType) {
                    case 'P':
                        r = Math.abs(Math.sin(2 * theta) * Math.sin(phi));
                        break;
                    case 'SV':
                        r = Math.abs(Math.cos(2 * theta) * Math.sin(phi));
                        break;
                    case 'SH':
                        r = Math.abs(Math.cos(theta) * Math.cos(phi));
                        break;
                }

                r = Math.max(r, 0.1); // Ensure minimum radius

                target.x = r * Math.sin(theta) * Math.cos(phi);
                target.y = r * Math.sin(theta) * Math.sin(phi);
                target.z = r * Math.cos(theta);
            }, 64, 64);

            const material = new THREE.MeshStandardMaterial({
                color: 0xD2B48C,
                side: THREE.DoubleSide,
                transparent: true,
                opacity: 0.8
            });

            radiationMesh = new THREE.Mesh(geometry, material);
            parentObject.add(radiationMesh);

            updateRadiationOrientation();
        }

        function createCompass() {
            compassGroup = new THREE.Object3D();
            scene.add(compassGroup);

            // Create circle
            const circleGeometry = new THREE.BufferGeometry();
            const circlePoints = [];
            for (let i = 0; i <= 64; i++) {
                const angle = (i / 64) * Math.PI * 2;
                circlePoints.push(new THREE.Vector3(
                    Math.cos(angle) * 2.5,
                    Math.sin(angle) * 2.5,
                    0
                ));
            }
            circleGeometry.setFromPoints(circlePoints);
            const circleMaterial = new THREE.LineDashedMaterial({
                color: 0x000000,
                dashSize: 0.1,
                gapSize: 0.05
            });
            const circle = new THREE.Line(circleGeometry, circleMaterial);
            circle.computeLineDistances();
            compassGroup.add(circle);

            // Create labels
            const labels = ['N', 'E', 'S', 'W'];
            const positions = [
                [0, 2.7, 0],
                [2.7, 0, 0],
                [0, -2.7, 0],
                [-2.7, 0, 0]
            ];

            labels.forEach((label, i) => {
                const canvas = document.createElement('canvas');
                const context = canvas.getContext('2d');
                canvas.width = 128;
                canvas.height = 64;
                context.font = 'bold 48px Arial';
                context.fillStyle = 'black';
                context.textAlign = 'center';
                context.textBaseline = 'middle';
                context.fillText(label, 64, 32);

                const texture = new THREE.CanvasTexture(canvas);
                const spriteMaterial = new THREE.SpriteMaterial({ map: texture });
                const sprite = new THREE.Sprite(spriteMaterial);
                sprite.position.set(...positions[i]);
                sprite.scale.set(0.5, 0.25, 1);
                compassGroup.add(sprite);
            });

            // Degree markings
            for (let angle = 30; angle < 360; angle += 30) {
                const canvas = document.createElement('canvas');
                const context = canvas.getContext('2d');
                canvas.width = 128;
                canvas.height = 64;
                context.font = '32px Arial';
                context.fillStyle = 'gray';
                context.textAlign = 'center';
                context.textBaseline = 'middle';
                context.fillText(`${angle}°`, 64, 32);

                const texture = new THREE.CanvasTexture(canvas);
                const spriteMaterial = new THREE.SpriteMaterial({ map: texture });
                const sprite = new THREE.Sprite(spriteMaterial);
                const rad = (angle * Math.PI) / 180;
                sprite.position.set(
                    Math.cos(rad) * 2.3,
                    Math.sin(rad) * 2.3,
                    0
                );
                sprite.scale.set(0.3, 0.15, 1);
                compassGroup.add(sprite);
            }
        }

        function updateRadiationOrientation() {
            const strike = parseFloat(document.getElementById('slider-strike').value);
            const dip = parseFloat(document.getElementById('slider-dip').value);
            const rake = parseFloat(document.getElementById('slider-rake').value);

            // Convert to radians
            const strikeRad = (strike * Math.PI) / 180;
            const dipRad = (dip * Math.PI) / 180;
            const rakeRad = (rake * Math.PI) / 180;

            // Apply rotations in ZYX order
            parentObject.rotation.set(0, 0, 0); // Reset
            parentObject.rotateZ(strikeRad);
            parentObject.rotateX(dipRad);
            parentObject.rotateY(rakeRad);
        }

        function onWindowResize() {
            const container = document.getElementById('canvas-container');
            camera.aspect = container.clientWidth / container.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(container.clientWidth, container.clientHeight);
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();

            // Update compass to face camera
            compassGroup.lookAt(camera.position);

            renderer.render(scene, camera);
        }

        // Event listeners
        document.getElementById('slider-strike').addEventListener('input', (e) => {
            document.getElementById('strike-value').textContent = e.target.value;
            updateRadiationOrientation();
        });

        document.getElementById('slider-dip').addEventListener('input', (e) => {
            document.getElementById('dip-value').textContent = e.target.value;
            updateRadiationOrientation();
        });

        document.getElementById('slider-rake').addEventListener('input', (e) => {
            document.getElementById('rake-value').textContent = e.target.value;
            updateRadiationOrientation();
        });

        document.getElementById('btn-p').addEventListener('click', (e) => {
            document.querySelectorAll('.button-container button').forEach(btn => btn.classList.remove('active'));
            e.target.classList.add('active');
            currentWaveType = 'P';
            createRadiationPattern();
        });

        document.getElementById('btn-sv').addEventListener('click', (e) => {
            document.querySelectorAll('.button-container button').forEach(btn => btn.classList.remove('active'));
            e.target.classList.add('active');
            currentWaveType = 'SV';
            createRadiationPattern();
        });

        document.getElementById('btn-sh').addEventListener('click', (e) => {
            document.querySelectorAll('.button-container button').forEach(btn => btn.classList.remove('active'));
            e.target.classList.add('active');
            currentWaveType = 'SH';
            createRadiationPattern();
        });

        document.getElementById('checkbox-box').addEventListener('change', (e) => {
            boxHelper.visible = e.target.checked;
        });

        // Initialize
        init();
        animate();

        // Render MathJax
        MathJax.typeset();
    </script>
</body>
</html>
