</head>
<body>
    <div id="demo-container">
        <div id="control-panel">
            <div class="share-control">
                <label for="joystick-share1">share 1</label>
                <div id="joystick-share1" class="joystick-container">
                    <div id="joystick-handle1" class="joystick-handle"></div>
                </div>
            </div>

            <div class="share-control">
                <label for="joystick-share2">share 2</label>
                <div id="joystick-share2" class="joystick-container">
                    <div id="joystick-handle2" class="joystick-handle"></div>
                </div>
            </div>
        </div>

        <div id="visualization-area">
            <!-- The p5.js canvas will be attached here -->
        </div>

        <button id="btn-reset">+</button>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script>
        let share1_graphic, share2_graphic;
        let share1_x = 0, share1_y = 0;
        let share2_x = 0, share2_y = 0;
        let isDragging = null;
        let draggingHandle = null;
        let canvasWidth, canvasHeight;

        // Secret smiley face pattern (simplified 32x32)
        const SECRET_SIZE = 32;
        let secret_pattern = [];

        function generateSecretPattern() {
            // Create a smiley face pattern
            secret_pattern = [];
            for (let i = 0; i < SECRET_SIZE; i++) {
                secret_pattern[i] = [];
                for (let j = 0; j < SECRET_SIZE; j++) {
                    // Face outline
                    let x = j - SECRET_SIZE/2;
                    let y = i - SECRET_SIZE/2;
                    let r = sqrt(x*x + y*y);

                    // Basic smiley face
                    let is_face = r < SECRET_SIZE/2;
                    let is_eye_left = false;
                    let is_eye_right = false;
                    let is_smile = false;

                    // Eyes
                    if (i > SECRET_SIZE/3 && i < SECRET_SIZE/2) {
                        is_eye_left = abs(j - SECRET_SIZE/4) < SECRET_SIZE/12;
                        is_eye_right = abs(j - 3*SECRET_SIZE/4) < SECRET_SIZE/12;
                    }

                    // Smile
                    for (let smile_r = SECRET_SIZE/3; smile_r < SECRET_SIZE/2.5; smile_r++) {
                        if (abs(r - smile_r) < 0.5) {
                            let angle = atan2(y, x);
                            if (angle > 0 && abs(angle) < PI/2) {
                                is_smile = true;
                            }
                        }
                    }

                    secret_pattern[i][j] = is_eye_left || is_eye_right || is_smile ? 1 : 0;
                }
            }
        }

        function setup() {
            createCanvas(windowWidth - 300, windowHeight);
            canvasWidth = windowWidth - 300;
            canvasHeight = windowHeight;

            // Center the shares initially
            share1_x = canvasWidth/2 - SECRET_SIZE;
            share2_x = canvasWidth/2 - SECRET_SIZE;
            share1_y = canvasHeight/2 - SECRET_SIZE;
            share2_y = canvasHeight/2 - SECRET_SIZE;

            // Generate the secret pattern
            generateSecretPattern();

            // Create share graphics
            share1_graphic = createGraphics(SECRET_SIZE * 2, SECRET_SIZE * 2);
            share2_graphic = createGraphics(SECRET_SIZE * 2, SECRET_SIZE * 2);

            // Generate shares based on secret
            share1_graphic.clear();
            share2_graphic.clear();
            share1_graphic.noStroke();
            share2_graphic.noStroke();

            for (let i = 0; i < SECRET_SIZE; i++) {
                for (let j = 0; j < SECRET_SIZE; j++) {
                    let val = secret_pattern[i][j];

                    // Create 2x2 pixel patterns
                    let pattern = random() > 0.5 ? 1 : 2;

                    if (val === 1) { // Black pixel
                        if (pattern === 1) {
                            // Share 1 pattern
                            share1_graphic.fill(0);
                            share1_graphic.rect(j*2, i*2, 1, 1);
                            share1_graphic.rect(j*2+1, i*2+1, 1, 1);

                            // Share 2 complementary pattern
                            share2_graphic.fill(0);
                            share2_graphic.rect(j*2+1, i*2, 1, 1);
                            share2_graphic.rect(j*2, i*2+1, 1, 1);
                        } else {
                            share1_graphic.fill(0);
                            share1_graphic.rect(j*2+1, i*2, 1, 1);
                            share1_graphic.rect(j*2, i*2+1, 1, 1);

                            share2_graphic.fill(0);
                            share2_graphic.rect(j*2, i*2, 1, 1);
                            share2_graphic.rect(j*2+1, i*2+1, 1, 1);
                        }
                    } else { // White pixel
                        if (pattern === 1) {
                            share1_graphic.fill(0);
                            share1_graphic.rect(j*2, i*2, 1, 1);
                            share2_graphic.fill(0);
                            share2_graphic.rect(j*2+1, i*2+1, 1, 1);
                        } else {
                            share1_graphic.fill(0);
                            share1_graphic.rect(j*2+1, i*2+1, 1, 1);
                            share2_graphic.fill(0);
                            share2_graphic.rect(j*2, i*2, 1, 1);
                        }
                    }
                }
            }

            // Initial slider positions
            updateSharePositions();
        }

        function draw() {
            clear();
            background(255);

            // Draw shares
            image(share1_graphic, share1_x, share1_y);
            image(share2_graphic, share2_x, share2_y);
        }

        function updateSharePositions() {
            // Get joystick positions
            const h1 = document.getElementById('joystick-handle1');
            const h2 = document.getElementById('joystick-handle2');

            const rect1 = document.getElementById('joystick-share1').getBoundingClientRect();
            const rect2 = document.getElementById('joystick-share2').getBoundingClientRect();

            const x1 = parseFloat(h1.style.left || '75') / 100;
            const y1 = parseFloat(h1.style.top || '25') / 100;
            const x2 = parseFloat(h2.style.left || '25') / 100;
            const y2 = parseFloat(h2.style.top || '75') / 100;

            // Map to canvas positions
            share1_x = (canvasWidth - SECRET_SIZE * 2) * x1;
            share1_y = (canvasHeight - SECRET_SIZE * 2) * y1;

            share2_x = (canvasWidth - SECRET_SIZE * 2) * x2;
            share2_y = (canvasHeight - SECRET_SIZE * 2) * y2;
        }

        function windowResized() {
            resizeCanvas(windowWidth - 300, windowHeight);
            canvasWidth = windowWidth - 300;
            canvasHeight = windowHeight;
            updateSharePositions();
        }

        // Joystick drag handling
        function handleMouseDown(e) {
            if (e.target.classList.contains('joystick-handle')) {
                isDragging = true;
                draggingHandle = e.target;
                draggingHandle.style.cursor = 'grabbing';
            }
        }

        function handleMouseMove(e) {
            if (isDragging && draggingHandle) {
                const container = draggingHandle.parentElement;
                const rect = container.getBoundingClientRect();

                const x = e.clientX - rect.left;
                const y = e.clientY - rect.top;

                // Clamp to container bounds
                const clampedX = Math.max(20, Math.min(rect.width - 20, x));
                const clampedY = Math.max(20, Math.min(rect.height - 20, y));

                // Update position as percentage
                draggingHandle.style.left = (clampedX / rect.width * 100) + '%';
                draggingHandle.style.top = (clampedY / rect.height * 100) + '%';

                updateSharePositions();
            }
        }

        function handleMouseUp() {
            if (draggingHandle) {
                draggingHandle.style.cursor = 'grab';
            }
            isDragging = false;
            draggingHandle = null;
        }

        function resetPositions() {
            document.getElementById('joystick-handle1').style.left = '75%';
            document.getElementById('joystick-handle1').style.top = '25%';

            document.getElementById('joystick-handle2').style.left = '25%';
            document.getElementById('joystick-handle2').style.top = '75%';

            updateSharePositions();
        }

        // Event listeners
        document.addEventListener('mousedown', handleMouseDown);
        document.addEventListener('mousemove', handleMouseMove);
        document.addEventListener('mouseup', handleMouseUp);
        document.getElementById('btn-reset').addEventListener('click', resetPositions);

        // Prevent text selection during drag
        document.addEventListener('dragstart', e => e.preventDefault());
    </script>
</body>
</html>
