            <canvas id="p5-canvas"></canvas>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let rings = 1;
        let circlesPerRing = 8;
        let inverse = false;
        let locators = [];
        let draggedLocator = -1;
        let canvasSize = 550;

        function setup() {
            let canvas = createCanvas(canvasSize, canvasSize);
            canvas.parent('canvas-container');
            angleMode(RADIANS);

            // Initialize locators
            locators[0] = createVector(-50, 0);
            locators[1] = createVector(50, 0);

            // Set up event listeners
            document.querySelectorAll('input[name="rings"]').forEach(radio => {
                radio.addEventListener('change', updateSettings);
            });
            document.querySelectorAll('input[name="cpr"]').forEach(radio => {
                radio.addEventListener('change', updateSettings);
            });
            document.getElementById('checkbox-inverse').addEventListener('change', function() {
                inverse = this.checked;
                redraw();
            });
        }

        function updateSettings() {
            rings = parseInt(document.querySelector('input[name="rings"]:checked').value);
            circlesPerRing = parseInt(document.querySelector('input[name="cpr"]:checked').value);
            redraw();
        }

        function draw() {
            background('#C1D4E6');
            translate(width / 2, height / 2);

            // Calculate geometries
            let N = circlesPerRing;
            let M = rings;
            let R_max = (width / 2) * 0.9;
            let k = (1 - sin(PI / N)) / (1 + sin(PI / N));
            let R_c = R_max / (1 + sin(PI / N));
            let r = R_c * sin(PI / N);

            if (!inverse) {
                // Draw normal pattern
                for (let m = 0; m < M; m++) {
                    let Rm = R_c * Math.pow(k, m);
                    let rm = r * Math.pow(k, m);

                    for (let j = 0; j < N; j++) {
                        let angle = j * 2 * PI / N;
                        let x = Rm * cos(angle);
                        let y = Rm * sin(angle);

                        fill('#8C929D');
                        stroke(0);
                        strokeWeight(1);
                        circle(x, y, 2 * rm);

                        // Draw guide lines for outermost ring
                        if (m === 0) {
                            stroke('#A0A0A0');
                            strokeWeight(1);
                            line(0, 0, x, y);
                        }
                    }
                }
            } else {
                // Draw inverse mode
                drawInverseMode(R_c, r, N, M, k);
            }
        }

        function drawInverseMode(R_c, r, N, M, k) {
            // Draw inversion circle
            let C_inv = p5.Vector.add(locators[0], locators[1]).mult(0.5);
            let R_inv = p5.Vector.dist(locators[0], locators[1]) / 2;

            fill('rgba(255, 0, 0, 0.3)');
            stroke(0);
            strokeWeight(1);
            circle(C_inv.x, C_inv.y, 2 * R_inv);

            // Draw connecting line
            stroke('#FF0000');
            strokeWeight(1);
            line(locators[0].x, locators[0].y, locators[1].x, locators[1].y);

            // Draw locators
            fill('#FF0000');
            noStroke();
            for (let locator of locators) {
                circle(locator.x, locator.y, 8);
            }

            // Invert all original circles
            let circles = [];
            for (let m = 0; m < M; m++) {
                let Rm = R_c * Math.pow(k, m);
                let rm = r * Math.pow(k, m);

                for (let j = 0; j < N; j++) {
                    let angle = j * 2 * PI / N;
                    let x = Rm * cos(angle);
                    let y = Rm * sin(angle);
                    circles.push({x: x, y: y, r: rm});
                }
            }

            // Draw inverted circles
            fill('rgba(220, 100, 220, 0.5)');
            stroke(0);
            strokeWeight(1);

            for (let circle of circles) {
                let C_orig = createVector(circle.x, circle.y);
                let r_orig = circle.r;

                // Calculate inversion
                let d = p5.Vector.dist(C_inv, C_orig);
                let epsilon = 0.0001;

                if (abs(d - r_orig) < epsilon) {
                    // Invert to line
                    let mid = p5.Vector.sub(C_orig, C_inv).setMag(R_inv * R_inv / (2 * d));
                    let p1 = createVector(-mid.y, mid.x);
                    let p2 = createVector(mid.y, -mid.x);
                    p1.add(mid).add(C_inv);
                    p2.add(mid).add(C_inv);

                    line(p1.x, p1.y, p2.x, p2.y);
                } else if (d > r_orig) {
                    // Invert to circle
                    let C_new = p5.Vector.sub(C_orig, C_inv);
                    C_new.mult(R_inv * R_inv / (d * d - r_orig * r_orig));
                    C_new.add(C_inv);

                    let r_new = abs(R_inv * R_inv / (d * d - r_orig * r_orig) * r_orig);

                    circle(C_new.x, C_new.y, 2 * r_new);
                } else {
                    // Circle contains inversion circle
                    let C_new = p5.Vector.sub(C_orig, C_inv);
                    let scale = R_inv * R_inv / (r_orig * r_orig - d * d);
                    C_new.mult(scale);
                    C_new.add(C_inv);

                    let r_new = abs(scale * r_orig);

                    circle(C_new.x, C_new.y, 2 * r_new);
                }
            }
        }

        function mousePressed() {
            if (inverse) {
                let mX = mouseX - width / 2;
                let mY = mouseY - height / 2;

                for (let i = 0; i < locators.length; i++) {
                    let d = dist(mX, mY, locators[i].x, locators[i].y);
                    if (d < 10) {
                        draggedLocator = i;
                        break;
                    }
                }
            }
        }

        function mouseDragged() {
            if (draggingLocator()) {
                locators[draggedLocator].x = mouseX - width / 2;
                locators[draggedLocator].y = mouseY - height / 2;
                redraw();
            }
        }

        function mouseReleased() {
            draggedLocator = -1;
            if (inverse) redraw();
        }

        function draggingLocator() {
            return draggedLocator !== -1;
        }
    </script>
</body>
</html>
