<body>
    <div id="app">
        <div id="controls">
            <div>
                <label for="slider-logCa">\( C_a \): 10<input type="range" id="slider-logCa" min="-4" max="0" step="0.01" value="-1"> M</label>
            </div>
            <div>
                <label for="slider-pka">\( pK_a \): <input type="range" id="slider-pka" min="-4" max="7" step="0.1" value="1.2")></label>
            </div>
        </div>
        <div id="viz-container">
            <!-- canvas injected by p5.js -->
            <div class="formula" id="formula-strong">\( \color{maroon}{pH = - \log C_a} \)</div>
            <div class="formula" id="formula-weak">\( \color{green}{pH = \frac{1}{2} (pK_a - \log C_a)} \)</div>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.9.0/p5.js"></script>
    <script>
        let logCa = -1;
        let pKa = 1.2;
        const w = 800, h = 500;

        const elemLogCa = document.getElementById('slider-logCa');
        const elemPKa  = document.getElementById('slider-pka');

        elemLogCa.addEventListener('input', e => {
            logCa = parseFloat(e.target.value);
            redraw();
        });

        elemPKa.addEventListener('input', e => {
            pKa = parseFloat(e.target.value);
            redraw();
        });

        function setup() {
            const c = createCanvas(w, h);
            c.parent('viz-container');
            noLoop();
        }

        function draw() {
            background(255);

            // axes
            stroke(80);
            strokeWeight(1);
            line(50, h - 50, w - 50, h - 50); // x
            line(50, h - 50, 50, 50);         // y

            // tick labels
            textAlign(CENTER, CENTER);
            fill(0);
            strokeWeight(0);
            textSize(12);
            for (let px = 0; px <= 11; px += 1) {
                let x = map(px - 4, -4, 7, 50, w - 50);
                line(x, h - 50, x, h - 45);
                text(px - 4, x, h - 30);
            }
            for (let py = 0; py <= 7; py += 1) {
                let y = map(py, 0, 7, h - 50, 50);
                line(50, y, 45, y);
                text(py, 30, y);
            }
            textAlign(LEFT, CENTER);
            textSize(14);
            text("pKa", w - 50, h - 20);
            push();
            translate(20, h / 2);
            rotate(-PI/2);
            text("pH", 0, 0);
            pop();

            strokeWeight(2);

            // Strong-acid approximation
            stroke(128, 0, 0);
            let yStrong = -logCa;
            line(50, map(yStrong, 0, 7, h - 50, 50),
                 w - 50, map(yStrong, 0, 7, h - 50, 50));

            // Weak-acid approximation
            stroke(0, 128, 0);
            beginShape();
            for (let pk = -4; pk <= 7; pk += 0.05) {
                let yw = 0.5 * (pk - logCa);
                let x = map(pk, -4, 7, 50, w - 50);
                let yy = map(yw, 0, 7, h - 50, 50);
                vertex(x, yy);
            }
            endShape();

            // Exact pH curve
            stroke(0, 0, 255);
            strokeWeight(2.5);
            beginShape();
            for (let pk = -4; pk <= 7; pk += 0.02) {
                let ka = pow(10, -pk);
                let ca = pow(10, logCa);
                let disc = sqrt(ka*ka + 4*ka*ca);
                let h = (-ka + disc) / 2;
                if (h < 0) h = 0;
                let exactPH = -log10(h);
                if (exactPH < 0 || exactPH > 7) continue;
                let x = map(pk, -4, 7, 50, w - 50);
                let yy = map(exactPH, 0, 7, h - 50, 50);
                vertex(x, yy);
            }
            endShape();

            // Indicator point
            let ka = pow(10, -pKa);
            let ca = pow(10, logCa);
            let disc = sqrt(ka*ka + 4*ka*ca);
            let h = (-ka + disc) / 2;
            let exactPH = -log10(max(h, 1e-14));
            exactPH = constrain(exactPH, 0, 7);
            let ptX = map(pKa, -4, 7, 50, w - 50);
            let ptY = map(exactPH, 0, 7, h - 50, 50);
            stroke(0);
            fill(255, 140, 0);
            circle(ptX, ptY, 10);
        }
    </script>
</body>
</html>
