<!-- CDN for p5.js -->
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
<script>
    // ---- Configuration Variables ----
    const GRID_SIZE = 50;
    const CELL_SIZE = 10;

    let algorithm = 'dijkstra';
    let currentStep = 1;
    let maxSteps = 2;
    let history = [];

    let startPoint = { x: 7, y: 42 };
    let endPoint = { x: 42, y: 7 };
    let obstacleLayout = '1-square';

    const obstacleLayouts = {
        '1-square': [
            { x: 25, y: 25 }
        ],
        '2-squares': [
            { x: 20, y: 20 },
            { x: 30, y: 30 }
        ],
        'l-shaped-wall': (() => {
            const wall = [];
            for (let i = 15; i < 35; i++) wall.push({ x: i, y: 20 });
            for (let j = 20; j < 35; j++) wall.push({ x: 15, y: j });
            return wall;
        })()
    };

    // p5.js setup
    function setup() {
        const canvas = createCanvas(GRID_SIZE * CELL_SIZE, GRID_SIZE * CELL_SIZE);
        canvas.parent('canvas-container');
        noLoop();
        recomputePath();
    }

    // p5.js draw
    function draw() {
        background(255);

        // Draw grid
        strokeWeight(1);
        stroke(230);
        for (let x = 0; x <= width; x += CELL_SIZE) {
            line(x, 0, x, height);
        }
        for (let y = 0; y <= height; y += CELL_SIZE) {
            line(0, y, width, y);
        }

        // Draw obstacles
        noStroke();
        fill(0);
        obstacleLayouts[obstacleLayout].forEach(obs => {
            rect(obs.x * CELL_SIZE, obs.y * CELL_SIZE, CELL_SIZE, CELL_SIZE);
        });

        if (history.length > 0) {
            const frame = history[currentStep - 1];

            // Draw visited cells
            noStroke();
            fill(0, 0, 205);
            frame.visited.forEach(pos => {
                const [x, y] = pos;
                rect(x * CELL_SIZE, y * CELL_SIZE, CELL_SIZE, CELL_SIZE);
            });

            // Draw frontier cells
            fill(189, 224, 254);
            frame.frontier.forEach(pos => {
                const [x, y] = pos;
                rect(x * CELL_SIZE, y * CELL_SIZE, CELL_SIZE, CELL_SIZE);
            });

            // Draw start and end points
            fill(52, 168, 83);
            rect(startPoint.x * CELL_SIZE, startPoint.y * CELL_SIZE, CELL_SIZE, CELL_SIZE);
            rect(endPoint.x * CELL_SIZE, endPoint.y * CELL_SIZE, CELL_SIZE, CELL_SIZE);

            // Draw path if this is the final step
            if (currentStep === maxSteps && frame.path && frame.path.length > 1) {
                for (let i = 0; i < frame.path.length - 1; i++) {
                    const current = frame.path[i];
                    const next = frame.path[i + 1];
                    strokeWeight(3);
                    stroke(255, 0, 0);
                    line(
                        current.x * CELL_SIZE + CELL_SIZE / 2,
                        current.y * CELL_SIZE + CELL_SIZE / 2,
                        next.x * CELL_SIZE + CELL_SIZE / 2,
                        next.y * CELL_SIZE + CELL_SIZE / 2
                    );
                }
            }
        }

        // Draw start and end point indicators
        noStroke();
        fill(52, 168, 83);
        rect(startPoint.x * CELL_SIZE, startPoint.y * CELL_SIZE, CELL_SIZE, CELL_SIZE);
        rect(endPoint.x * CELL_SIZE, endPoint.y * CELL_SIZE, CELL_SIZE, CELL_SIZE);
    }

    // Pathfinding algorithms
    function recomputePath() {
        history = [];
        let openSet = [];
        let closedSet = [];
        let cameFrom = {};
        let gScore = {};
        let fScore = {};

        const startKey = `${startPoint.x},${startPoint.y}`;
        gScore[startKey] = 0;
        fScore[startKey] = heuristic(startPoint, endPoint);

        openSet.push({ ...startPoint, f: fScore[startKey] });

        while (openSet.length > 0) {
            openSet.sort((a, b) => a.f - b.f);
            const current = openSet.shift();

            history.push({
                visited: Array.from(closedSet),
                frontier: openSet.map(node => [node.x, node.y]),
                path: null
            });

            const currentKey = `${current.x},${current.y}`;
            if (current.x === endPoint.x && current.y === endPoint.y) {
                const path = [];
                let temp = current;
                while (temp) {
                    path.unshift(temp);
                    temp = cameFrom[`${temp.x},${temp.y}`];
                }
                history[history.length - 1].path = path;
                break;
            }

            closedSet.push([current.x, current.y]);

            const neighbors = getNeighbors(current);
            neighbors.forEach(neighbor => {
                const neighborKey = `${neighbor.x},${neighbor.y}`;
                if (closedSet.includes(neighborKey)) return;

                const tentativeGScore = gScore[currentKey] + neighbor.cost;
                if (tentativeGScore < (gScore[neighborKey] || Infinity)) {
                    cameFrom[neighborKey] = { ...current };
                    gScore[neighborKey] = tentativeGScore;
                    const newF = tentativeGScore + heuristic(neighbor, endPoint);
                    fScore[neighborKey] = newF;

                    const existing = openSet.find(n => n.x === neighbor.x && n.y === neighbor.y);
                    if (!existing && !isObstacle(neighbor.x, neighbor.y)) {
                        openSet.push({ ...neighbor, f: newF });
                    } else if (existing && newF < existing.f) {
                        existing.f = newF;
                    }
                }
            });
        }

        maxSteps = history.length;
        if (maxSteps < 1) maxSteps = 1;
        document.getElementById('slider-step').max = maxSteps;
        document.getElementById('slider-step').value = 1;
        updateStepLabel();
        redraw();
    }

    function heuristic(a, b) {
        if (algorithm === 'a-star') {
            return Math.abs(a.x - b.x) + Math.abs(a.y - b.y);
        }
        return 0;
    }

    function getNeighbors(pos) {
        const neighbors = [];
        const directions = [
            { dx: -1, dy: -1, cost: Math.SQRT2 },
            { dx: 0, dy: -1, cost: 1 },
            { dx: 1, dy: -1, cost: Math.SQRT2 },
            { dx: -1, dy: 0, cost: 1 },
            { dx: 1, dy: 0, cost: 1 },
            { dx: -1, dy: 1, cost: Math.SQRT2 },
            { dx: 0, dy: 1, cost: 1 },
            { dx: 1, dy: 1, cost: Math.SQRT2 }
        ];

        directions.forEach(({ dx, dy, cost }) => {
            const x = pos.x + dx;
            const y = pos.y + dy;
            if (x >= 0 && x < GRID_SIZE && y >= 0 && y < GRID_SIZE && !isObstacle(x, y)) {
                neighbors.push({ x, y, cost });
            }
        });

        return neighbors;
    }

    function isObstacle(x, y) {
        return obstacleLayouts[obstacleLayout].some(obs => obs.x === x && obs.y === y);
    }

    // UI Interactions
    document.addEventListener('DOMContentLoaded', () => {
        // Algorithm change
        document.querySelectorAll('input[name="algorithm"]').forEach(radio => {
            radio.addEventListener('change', (e) => {
                algorithm = e.target.value;
                recomputePath();
            });
        });

        // Step slider change
        const slider = document.getElementById('slider-step');
        slider.addEventListener('input', () => {
            currentStep = Math.max(1, Math.min(parseInt(slider.value, 10), maxSteps));
            updateStepLabel();
            redraw();
        });

        // Obstacle change
        const obstacleSelect = document.getElementById('select-obstacles');
        obstacleSelect.addEventListener('change', (e) => {
            obstacleLayout = e.target.value;
            recomputePath();
        });

        // Joystick interactions
        setupJoystick('joystick-start', 'handle-start', (x, y) => {
            startPoint = { x: Math.floor(x * (GRID_SIZE - 1)), y: Math.floor(y * (GRID_SIZE - 1)) };
            recomputePath();
        });

        setupJoystick('joystick-end', 'handle-end', (x, y) => {
            endPoint = { x: Math.floor(x * (GRID_SIZE - 1)), y: Math.floor(y * (GRID_SIZE - 1)) };
            recomputePath();
        });
    });

    function setupJoystick(containerId, handleId, callback) {
        const container = document.getElementById(containerId);
        const handle = document.getElementById(handleId);
        let isDragging = false;

        container.addEventListener('mousedown', (e) => {
            isDragging = true;
            updateHandlePosition(e);
        });

        container.addEventListener('mousemove', (e) => {
            if (isDragging) updateHandlePosition(e);
        });

        container.addEventListener('mouseup', () => {
            isDragging = false;
        });

        container.addEventListener('mouseleave', () => {
            isDragging = false;
        });

        function updateHandlePosition(e) {
            if (!isDragging) return;
            const rect = container.getBoundingClientRect();
            let x = (e.clientX - rect.left) / rect.width;
            let y = (e.clientY - rect.top) / rect.height;
            x = Math.max(0, Math.min(1, x));
            y = Math.max(0, Math.min(1, y));
            handle.style.left = `${x * 100}%`;
            handle.style.top = `${y * 100}%`;
            callback(x, y);
        }
    }

    function updateStepLabel() {
        document.getElementById('label-step').textContent = `+ ${currentStep}`;
    }
</script>
</body>
</html>
