            color: #000;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="controls-container">
            <div class="slider-group">
                <label for="slider-source-charge">Source charge</label>
                <input type="range" id="slider-source-charge" min="-5" max="5" step="0.1" value="2.0">
                <span id="label-source-charge">2.0</span>
            </div>
            <div class="slider-group">
                <label for="slider-test-charge">Charge</label>
                <input type="range" id="slider-test-charge" min="-0.2" max="0.2" step="0.0005" value="-0.0845">
                <span id="label-test-charge">-0.0845</span>
            </div>
        </div>

        <div id="main-content">
            <div id="canvas-container"></div>
            <div id="info-panel">
                <p id="display-E-mag">|E| = 0.153 N/C</p>
                <p id="display-Ex">E<sub>x</sub> = 0.085 N/C</p>
                <p id="display-Ey">E<sub>y</sub> = 0.127 N/C</p>
                <p id="display-x-pos">x = 2.00 m</p>
                <p id="display-y-pos">y = 3.00 m</p>
                <p id="display-force">Electric force = 0.013 N</p>
            </div>
        </div>
    </div>

    <script>
        let Q = 2.0;  // Source charge
        let q = -0.0845;  // Test charge
        let testX = 2;  // Test charge x position
        let testY = 3;  // Test charge y position
        let dragging = false;
        let offsetX = 0;
        let offsetY = 0;

        function setup() {
            const canvas = createCanvas(600, 500);
            canvas.parent('canvas-container');

            // Set up sliders
            document.getElementById('slider-source-charge').addEventListener('input', (e) => {
                Q = parseFloat(e.target.value);
                document.getElementById('label-source-charge').textContent = Q.toFixed(1);
            });

            document.getElementById('slider-test-charge').addEventListener('input', (e) => {
                q = parseFloat(e.target.value);
                document.getElementById('label-test-charge').textContent = q.toFixed(4);
            });
        }

        function draw() {
            background(240);

            push();
            translate(width/2, height/2);
            scale(40, -40);
            stroke(200);
            strokeWeight(1);

            // Draw grid
            for(let x = -7; x <= 7; x++) {
                if(x === 0) {
                    stroke(0);
                    strokeWeight(2);
                }
                line(x, -7, x, 7);
                stroke(200);
                strokeWeight(1);
            }
            for(let y = -7; y <= 7; y++) {
                if(y === 0) {
                    stroke(0);
                    strokeWeight(2);
                }
                line(-7, y, 7, y);
                stroke(200);
                strokeWeight(1);
            }

            // Draw source charge
            fill(0, 100, 255);
            noStroke();
            ellipse(0, 0, 0.5, 0.5);

            // Draw test charge locator
            fill(0);
            ellipse(testX, testY, 0.4, 0.4);

            // Calculate vectors
            let dx = testX - 0;
            let dy = testY - 0;
            let r = sqrt(dx*dx + dy*dy);
            let rHatX = dx/r;
            let rHatY = dy/r;

            // Electric field
            let E = Q/(r*r);
            let Ex = E * rHatX;
            let Ey = E * rHatY;

            // Draw electric field vector
            stroke(255, 0, 0);
            strokeWeight(3);
            line(testX, testY, testX + Ex, testY + Ey);

            fill(255, 0, 0);
            push();
            translate(testX + Ex, testY + Ey);
            rotate(atan2(Ey, Ex));
            triangle(0, 0, -0.2, 0.1, -0.2, -0.1);
            pop();

            // Electric force
            let Fx = q * Ex;
            let Fy = q * Ey;
            let forceMag = sqrt(Fx*Fx + Fy*Fy);

            // Draw force vector
            if (q !== 0) {
                stroke(0, 0, 255);
                strokeWeight(3);
                line(testX, testY, testX + Fx, testY + Fy);

                fill(0, 0, 255);
                push();
                translate(testX + Fx, testY + Fy);
                rotate(atan2(Fy, Fx));
                triangle(0, 0, -0.2, 0.1, -0.2, -0.1);
                pop();
            }

            pop();

            // Update display values
            document.getElementById('display-E-mag').textContent = `|E| = ${E.toFixed(3)} N/C`;
            document.getElementById('display-Ex').innerHTML = `E<sub>x</sub> = ${Ex.toFixed(3)} N/C`;
            document.getElementById('display-Ey').innerHTML = `E<sub>y</sub> = ${Ey.toFixed(3)} N/C`;
            document.getElementById('display-x-pos').textContent = `x = ${testX.toFixed(2)} m`;
            document.getElementById('display-y-pos').textContent = `y = ${testY.toFixed(2)} m`;
            document.getElementById('display-force').textContent = `Electric force = ${forceMag.toFixed(3)} N`;
        }

        function mousePressed() {
            let mouseXWorld = (mouseX - width/2) / 40;
            let mouseYWorld = -(mouseY - height/2) / 40;

            let d = sqrt((mouseXWorld - testX)**2 + (mouseYWorld - testY)**2);
            if (d < 0.2) {
                dragging = true;
                offsetX = mouseXWorld - testX;
                offsetY = mouseYWorld - testY;
            }
        }

        function mouseDragged() {
            if (dragging) {
                testX = (mouseX - width/2) / 40 - offsetX;
                testY = -(mouseY - height/2) / 40 - offsetY;
                testX = constrain(testX, -6, 6);
                testY = constrain(testY, -6, 6);
            }
        }

        function mouseReleased() {
            dragging = false;
        }
    </script>
</body>
</html>
