                <span id="span-k-value">50</span>
            </div>
        </div>
    </div>

    <p id="hover-info">(Hover over the curves to see math labels.)</p>

    <div id="plots-container">
        <div id="plot-left"></div>
        <div id="plot-right"></div>
    </div>

    <script>
        let leftPlot, rightPlot;

        window.MathJax = {
            tex: {inlineMath: [['$', '$'], ['\\(', '\\)']]},
            startup: {
                ready: () => {
                    MathJax.startup.defaultReady();
                    init();
                }
            }
        };

        function init() {
            updatePlots();
        }

        function updatePlots() {
            const rMax = parseFloat(document.getElementById('slider-r').value);
            const kMax = parseFloat(document.getElementById('slider-k').value);

            document.getElementById('span-r-value').textContent = rMax;
            document.getElementById('span-k-value').textContent = kMax.toFixed(2);

            const showHertz = document.getElementById('checkbox-hertz').checked;
            const showOverlap = document.getElementById('checkbox-overlap').checked;
            const showGaussian = document.getElementById('checkbox-gaussian').checked;

            const activeScale = document.querySelector('#scale-selector button.active').id;
            const yScale = activeScale === 'btn-linear-linear' ? 'linear' : 'log';

            // Generate data for left plot
            const rValues = [];
            const hertzValues = [];
            const overlapValues = [];
            const gaussianValues = [];

            for (let i = 0; i < 200; i++) {
                const r = (rMax * i) / 199;
                rValues.push(r);

                // Hertz function
                if (r <= 1) {
                    hertzValues.push(Math.pow(1 - r * r, 2.5));
                } else {
                    hertzValues.push(0);
                }

                // Overlap function
                if (r <= 1) {
                    overlapValues.push(Math.pow(1 - r, 2));
                } else {
                    overlapValues.push(0);
                }

                // Gaussian function
                gaussianValues.push(Math.exp(-Math.PI * r * r));
            }

            const leftData = [];
            if (showHertz) {
                leftData.push({
                    x: rValues,
                    y: hertzValues,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Hertz',
                    line: {color: '#ff0000'},
                    hovertemplate: 'Hertz<br>$f(r) = (1 - r^2)^{5/2}, r \\le 1$<extra></extra>'
                });
            }
            if (showOverlap) {
                leftData.push({
                    x: rValues,
                    y: overlapValues,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'overlap',
                    line: {color: '#000000'},
                    hovertemplate: 'overlap<br>$f(r) = (1 - r)^2, r \\le 1$<extra></extra>'
                });
            }
            if (showGaussian) {
                leftData.push({
                    x: rValues,
                    y: gaussianValues,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Gaussian',
                    line: {color: '#0000ff'},
                    hovertemplate: 'Gaussian<br>$f(r) = e^{-\\pi r^2}$<extra></extra>'
                });
            }

            const leftLayout = {
                title: 'function',
                xaxis: {title: 'r', range: [0, rMax]},
                yaxis: {title: 'f(r)'},
                showlegend: true,
                plot_bgcolor: '#f5f5f5'
            };

            Plotly.react('plot-left', leftData, leftLayout);

            // Generate data for right plot
            const kValues = [];
            const hertzFValues = [];
            const overlapFValues = [];
            const gaussianFValues = [];

            for (let i = 0; i < 200; i++) {
                const k = (kMax * i) / 199;
                kValues.push(k);

                // Hertz Fourier transform
                if (Math.abs(k) < 1e-10) {
                    hertzFValues.push(Math.PI);
                } else {
                    const numerator = (3 - k * k) * Math.sin(k) - 3 * k * Math.cos(k);
                    hertzFValues.push(15 * Math.PI * numerator / Math.pow(k, 5));
                }

                // Overlap Fourier transform
                if (Math.abs(k) < 1e-10) {
                    overlapFValues.push(Math.PI / 3);
                } else {
                    const numerator = 2 - k * k - 2 * Math.cos(k) - 2 * k * Math.sin(k);
                    overlapFValues.push(8 * Math.PI * numerator / Math.pow(k, 4));
                }

                // Gaussian Fourier transform
                gaussianFValues.push((1/Math.sqrt(Math.PI)) * Math.exp(-k * k / (4 * Math.PI)));
            }

            const rightData = [];
            if (showHertz) {
                rightData.push({
                    x: kValues,
                    y: hertzFValues,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Hertz',
                    line: {color: '#ff0000'},
                    hovertemplate: 'Hertz<br>$F(k) = 15\\pi \\frac{(3-k^2)\\sin(k) - 3k\\cos(k)}{k^5}$<extra></extra>'
                });
            }
            if (showOverlap) {
                rightData.push({
                    x: kValues,
                    y: overlapFValues,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'overlap',
                    line: {color: '#000000'},
                    hovertemplate: 'overlap<br>$F(k) = 8\\pi \\frac{2 - k^2 - 2\\cos(k) - 2k\\sin(k)}{k^4}$<extra></extra>'
                });
            }
            if (showGaussian) {
                rightData.push({
                    x: kValues,
                    y: gaussianFValues,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Gaussian',
                    line: {color: '#0000ff'},
                    hovertemplate: 'Gaussian<br>$F(k) = \\frac{1}{\\sqrt{\\pi}} e^{-k^2 / (4\\pi)}$<extra></extra>'
                });
            }

            const rightLayout = {
                title: '3D Fourier transform (real part)',
                xaxis: {title: 'k', range: [0, kMax]},
                yaxis: {title: 'F(k)', type: yScale === 'log' && activeScale === 'btn-log-log' ? 'log' : yScale},
                showlegend: true,
                plot_bgcolor: '#f5f5f5'
            };

            Plotly.react('plot-right', rightData, rightLayout);

            MathJax.typeset();
        }

        // Event listeners
        document.getElementById('checkbox-hertz').addEventListener('change', updatePlots);
        document.getElementById('checkbox-overlap').addEventListener('change', updatePlots);
        document.getElementById('checkbox-gaussian').addEventListener('change', updatePlots);

        document.getElementById('slider-r').addEventListener('input', updatePlots);
        document.getElementById('slider-k').addEventListener('input', updatePlots);

        document.querySelectorAll('#scale-selector button').forEach(btn => {
            btn.addEventListener('click', function() {
                document.querySelectorAll('#scale-selector button').forEach(b => b.classList.remove('active'));
                this.classList.add('active');
                updatePlots();
            });
        });
    </script>
</body>
</html>
