<body>
    <div id="app-container">
        <div id="control-panel">
            <h4>scale</h4>
            <div id="scale-switch">
                <input type="radio" id="scale-population" name="scale" value="population">
                <label for="scale-population">population</label>
                <input type="radio" id="scale-individual" name="scale" value="individual" checked>
                <label for="scale-individual">individual</label>
            </div>
            <label for="slider-population">average city population (k)</label>
            <input type="range" id="slider-population" min="1" max="1000" value="250" step="1" disabled>
            <span id="value-population">250</span>

            <h4>type</h4>
            <div id="type-switch">
                <input type="radio" id="type-emissions" name="type" value="emissions" checked>
                <label for="type-emissions">emissions</label>
                <input type="radio" id="type-costs" name="type" value="costs">
                <label for="type-costs">costs</label>
            </div>

            <label for="slider-annual-emissions">annual CO₂ emissions per person (tons/year)</label>
            <input type="range" id="slider-annual-emissions" min="1" max="25" value="14" step="0.1">
            <span id="value-annual-emissions">14</span>

            <label for="slider-reduction-time">reduction time until 50% emissions (years)</label>
            <input type="range" id="slider-reduction-time" min="5" max="50" value="20" step="1">
            <span id="value-reduction-time">20</span>

            <label for="slider-annual-budget">annual CO₂ budget per person (tons/year)</label>
            <input type="range" id="slider-annual-budget" min="0" max="5" value="1.5" step="0.1">
            <span id="value-annual-budget">1.5</span>

            <label for="slider-start-year">start of action</label>
            <input type="range" id="slider-start-year" min="2020" max="2049" value="2020" step="1">
            <span id="value-start-year">2020</span>

            <label for="slider-compensation-cost">compensation costs per ton of CO₂ ($/ton)</label>
            <input type="range" id="slider-compensation-cost" min="5" max="100" value="15" step="1">
            <span id="value-compensation-cost">15</span>

            <label for="slider-fractional-compensation">fractional compensation (%)</label>
            <input type="range" id="slider-fractional-compensation" min="0" max="100" value="50" step="1">
            <span id="value-fractional-compensation">50</span>

            <label for="slider-constant-compensation">constant compensation (%)</label>
            <input type="range" id="slider-constant-compensation" min="0" max="100" value="25" step="1">
            <span id="value-constant-compensation">25</span>
        </div>

        <div id="vis-panel">
            <div id="climate-neutral-buttons">
                <button id="btn-neutral-now" onclick="setNeutralYear(2020)">now</button>
                <button id="btn-neutral-2030" onclick="setNeutralYear(2030)">2030</button>
                <button id="btn-neutral-2040" onclick="setNeutralYear(2040)" class="active">2040</button>
                <button id="btn-neutral-2050" onclick="setNeutralYear(2050)">2050</button>
                <button id="btn-neutral-never" onclick="setNeutralYear(null)">never</button>
            </div>
            <div id="main-plot-container"></div>
            <div id="budget-plot-container"></div>
        </div>
    </div>

    <script>
        let neutralYear = 2040;
        let sketch;

        function getState() {
            return {
                scale: document.querySelector('input[name="scale"]:checked').value,
                type: document.querySelector('input[name="type"]:checked').value,
                population: parseFloat(document.getElementById('slider-population').value),
                annualEmissions: parseFloat(document.getElementById('slider-annual-emissions').value),
                reductionTime: parseFloat(document.getElementById('slider-reduction-time').value),
                annualBudget: parseFloat(document.getElementById('slider-annual-budget').value),
                startYear: parseInt(document.getElementById('slider-start-year').value),
                compensationCost: parseFloat(document.getElementById('slider-compensation-cost').value),
                fractionalCompensation: parseFloat(document.getElementById('slider-fractional-compensation').value),
                constantCompensation: parseFloat(document.getElementById('slider-constant-compensation').value),
                neutralYear: neutralYear
            };
        }

        function setNeutralYear(year) {
            neutralYear = year;
            document.querySelectorAll('#climate-neutral-buttons button').forEach(btn => {
                btn.classList.remove('active');
            });
            if (year === 2020) document.getElementById('btn-neutral-now').classList.add('active');
            else if (year) document.getElementById('btn-neutral-' + year).classList.add('active');
            else document.getElementById('btn-neutral-never').classList.add('active');
            sketch.redraw();
        }

        function setupEventListeners() {
            const controls = [
                'scale-population', 'scale-individual', 'type-emissions', 'type-costs',
                'slider-population', 'slider-annual-emissions', 'slider-reduction-time',
                'slider-annual-budget', 'slider-start-year', 'slider-compensation-cost',
                'slider-fractional-compensation', 'slider-constant-compensation'
            ];

            controls.forEach(id => {
                document.getElementById(id).addEventListener('change', (e) => {
                    if (id === 'scale-population') {
                        document.getElementById('slider-population').disabled = false;
                    } else if (id === 'scale-individual') {
                        document.getElementById('slider-population').disabled = true;
                    }
                    updateSliderValues();
                    sketch.redraw();
                });
            });

            controls.filter(id => id.startsWith('slider-')).forEach(id => {
                document.getElementById(id).addEventListener('input', (e) => {
                    const valueSpan = document.getElementById(id.replace('slider-', 'value-'));
                    if (valueSpan) {
                        valueSpan.textContent = e.target.value;
                    }
                    sketch.redraw();
                });
            });
        }

        function updateSliderValues() {
            document.getElementById('value-population').textContent = document.getElementById('slider-population').value;
            document.getElementById('value-annual-emissions').textContent = document.getElementById('slider-annual-emissions').value;
            document.getElementById('value-reduction-time').textContent = document.getElementById('slider-reduction-time').value;
            document.getElementById('value-annual-budget').textContent = document.getElementById('slider-annual-budget').value;
            document.getElementById('value-start-year').textContent = document.getElementById('slider-start-year').value;
            document.getElementById('value-compensation-cost').textContent = document.getElementById('slider-compensation-cost').value;
            document.getElementById('value-fractional-compensation').textContent = document.getElementById('slider-fractional-compensation').value;
            document.getElementById('value-constant-compensation').textContent = document.getElementById('slider-constant-compensation').value;
        }

        new p5(p => {
            sketch = p;
            p.setup = function() {
                const container = document.getElementById('main-plot-container');
                const canvas = p.createCanvas(container.offsetWidth, container.offsetHeight + 150);
                canvas.parent('main-plot-container');
                p.noLoop();
                setupEventListeners();
                updateSliderValues();
            };

            p.draw = function() {
                p.clear();

                const state = getState();
                const startX = 60;
                const endX = p.width - 60;
                const plotWidth = endX - startX;
                const plotStartY = 40;
                const plotHeight = p.height - 250;
                const budgetStartY = plotStartY + plotHeight + 40;

                // Calculate data
                const years = [];
                const realEmissions = [];
                const effectiveEmissions = [];
                const compensated = [];
                const costs = [];
                const cumulative = [];

                let cumulativeEffective = 0;

                for (let year = 2020; year <= 2050; year++) {
                    years.push(year);

                    const initial = state.annualEmissions;
                    let re;
                    if (year < state.startYear) {
                        re = initial;
                    } else {
                        re = initial * Math.pow(0.5, (year - state.startYear) / state.reductionTime);
                    }

                    const fc = re * (state.fractionalCompensation / 100);
                    const cc = initial * (state.constantCompensation / 100);
                    const comp = fc + cc;
                    const eff = re - comp;

                    realEmissions.push(re);
                    compensated.push(comp);
                    effectiveEmissions.push(eff);
                    costs.push(comp * state.compensationCost);

                    cumulativeEffective += eff;
                    cumulative.push(cumulativeEffective);
                }

                // Scale factor
                let scaleFactor = 1;
                let yLabel = 'annual CO₂ emissions per person (tons)';
                let budgetLabel = 'individual climate budget';
                if (state.scale === 'population') {
                    scaleFactor = state.population * 1000;
                    yLabel = 'annual city emission of CO₂ (mil tons)';
                    budgetLabel = 'population climate budget';
                }

                // Draw axes
                p.stroke(0);
                p.strokeWeight(1);
                p.line(startX, plotStartY, startX, plotStartY + plotHeight);
                p.line(startX, plotStartY + plotHeight, endX, plotStartY + plotHeight);

                // Y-axis labels
                p.push();
                p.translate(20, plotStartY + plotHeight/2);
                p.rotate(-p.PI/2);
                p.textAlign('center');
                p.textSize(12);
                p.text(yLabel, 0, 0);
                p.pop();

                // Find max value for scaling
                let maxY = p.max(p.max(realEmissions), p.max(p.abs(cumulative))) * scaleFactor;
                if (state.type === 'costs') {
                    maxY = p.max(costs) * scaleFactor;
                }
                maxY = Math.ceil(maxY * 1.2 / 10) * 10;

                // Plot based on type
                if (state.type === 'emissions') {
                    // Draw real emissions (red)
                    p.stroke(220, 20, 60);
                    p.strokeWeight(2);
                    p.noFill();
                    p.beginShape();
                    for (let i = 0; i < years.length; i++) {
                        const x = startX + (years[i] - 2020) / 30 * plotWidth;
                        const y = plotStartY + plotHeight - (realEmissions[i] * scaleFactor / maxY * plotHeight);
                        p.vertex(x, y);
                    }
                    p.endShape();

                    // Draw effective emissions (black)
                    p.stroke(0);
                    p.strokeWeight(2);
                    p.beginShape();
                    for (let i = 0; i < years.length; i++) {
                        const x = startX + (years[i] - 2020) / 30 * plotWidth;
                        const y = plotStartY + plotHeight - (effectiveEmissions[i] * scaleFactor / maxY * plotHeight);
                        p.vertex(x, y);
                    }
                    p.endShape();

                    // Fill areas
                    p.noStroke();

                    // Blue area (compensated)
                    p.fill(100, 150, 255, 100);
                    p.beginShape();
                    for (let i = 0; i < years.length; i++) {
                        const x = startX + (years[i] - 2020) / 30 * plotWidth;
                        const y1 = plotStartY + plotHeight - (realEmissions[i] * scaleFactor / maxY * plotHeight);
                        const y2 = plotStartY + plotHeight - (effectiveEmissions[i] * scaleFactor / maxY * plotHeight);
                        p.vertex(x, y1);
                    }
                    for (let i = years.length - 1; i >= 0; i--) {
                        const x = startX + (years[i] - 2020) / 30 * plotWidth;
                        const y = plotStartY + plotHeight - (effectiveEmissions[i] * scaleFactor / maxY * plotHeight);
                        p.vertex(x, y);
                    }
                    p.endShape(p.CLOSE);

                    // Red and green areas
                    p.beginShape();
                    for (let i = 0; i < years.length; i++) {
                        const x = startX + (years[i] - 2020) / 30 * plotWidth;
                        const y = plotStartY + plotHeight - (effectiveEmissions[i] * scaleFactor / maxY * plotHeight);
                        if (effectiveEmissions[i] >= 0) {
                            p.fill(220, 20, 60, 100);
                        } else {
                            p.fill(40, 180, 100, 100);
                        }
                        p.rect(x - plotWidth/62, y > plotStartY + plotHeight ? plotStartY + plotHeight : y, 
                               plotWidth/31, y > plotStartY + plotHeight ? 0 : (plotStartY + plotHeight - y));
                    }

                    // Annotations
                    if (state.neutralYear) {
                        const x = startX + (state.neutralYear - 2020) / 30 * plotWidth;
                        p.stroke(0);
                        p.strokeWeight(1);
                        p.drawingContext.setLineDash([5, 5]);
                        p.line(x, plotStartY, x, plotStartY + plotHeight);
                        p.drawingContext.setLineDash([]);
                        p.fill(0);
                        p.noStroke();
                        p.textAlign('center');
                        p.textSize(12);
                        p.text('climate neutral ' + state.neutralYear, x, plotStartY - 10);

                        const cumIndex = state.neutralYear - 2020;
                        const cumVal = cumulative[cumIndex] * scaleFactor;
                        const y = plotStartY + plotHeight - (cumVal / maxY * plotHeight);
                        p.drawingContext.setLineDash([5, 5]);
                        p.line(startX, y, endX, y);
                        p.drawingContext.setLineDash([]);
                        p.textAlign('left');
                        p.text(`CO₂ emissions until ${state.neutralYear} = ${cumVal.toFixed(1)} tons CO₂`, startX + 5, y - 5);
                    }

                    const total2050 = cumulative[cumulative.length - 1] * scaleFactor;
                    const y2050 = plotStartY + plotHeight - (total2050 / maxY * plotHeight);
                    p.drawingContext.setLineDash([5, 5]);
                    p.line(startX, y2050, endX, y2050);
                    p.drawingContext.setLineDash([]);
                    p.textAlign('right');
                    p.text(`CO₂ emissions until 2050 = ${total2050.toFixed(1)} tons CO₂`, endX - 5, y2050 - 5);

                } else { // costs
                    // Blue line for costs
                    p.stroke(100, 150, 255);
                    p.strokeWeight(2);
                    p.noFill();
                    p.beginShape();
                    for (let i = 0; i < years.length; i++) {
                        const x = startX + (years[i] - 2020) / 30 * plotWidth;
                        const y = plotStartY + plotHeight - (costs[i] * scaleFactor / maxY * plotHeight);
                        p.vertex(x, y);
                    }
                    p.endShape();

                    // Fill area under curve
                    p.fill(100, 150, 255, 100);
                    p.beginShape();
                    p.vertex(startX, plotStartY + plotHeight);
                    for (let i = 0; i < years.length; i++) {
                        const x = startX + (years[i] - 2020) / 30 * plotWidth;
                        const y = plotStartY + plotHeight - (costs[i] * scaleFactor / maxY * plotHeight);
                        p.vertex(x, y);
                    }
                    p.vertex(endX, plotStartY + plotHeight);
                    p.endShape(p.CLOSE);

                    // Annotations
                    const totalCost = p.sum(costs) * scaleFactor;
                    const avgAnnual = totalCost / 31;
                    const avgY = plotStartY + plotHeight - (avgAnnual / maxY * plotHeight);
                    p.drawingContext.setLineDash([5, 5]);
                    p.line(startX, avgY, endX, avgY);
                    p.drawingContext.setLineDash([]);
                    p.fill(0);
                    p.noStroke();
                    p.textAlign('left');
                    p.text(`annual personal compensation costs (average) = $${avgAnnual.toFixed(0)} per year`, startX + 5, avgY - 5);
                    p.textAlign('center');
                    p.text(`total personal compensation costs (until 2050) = $${totalCost.toFixed(0)}`, startX + plotWidth/2, plotStartY + plotHeight + 20);
                }

                // Budget circles
                const budgetYears = [2020, 2030, 2040, 2050];
                const circleSpacing = plotWidth / 4;
                let currentX = startX + circleSpacing / 2;

                p.textAlign('center');
                p.textSize(12);
                p.text(budgetLabel, startX + plotWidth/2, budgetStartY - 10);

                budgetYears.forEach((year, index) => {
                    const yearIndex = year - 2020;
                    const totalBudget = 30 * state.annualBudget * scaleFactor;
                    const circleValue = totalBudget - (cumulative[yearIndex] * scaleFactor);

                    // Circle style
                    const isPositive = effectiveEmissions[yearIndex] <= 0;
                    const maxRadius = 40;
                    const radius = Math.min(maxRadius, Math.sqrt(Math.abs(circleValue)) * 3);

                    p.push();
                    p.translate(currentX + index * circleSpacing, budgetStartY + maxRadius);

                    if (isPositive) {
                        p.fill(40, 180, 100, 200);
                        p.noStroke();
                        p.circle(0, 0, radius * 2);
                    } else {
                        p.noFill();
                        p.stroke(220, 20, 60, 200);
                        p.strokeWeight(2);
                        p.circle(0, 0, radius * 2);
                    }

                    p.fill(0);
                    p.noStroke();
                    p.textSize(10);
                    p.text(`${circleValue.toFixed(1)} tons CO₂`, 0, radius + 15);
                    p.text(`${year}`, 0, radius + 25);

                    p.pop();
                });
            };

            p.windowResized = function() {
                const container = document.getElementById('main-plot-container');
                p.resizeCanvas(container.offsetWidth, container.offsetHeight + 150);
            };
        });
    </script>
</body>
</html>
