    </style>
</head>
<body>
    <!-- Main container for the entire demo -->
    <div id="main-container">
        <!-- Control Panel -->
        <div id="control-panel">
            <!-- Solid selector buttons -->
            <div id="solid-selector">
                <label>solid</label>
                <button id="btn-solid-1" class="solid-btn active">1</button>
                <button id="btn-solid-2" class="solid-btn">2</button>
                <button id="btn-solid-3" class="solid-btn">3</button>
            </div>
            <!-- Fold slider -->
            <div id="fold-slider-container">
                <label for="slider-fold">fold-unfold</label>
                <input type="range" id="slider-fold" min="0" max="1" step="0.01" value="0">
            </div>
            <!-- Reset view button -->
            <button id="btn-reset-view" title="Reset view">+</button>
        </div>

        <!-- Visualization Canvas -->
        <div id="canvas-container"></div>
    </div>

    <script>
        /* ========= Three.js Setup ========= */
        const canvasContainer = document.getElementById('canvas-container');
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xf0f0f0);

        const camera = new THREE.PerspectiveCamera(
            45,
            canvasContainer.clientWidth / canvasContainer.clientHeight,
            0.1,
            1000
        );
        camera.position.set(0, 0, 6);

        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
        renderer.setPixelRatio(window.devicePixelRatio);
        canvasContainer.appendChild(renderer.domElement);

        /* Lighting */
        const amLight = new THREE.AmbientLight(0xffffff, 0.6);
        scene.add(amLight);
        const dirLight = new THREE.DirectionalLight(0xffffff, 0.8);
        dirLight.position.set(5, 10, 7);
        scene.add(dirLight);

        /* OrbitControls */
        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.05;

        /* Resize handler */
        window.addEventListener('resize', () => {
            const w = canvasContainer.clientWidth;
            const h = canvasContainer.clientHeight;
            camera.aspect = w / h;
            camera.updateProjectionMatrix();
            renderer.setSize(w, h);
        });

        /* ========= Geometry Definitions ========= */
        const materials = {
            // 6 unique colors for the faces
            color0: new THREE.MeshStandardMaterial({ color: 0xE47A55, side: THREE.DoubleSide }),  // orange
            color1: new THREE.MeshStandardMaterial({ color: 0xF3C76E, side: THREE.DoubleSide }),  // yellow
            color2: new THREE.MeshStandardMaterial({ color: 0xA4C6E3, side: THREE.DoubleSide }),  // light blue
            color3: new THREE.MeshStandardMaterial({ color: 0xF4E0E1, side: THREE.DoubleSide }),  // pink
            color4: new THREE.MeshStandardMaterial({ color: 0xC5D85E, side: THREE.DoubleSide }),    // light green
            color5: new THREE.MeshStandardMaterial({ color: 0xB97A99, side: THREE.DoubleSide })   // purple
        };
        const unfoldedColor = new THREE.Color(0xF5E0E0);  // light pink start color

        /* Create geometry for a polyhedron */
        function createSolid(data) {
            const solid = new THREE.Group();
            // Process vertices
            const vertices2D = data.vertices.map(([x, y]) => new THREE.Vector2(x, y));
            // Build faces and hinges
            data.faces.forEach((face, idx) => {
                // Create vertices
                const triangle = face.verts.map(i => {
                    return new THREE.Vector3(vertices2D[i].x, vertices2D[i].y, 0);
                });
                const geom = new THREE.BufferGeometry().setFromPoints(triangle);
                geom.setIndex([0, 1, 2]);
                geom.computeVertexNormals();
                // Create material
                const mat = materials[data.colors[idx]].clone();
                const mesh = new THREE.Mesh(geom, mat);
                mesh.userData.unfoldedColor = unfoldedColor;
                mesh.userData.foldedColor = materials[data.colors[idx]].color.clone();
                mesh.userData.finalAngle = data.faces[idx].angle || 0;
                // Add to root or attach via pivot if parent provided
                if (face.parent !== undefined) {
                    const parent = solid.children[face.parent].mesh; // Assume child index order
                    // Create pivot
                    const pivot = new THREE.Object3D();
                    // Map hinge start/end points to 3D coords
                    const [start, end] = face.hinge.map(i => new THREE.Vector3(vertices2D[i].x, vertices2D[i].y, 0));
                    pivot.position.copy(start);
                    // make pivot look along hinge
                    pivot.lookAt(end);
                    parent.add(pivot);
                    // offset mesh position
                    mesh.position.copy(start.clone().negate());
                    pivot.add(mesh);
                } else {
                    solid.add(mesh);
                }
                // store mesh reference for later access
                solid.children[idx]?.mesh ? null : solid.children[idx] = { mesh };
            });
            return solid;
        }

        /* ===== Solid Definitions ===== */
        const solidDefs = {
            1: {
                vertices: [
                    [-1, 0], [1, 0], [0, -1.73],   // base triangle center vertex
                    [0, 1.73],                      // top apex
                    [-2, 0],                         // left base extension
                    [2, 0]                           // right base extension
                ],
                faces: [
                    { verts: [0, 2, 1], colors: 'color0', angle: 0.60466 },  // bottom center triangle, must fold 35deg inward
                    { verts: [0, 4, 2], colors: 'color1', angle: 1.0172, parent: 0, hinge: [0, 2] },
                    { verts: [1, 2, 5], colors: 'color2', angle: -1.0172, parent: 0, hinge: [1, 2] },
                    { verts: [0, 1, 3], colors: 'color3', angle: 1.23096, parent: 0, hinge: [0 , 1] },
                    { verts: [4, 3, 0], colors: 'color4', angle: 1.53089, parent: 3, hinge: [0 , 3] },
                    { verts: [5, 3, 1], colors: 'color5', angle: -1.53089, parent: 3, hinge: [1 , 3] }
                ]
            },
            2: {
                vertices: [
                    [-1,-1], [1,-1], [-1,1], [1,1]
                ],
                faces: [
                    { verts: [0, 2, 3], colors: 'color0', angle: 0 },
                    { verts: [0, 3, 1], colors: 'color1', angle: 1.5708, parent: 0, hinge: [0, 3] },
                ]
            },
            3: {
                vertices: [
                    [-1, -1], [1, -1], [0, -2],
                    [0, 2], [-1, 1], [1, 1]
                ],
                faces: [
                    { verts: [0, 2, 1], colors: 'color0', angle: 0 },
                    { verts: [0, 3, 5], colors: 'color1', angle: -Math.PI / 2, parent: 0, hinge: [0, 1] },
                    { verts: [0, 4, 3], colors: 'color2', angle: Math.PI / 2, parent: 1, hinge: [0, 5] }
                ]
            }
        };

        /* ===== State and Controls ===== */
        let currentSolid = null;
        const solidSelectorBtns = document.querySelectorAll('.solid-btn');
        const foldSlider = document.getElementById('slider-fold');
        const resetBtn = document.getElementById('btn-reset-view');

        function selectSolid(solidIdx) {
            const key = solidIdx + 1;
            if (currentSolid) scene.remove(currentSolid);
            currentSolid = createSolid(solidDefs[key]);
            scene.add(currentSolid);
            updateFolding(parseFloat(foldSlider.value));
            controls.reset();
        }

        function updateFolding(t) {
            if (!currentSolid) return;
            // Traverse pivots and faces to animate
            currentSolid.traverse((child) => {
                if (child.isMesh) {
                    const targetAngle = child.userData.finalAngle || 0;
                    const mesh = child;
                    if (mesh.material) {
                        const unfolded = mesh.userData.unfoldedColor;
                        const folded   = mesh.userData.foldedColor;
                        mesh.material.color.copy(unfolded.clone().lerp(folded, t));
                    }
                }
                // Apply rotation to hinges (pivot objects)
                if (child.userData.isPivot && child.userData.finalAngle !== undefined) {
                    child.rotation.y = t * child.userData.finalAngle;
                }
            });
            // Apply rotation based on pivot
            currentSolid.children.forEach((groupChild, idx) => {
                if (!groupChild.mesh) return; // sanity
                const face = solidDefs[1 + parseInt(document.querySelector('.solid-btn.active').textContent) - 1].faces[idx];
                if (!face) return;
                // Look for pivot
                let obj = groupChild.mesh;
                while(obj.parent && obj.parent !== currentSolid) {
                    if (obj.userData.finalAngle !== undefined) {
                        obj.userData.isPivot = true;  // mark pivot
                        obj.rotation.y = t * (face.angle || 0);
                    }
                    obj = obj.parent;
                }
            });
        }

        /* Hook up controls */
        solidSelectorBtns.forEach(btn => {
            btn.addEventListener('click', (e) => {
                solidSelectorBtns.forEach(b => b.classList.remove('active'));
                btn.classList.add('active');
                selectSolid(parseInt(btn.textContent) - 1);
            });
        });

        foldSlider.addEventListener('input', () => {
            const t = parseFloat(foldSlider.value);
            updateFolding(t);
        });

        resetBtn.addEventListener('click', () => {
            controls.reset();
        });

        /* ===== Initial Loading ===== */
        selectSolid(0);  // start with solid 1

        /* Animation loop */
        function animate() {
            controls.update();
            renderer.render(scene, camera);
            requestAnimationFrame(animate);
        }
        animate();
    </script>
</body>
</html>
