</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <h3>relative volatility</h3>
            <button id="btn-reset">+</button>
            <div class="slider-row">
                <label for="slider-ac">between A and C</label>
                <input type="range" id="slider-ac" min="0.1" max="5.0" step="0.01" value="0.6">
                <span id="value-ac">0.60</span>
            </div>
            <div class="slider-row">
                <label for="slider-bc">between B and C</label>
                <input type="range" id="slider-bc" min="0.1" max="5.0" step="0.01" value="3.93">
                <span id="value-bc">3.93</span>
            </div>
        </div>
        <div id="visualization-container">
            <canvas id="ternary-plot-canvas"></canvas>
        </div>
    </div>

    <script>
        let alpha13 = 0.6;
        let alpha23 = 3.93;
        let canvas;

        function setup() {
            canvas = createCanvas(600, 550);
            canvas.parent('ternary-plot-canvas');

            // Set up event listeners
            document.getElementById('slider-ac').addEventListener('input', function(e) {
                alpha13 = parseFloat(e.target.value);
                document.getElementById('value-ac').textContent = alpha13.toFixed(2);
                draw();
            });

            document.getElementById('slider-bc').addEventListener('input', function(e) {
                alpha23 = parseFloat(e.target.value);
                document.getElementById('value-bc').textContent = alpha23.toFixed(2);
                draw();
            });

            document.getElementById('btn-reset').addEventListener('click', function() {
                alpha13 = 0.6;
                alpha23 = 3.93;
                document.getElementById('slider-ac').value = alpha13;
                document.getElementById('slider-bc').value = alpha23;
                document.getElementById('value-ac').textContent = alpha13.toFixed(2);
                document.getElementById('value-bc').textContent = alpha23.toFixed(2);
                draw();
            });

            draw();
        }

        function draw() {
            clear();
            background(255);

            const margin = 60;
            const centerX = width / 2;
            const centerY = height / 2 + 20;
            const triangleSize = 400;

            // Calculate triangle vertices
            const A = { x: centerX, y: centerY - triangleSize * Math.sqrt(3) / 3 };
            const B = { x: centerX - triangleSize / 2, y: centerY + triangleSize * Math.sqrt(3) / 6 };
            const C = { x: centerX + triangleSize / 2, y: centerY + triangleSize * Math.sqrt(3) / 6 };

            // Draw grid triangles
            const N = 10;
            for (let i = 0; i < N; i++) {
                for (let j = 0; j < N - i; j++) {
                    const k = N - i - j;

                    // Calculate centroid barycentric coordinates
                    const x1 = (i + 0.5) / N;
                    const x2 = (j + 0.5) / N;
                    const x3 = (k - 0.5) / N;

                    // Calculate F value
                    const F = calculateF(x1, x2, x3);
                    const v = Math.min(F / 2.0, 1.0);

                    // Get color
                    const color = getColor(v);
                    fill(color.r, color.g, color.b);
                    stroke(0);
                    strokeWeight(1);

                    // Calculate triangle vertices
                    const v1 = barycentricToCartesian(x1 + 0.5/N, x2 - 0.5/N, x3, A, B, C);
                    const v2 = barycentricToCartesian(x1 - 0.5/N, x2 + 0.5/N, x3, A, B, C);
                    const v3 = barycentricToCartesian(x1, x2 - 0.5/N, x3 + 0.5/N, A, B, C);

                    triangle(v1.x, v1.y, v2.x, v2.y, v3.x, v3.y);
                }
            }

            // Draw triangle outline
            stroke(0);
            strokeWeight(2);
            noFill();
            triangle(A.x, A.y, B.x, B.y, C.x, C.y);

            // Draw labels
            fill(0);
            noStroke();
            textSize(16);
            textAlign(CENTER, CENTER);
            text('A', A.x, A.y - 20);
            text('B', B.x - 20, B.y + 10);
            text('C', C.x + 20, C.y + 10);

            // Draw tick marks
            textSize(10);
            for (let t = 0; t <= 10; t++) {
                const tNorm = t / 10;

                // AB ticks
                const abX = A.x + (B.x - A.x) * tNorm;
                const abY = A.y + (B.y - A.y) * tNorm;
                text(tNorm.toFixed(1), abX - 10, abY - 10);

                // AC ticks
                const acX = A.x + (C.x - A.x) * tNorm;
                const acY = A.y + (C.y - A.y) * tNorm;
                text(tNorm.toFixed(1), acX + 10, acY - 10);

                // BC ticks
                const bcX = B.x + (C.x - B.x) * tNorm;
                const bcY = B.y + (C.y - B.y) * tNorm;
                text((1 - tNorm).toFixed(1), bcX, bcY + 15);
            }
        }

        function calculateF(x1, x2, x3) {
            const alpha1 = alpha13;
            const alpha2 = alpha23;
            const alpha3 = 1;

            const D = alpha1 * x1 + alpha2 * x2 + alpha3 * x3;
            const y1 = (alpha1 * x1) / D;
            const y2 = (alpha2 * x2) / D;
            const y3 = (alpha3 * x3) / D;

            return Math.pow(x1 - y1, 2) + Math.pow(x2 - y2, 2) + Math.pow(x3 - y3, 2);
        }

        function barycentricToCartesian(x1, x2, x3, A, B, C) {
            return {
                x: x1 * A.x + x2 * B.x + x3 * C.x,
                y: x1 * A.y + x2 * B.y + x3 * C.y
            };
        }

        function getColor(v) {
            const colors = [
                { r: 44, g: 123, b: 182 },   // Dark Blue
                { r: 171, g: 217, b: 233 },  // Light Blue
                { r: 255, g: 255, b: 191 },  // Green-Yellow
                { r: 253, g: 174, b: 97 },   // Orange
                { r: 215, g: 25, b: 28 }     // Red
            ];

            const stops = [0, 0.25, 0.5, 0.75, 1.0];

            if (v <= 0) return colors[0];
            if (v >= 1) return colors[4];

            for (let i = 0; i < stops.length - 1; i++) {
                if (v >= stops[i] && v <= stops[i + 1]) {
                    const t = (v - stops[i]) / (stops[i + 1] - stops[i]);
                    return {
                        r: Math.round(colors[i].r + t * (colors[i + 1].r - colors[i].r)),
                        g: Math.round(colors[i].g + t * (colors[i + 1].g - colors[i].g)),
                        b: Math.round(colors[i].b + t * (colors[i + 1].b - colors[i].b))
                    };
                }
            }

            return colors[0];
        }
    </script>
</body>
</html>
