    <script>
        // Initialize plot
        const kRange = [];
        for (let k = -5; k <= 5; k += 0.1) {
            kRange.push(k);
        }

        function calculateEnergies(n, wso, BX, BY, BZ) {
            const E_plus = [];
            const E_minus = [];

            kRange.forEach(k => {
                const sqrt_term = Math.sqrt(Math.pow((BZ + wso * k), 2) + Math.pow(BX, 2) + Math.pow(BY, 2));
                E_plus.push((2 * n + 1) + k * k + sqrt_term);
                E_minus.push((2 * n + 1) + k * k - sqrt_term);
            });

            return { E_plus, E_minus };
        }

        function createTitle(n, wso, BX, BY, BZ) {
            let title = `n = ${n}`;
            const params = [
                { name: 'wso', value: wso },
                { name: 'BX', value: BX },
                { name: 'BY', value: BY },
                { name: 'BZ', value: BZ }
            ];

            params.forEach(param => {
                if (param.value > 0) {
                    title += `, ${param.name} = ${param.value.toFixed(1)}`;
                }
            });

            return title;
        }

        function updatePlot() {
            const n = parseFloat(document.getElementById('slider-n').value);
            const wso = parseFloat(document.getElementById('slider-wso').value);
            const BX = parseFloat(document.getElementById('slider-bx').value);
            const BY = parseFloat(document.getElementById('slider-by').value);
            const BZ = parseFloat(document.getElementById('slider-bz').value);

            const { E_plus, E_minus } = calculateEnergies(n, wso, BX, BY, BZ);

            const showBoth = wso !== 0 || BX !== 0 || BY !== 0 || BZ !== 0;

            let data;
            if (showBoth) {
                data = [
                    {
                        x: kRange,
                        y: E_plus,
                        mode: 'lines',
                        line: { color: 'red', dash: 'solid', width: 2 },
                        name: 'E₊'
                    },
                    {
                        x: kRange,
                        y: E_minus,
                        mode: 'lines',
                        line: { color: 'red', dash: 'dash', width: 2 },
                        name: 'E₋'
                    }
                ];
            } else {
                data = [{
                    x: kRange,
                    y: E_plus,
                    mode: 'lines',
                    line: { color: 'red', dash: 'solid', width: 2 },
                    name: 'degenerate'
                }];
            }

            const layout = {
                title: createTitle(n, wso, BX, BY, BZ),
                xaxis: {
                    title: 'normalized k length (lwky)',
                    range: [-5, 5],
                    zeroline: true
                },
                yaxis: {
                    title: '2 E / &#x210F;&omega;',
                    range: [0, 40],
                    zeroline: true
                },
                showlegend: false
            };

            Plotly.react('plot-container', data, layout);
        }

        // Add event listeners
        ['slider-wso', 'slider-n', 'slider-bx', 'slider-by', 'slider-bz'].forEach(id => {
            const slider = document.getElementById(id);
            const valueSpan = document.getElementById('value-' + id.split('-')[1]);

            slider.addEventListener('input', function() {
                valueSpan.textContent = parseFloat(this.value).toFixed(1);
                updatePlot();
            });
        });

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
