<body>
    <div id="app-container">
        <div id="controls-container">
            <div class="panel-header">
                <h3>Controls</h3>
                <button id="toggle-controls">−</button>
            </div>
            <div id="sliders-area">
                <div class="slider-group">
                    <h4>Precipitation</h4>
                    <div class="slider-container">
                        <label>hour 1</label>
                        <input type="range" id="slider-precip-1" min="0" max="10" step="1" value="1">
                        <span id="value-precip-1">1</span>
                    </div>
                    <div class="slider-container">
                        <label>hour 2</label>
                        <input type="range" id="slider-precip-2" min="0" max="10" step="1" value="0">
                        <span id="value-precip-2">0</span>
                    </div>
                    <div class="slider-container">
                        <label>hour 3</label>
                        <input type="range" id="slider-precip-3" min="0" max="10" step="1" value="0">
                        <span id="value-precip-3">0</span>
                    </div>
                </div>
                <div class="slider-group">
                    <h4>UH streamflow rates</h4>
                    <div class="slider-container">
                        <label>hour 1</label>
                        <input type="range" id="slider-uh-1" min="0" max="10" step="1" value="1">
                        <span id="value-uh-1">1</span>
                    </div>
                    <div class="slider-container">
                        <label>hour 2</label>
                        <input type="range" id="slider-uh-2" min="0" max="10" step="1" value="5">
                        <span id="value-uh-2">5</span>
                    </div>
                    <div class="slider-container">
                        <label>hour 3</label>
                        <input type="range" id="slider-uh-3" min="0" max="10" step="1" value="3">
                        <span id="value-uh-3">3</span>
                    </div>
                    <div class="slider-container">
                        <label>hour 4</label>
                        <input type="range" id="slider-uh-4" min="0" max="10" step="1" value="1">
                        <span id="value-uh-4">1</span>
                    </div>
                </div>
            </div>
        </div>

        <div id="plots-container">
            <div id="plot-canvas-1"></div>
            <div id="plot-canvas-2"></div>
            <div id="plot-canvas-3"></div>
            <div id="plot-canvas-4"></div>
            <div id="plot-canvas-5"></div>
            <div id="plot-canvas-6"></div>
        </div>
    </div>

    <script>
        // Global variables
        let canvases = {};
        let sketches = {};

        // Slider values
        let P = [1, 0, 0]; // Precipitation values
        let U = [1, 5, 3, 1]; // UH values

        // Calculated values
        let C1 = [];
        let C2 = [];
        let C3 = [];
        let Q = [];

        // Initialize canvases
        function initCanvases() {
            for (let i = 1; i <= 6; i++) {
                const canvasContainer = document.getElementById(`plot-canvas-${i}`);
                const canvasDiv = document.createElement('div');
                canvasDiv.className = 'plot-canvas-inner';
                canvasDiv.id = `canvas${i}`;
                canvasContainer.appendChild(canvasDiv);

                const sketch = createSketch(i, canvasDiv.id);
                sketches[i] = sketch;
                canvases[i] = new p5(sketch, canvasDiv.id);
            }
        }

        // Create p5.js sketch
        function createSketch(plotNumber, canvasId) {
            return function(p) {
                p.setup = function() {
                    p.createCanvas(300, 250);
                };

                p.drawPlot = function(data, xLabel, yLabel, xMax, yMax, timeOffset = 0, fillColor, strokeColor) {
                    p.clear();

                    const padding = 40;
                    const graphWidth = p.width - padding * 2;
                    const graphHeight = p.height - padding * 2;

                    // Fill and stroke colors
                    const fillCol = p.color(212, 230, 241, 150);
                    const strokeCol = p.color(46, 134, 193);
                    p.fill(fillCol);
                    p.stroke(strokeCol);
                    p.strokeWeight(2);

                    // Scale factors
                    const xScale = graphWidth / xMax;
                    const yScale = graphHeight / yMax;

                    // Draw axes
                    p.stroke(0);
                    p.line(padding, p.height - padding, padding + graphWidth, p.height - padding);
                    p.line(padding, padding, padding, p.height - padding);

                    // Draw data
                    if (data && data.length > 0) {
                        p.beginShape();
                        p.vertex(padding, p.height - padding);

                        for (let i = 0; i <= data.length; i++) {
                            if (i < data.length) {
                                const x1 = padding + (i + timeOffset) * xScale;
                                const y1 = p.height - padding - data[i] * yScale;
                                const x2 = padding + (i + 1 + timeOffset) * xScale;
                                const y2 = p.height - padding - data[i] * yScale;

                                p.vertex(x1, y1);
                                p.vertex(x2, y2);
                            }
                        }

                        p.vertex(padding + (data.length + timeOffset) * xScale, p.height - padding);
                        p.endShape(p.CLOSE);
                    }

                    // Draw axis labels and ticks
                    p.fill(0);
                    p.noStroke();
                    p.textAlign(p.CENTER, p.CENTER);

                    // Y-axis ticks
                    for (let i = 0; i <= yMax / 2; i++) {
                        const y = p.height - padding - (i * 2) * yScale;
                        p.text(i * 2, padding - 20, y);
                    }

                    // X-axis ticks
                    for (let i = 0; i <= xMax / 2; i++) {
                        const x = padding + (i * 2) * xScale;
                        p.text(i * 2, x, p.height - padding + 15);
                    }

                    // Labels
                    p.textAlign(p.CENTER, p.CENTER);
                    p.text(xLabel, padding + graphWidth / 2, p.height - padding / 2);

                    p.push();
                    p.translate(15, p.height / 2);
                    p.rotate(-p.HALF_PI);
                    p.text(yLabel, 0, 0);
                    p.pop();
                }

                p.draw = function() {
                    // This will be called by update()
                    if (plotNumber === 1) {
                        this.drawPlot(P, 't (hr)', 'P (in)', 5, 10);
                    } else if (plotNumber === 2) {
                        this.drawPlot(U, 't (hr)', 'Q (cfs/in)', 5, 10);
                    } else if (plotNumber === 3) {
                        this.drawPlot(C1, 't (hr)', 'Q (cfs)', 8, 100, 0);
                    } else if (plotNumber === 4) {
                        this.drawPlot(C2, 't (hr)', 'Q (cfs)', 8, 100, 1);
                    } else if (plotNumber === 5) {
                        this.drawPlot(C3, 't (hr)', 'Q (cfs)', 8, 100, 2);
                    } else if (plotNumber === 6) {
                        this.drawPlot(Q, 't (hr)', 'Q (cfs)', 8, 100, 0);
                    }
                }
            }
        }

        // Update function
        function update() {
            // Read values from sliders
            P = [
                parseInt(document.getElementById('slider-precip-1').value),
                parseInt(document.getElementById('slider-precip-2').value),
                parseInt(document.getElementById('slider-precip-3').value)
            ];

            U = [
                parseInt(document.getElementById('slider-uh-1').value),
                parseInt(document.getElementById('slider-uh-2').value),
                parseInt(document.getElementById('slider-uh-3').value),
                parseInt(document.getElementById('slider-uh-4').value)
            ];

            // Calculate component hydrographs
            C1 = U.map(val => P[0] * val);
            C2 = U.map(val => P[1] * val);
            C3 = U.map(val => P[2] * val);

            // Calculate composite hydrograph via convolution
            Q = [];
            for (let t = 0; t < 6; t++) {
                let sum = 0;
                for (let k = 0; k < 3; k++) {
                    if (t - k >= 0 && t - k < 4) {
                        sum += P[k] * U[t - k];
                    }
                }
                Q[t] = sum;
            }

            // Update all plots
            for (let i = 1; i <= 6; i++) {
                if (canvases[i]) {
                    canvases[i].redraw();
                }
            }
        }

        // Initialize event listeners
        function initEventListeners() {
            // Slider event listeners
            for (let i = 1; i <= 3; i++) {
                const slider = document.getElementById(`slider-precip-${i}`);
                const valueSpan = document.getElementById(`value-precip-${i}`);
                slider.addEventListener('input', (e) => {
                    valueSpan.textContent = e.target.value;
                    update();
                });
            }

            for (let i = 1; i <= 4; i++) {
                const slider = document.getElementById(`slider-uh-${i}`);
                const valueSpan = document.getElementById(`value-uh-${i}`);
                slider.addEventListener('input', (e) => {
                    valueSpan.textContent = e.target.value;
                    update();
                });
            }

            // Toggle button event listener
            const toggleBtn = document.getElementById('toggle-controls');
            const slidersArea = document.getElementById('sliders-area');
            toggleBtn.addEventListener('click', () => {
                const isVisible = slidersArea.style.display !== 'none';
                if (isVisible) {
                    slidersArea.style.display = 'none';
                    toggleBtn.textContent = '+';
                } else {
                    slidersArea.style.display = 'block';
                    toggleBtn.textContent = '−';
                }
            });
        }

        // Initialize application
        document.addEventListener('DOMContentLoaded', () => {
            initCanvases();
            initEventListeners();
            update();
        });
    </script>
</body>
</html>
