          </tr>
          <tr>
            <th>Player 1:2</th>
            <td id="cell21">a<sub>21</sub>, b<sub>21</sub></td>
            <td id="cell22">a<sub>22</sub>, b<sub>22</sub></td>
          </tr>
        </table>
      </div>
    </div>

    <!-- Right panel -->
    <div id="visualization-panel">
      <div id="canvas-container"></div>
      <h4><span>set of Nash equilibria</span></h4>
      <div id="equilibria-display">\\(\\varnothing\\)</div>
    </div>
  </div>

  <script>
    // ------------------------------------------------------------------
    //  Utility: continued-fraction-based fraction simplification
    // ------------------------------------------------------------------
    function toFraction(dec, tol=1e-6){
      if(Math.abs(dec) < tol) return "0";
      if(Math.abs(1-dec) < tol) return "1";
      let negative = dec < 0;
      let x = Math.abs(dec);
      // simple version with small denominators
      let bestN = 1, bestD = 1, bestErr = Math.abs(x-1);
      for(let d=1;d<=50;d++){
        let n = Math.round(x*d);
        let err = Math.abs(x - n/d);
        if(err < bestErr){ bestErr = err; bestN = n; bestD = d; }
      }
      if(bestErr < tol) return (negative? "-":"") + (bestD===1? bestN : bestN+'/'+bestD);
      return dec.toFixed(2);
    }

    // ------------------------------------------------------------------
    //  State holder for 8 payoff matrix values
    // ------------------------------------------------------------------
    const state = {
      a11:-5, a12:-3, a21:-1, a22:-4,
      b11:-5, b12:-3, b21:-1, b22:-4
    };
    const sliderIds = ['a11','a12','a21','a22','b11','b12','b21','b22'];

    // ------------------------------------------------------------------
    //  Bind all sliders & buttons
    // ------------------------------------------------------------------
    sliderIds.forEach(k=>{
      const slider = document.getElementById(`slider-${k}`);
      const valueSpan = document.getElementById(`value-${k}`);
      const minusBtn = document.getElementById(`btn-minus-${k}`);
      const plusBtn  = document.getElementById(`btn-plus-${k}`);
      const set = v=>{
        v = Math.max(-10,Math.min(10,Math.round(v)));
        slider.value = v;
        valueSpan.innerText = v;
        // store numeric key
        state[k.slice(-2)] = v;   // e.g. 'a11' → 'a11'
        triggerUpdate();
      };
      slider.addEventListener('input',()=>set(parseFloat(slider.value)));
      minusBtn.addEventListener('click',()=>set(parseFloat(slider.value)-1));
      plusBtn.addEventListener('click',()=>set(parseFloat(slider.value)+1));
    });

    // ------------------------------------------------------------------
    //  Refresh payoff table
    // ------------------------------------------------------------------
    function updatePayoffTable(){
      document.getElementById('cell11').innerHTML = `${state.a11}, ${state.b11}`;
      document.getElementById('cell12').innerHTML = `${state.a12}, ${state.b12}`;
      document.getElementById('cell21').innerHTML = `${state.a21}, ${state.b21}`;
      document.getElementById('cell22').innerHTML = `${state.a22}, ${state.b22}`;
    }

    // ------------------------------------------------------------------
    //  Calculate best-response graphs and Nash equilibria segments
    // ------------------------------------------------------------------
    function computeEquilibria(){
      // Player 1 indiff
      const den1 = (state.a11 - state.a12 - state.a21 + state.a22);
      let y1_indiff = null;
      if(Math.abs(den1)>1e-6){
        y1_indiff = (state.a22 - state.a12)/den1;
      }

      // Player 2 indiff
      const den2 = (state.b11 - state.b12 - state.b21 + state.b22);
      let x1_indiff = null;
      if(Math.abs(den2)>1e-6){
        x1_indiff = (state.b22 - state.b21)/den2;
      }

      // Build P1's best response polyline
      let p1Poly = [];
      (()=>{
        if(Math.abs(den1)<1e-6){
          // dominant strategy
          const pref12 = state.a12 - state.a22;
          const best = pref12 > 0 ? 1 : 0;
          p1Poly.push([best,0],[best,1]);
        }else if(0<=y1_indiff && y1_indiff<=1){
          const isNeg = den1<0;
          p1Poly.push([isNeg?0:1, 0], [isNeg?0:1, y1_indiff], [isNeg?1:0, y1_indiff], [isNeg?1:0,1]);
        }else{
          // test preference at y=0.5
          const best = (state.a11 - state.a21)*0.5 > (state.a12 - state.a22)*0.5 ? 1 : 0;
          p1Poly.push([best,0],[best,1]);
        }
      })();

      // Build P2's best response polyline
      let p2Poly = [];
      (()=>{
        if(Math.abs(den2)<1e-6){
          const pref21 = state.b21 - state.b11;
          const best = pref21 > 0 ? 1 : 0;
          p2Poly.push([0,best],[1,best]);
        }else if(0<=x1_indiff && x1_indiff<=1){
          const isNeg = den2<0;
          p2Poly.push([0,isNeg?1:0], [0,isNeg?1:0], [x1_indiff,isNeg?1:0], [x1_indiff,isNeg?0:1], [1,isNeg?0:1], [1,isNeg?1:0]);
        }else{
          const best = (state.b11 - state.b12)*0.5 > (state.b21 - state.b22)*0.5 ? 1 : 0;
          p2Poly.push([0,best],[1,best]);
        }
        // simplify remove duplicate sequential points
        const simplified = [];
        for(let i=0;i<p2Poly.length;i++){
          const p = p2Poly[i];
          const last = simplified[simplified.length-1];
          if(!last || !(p[0]===last[0] && p[1]===last[1])) simplified.push(p);
        }
        p2Poly = simplified;
      })();

      // Intersection (axis-aligned segments)
      // simple brute-force segment intersection between all pairs
      const segments1 = [], segments2=[];
      for(let i=0;i<p1Poly.length-1;i++) segments1.push([p1Poly[i],p1Poly[i+1]]);
      for(let i=0;i<p2Poly.length-1;i++) segments2.push([p2Poly[i],p2Poly[i+1]]);

      let intersections = [];
      // segment intersection check
      function intersectSeg(s1,s2){
        const [a,b]=s1, [c,d]=s2;
        // horizontal vs vertical
        if(a[0]===b[0] && c[1]===d[1]){
          const x0 = a[0], y0=c[1];
          if(x0>=Math.min(c[0],d[0]) && x0<=Math.max(c[0],d[0]) && y0>=Math.min(a[1],b[1]) && y0<=Math.max(a[1],b[1])){
            return [x0,y0];
          }
        }
        if(a[1]===b[1] && c[0]===d[0]){
          const x0=c[0], y0=a[1];
          if(y0>=Math.min(c[1],d[1]) && y0<=Math.max(c[1],d[1]) && x0>=Math.min(a[0],b[0]) && x0<=Math.max(a[0],b[0])){
            return [x0,y0];
          }
        }
        return null;
      }

      const endpoints = new Set();
      segments1.forEach(seg1=>{
        segments2.forEach(seg2=>{
          const p = intersectSeg(seg1,seg2);
          if(p){
            // round to 6 decimals
            const key = p.map(v=>v.toFixed(6)).join(',');
            endpoints.add(key);
          }
        });
      });

      intersections = [...endpoints].map(k=>k.split(',').map(Number));

      // special case: overlapping segments
      // (Axis aligned overlap is complicated; we simplify to include endpoints only)

      // Return useful info
      return {p1Poly,p2Poly,intersections};
    }

    // ------------------------------------------------------------------
    //  Trigger update chain
    // ------------------------------------------------------------------
    function triggerUpdate(){
      updatePayoffTable();
      const info = computeEquilibria();

      // update p5.js
      window.dispatchEvent(new Event('redrawP5'));

      // update MathJax
      let eqText = "\\(\\varnothing\\)";
      if(info.intersections.length){
        const pts = info.intersections
              .map(p=>`(${toFraction(p[0])}, ${toFraction(p[1])})`)
              .join(', ');
        eqText = `\\(\\{${pts}\\}\\)`;
      }
      const disp = document.getElementById('equilibria-display');
      disp.innerHTML = eqText;
      MathJax.typesetPromise([disp]);
    }

    // ------------------------------------------------------------------
    //  p5.js sketch
    // ------------------------------------------------------------------
    const sketch = (p)=>{
      const W=400, H=400, PAD=30;
      let lastInfo = null;

      p.setup = ()=>{
        const c = p.createCanvas(W,H);
        c.parent('canvas-container');
        updateDrawing();
        // event listener for trigger
        window.addEventListener('redrawP5', updateDrawing);
      };

      function updateDrawing(){
        lastInfo = computeEquilibria();
        p.redraw();
      }
      p.draw = ()=>{
        p.clear();
        p.noLoop(); // one-shot redraw
        // coordinate transform
        p.push();
        p.translate(PAD,H-PAD);
        p.scale((W-2*PAD)/1.2, (H-2*PAD)/-1.2);
        p.translate(0.1,0.1);

        // axes and grid
        p.stroke(200);
        for(let v=0;v<=1;v+=0.2){
          p.line(v,0,v,1);
          p.line(0,v,1,v);
        }
        p.stroke(0);
        p.line(0,0,1,0);
        p.line(0,0,0,1);
        // labels
        p.strokeWeight(0.01);
        p.fill(0);
        p.textSize(0.05);
        p.text('x₁',1.03,0.05);
        p.text('y₁',-0.05,1.06);
        for(let v=0;v<=1;v+=0.2){
          p.text(v.toFixed(1), v, -0.05);
          p.text(v.toFixed(1), -0.1, v);
        }

        // draw blue best response
        p.stroke(0,100,255);
        p.strokeWeight(0.015);
        let pts = lastInfo.p1Poly;
        for(let i=0;i<pts.length-1;i++){
          p.line(pts[i][0], pts[i][1], pts[i+1][0], pts[i+1][1]);
        }

        // draw green best response
        p.stroke(0,150,0);
        pts = lastInfo.p2Poly;
        for(let i=0;i<pts.length-1;i++){
          p.line(pts[i][0], pts[i][1], pts[i+1][0], pts[i+1][1]);
        }

        // draw red intersection
        p.stroke(255,0,0);
        p.strokeWeight(0.03);

        const uniqueSegs = (()=>{
          // build segments between unique endpoints
          // for axis-aligned case, segments are small edges (points or lines)
          // simplest: treat each pair as segment for lines only
          // Here we only have points, so draw nothing for lines
          if(lastInfo.intersections.length===0) return [];
          // edges for non-point segments would come here
          return [];
        })();
        // Because regions may be continuous segments, drawing the vertices is sufficient here
        lastInfo.intersections.forEach(pt=>{
          p.ellipse(pt[0],pt[1],0.08,0.08);
          // we don't join continuous lines explicitly; the red circles mark endpoints
        });

        p.pop();
      };
    };

    // launch sketch
    new p5(sketch);

    // initial update
    triggerUpdate();

    // tell MathJax to typeset labels
    MathJax.typesetPromise();
  </script>
</body>
</html>
