<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <title>Quadratic Tangents Explorer</title>
  <script src="https://cdn.jsdelivr.net/npm/p5@1.9.0/lib/p5.min.js"></script>
  <style>
    body {
      margin: 0;
      font-family: Arial, sans-serif;
      display: flex;
      flex-direction: column;
      align-items: center;
      background: #fafafa;
    }

    #demo-container {
      width: 600px;
      margin-top: 20px;
    }

    #controls-container {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: 10px;
      padding: 0 15px;
    }

    #slider-group {
      display: flex;
      align-items: center;
      gap: 8px;
    }

    label[for="slider-t"] {
      font-size: 14px;
    }

    #slider-t {
      width: 120px;
    }

    #input-t {
      width: 60px;
      padding: 3px;
    }

    #btn-reset {
      font-family: monospace;
      font-size: 18px;
      border: none;
      background: none;
      cursor: pointer;
      padding: 5px 10px;
    }

    #checkbox-group {
      display: flex;
      align-items: center;
      gap: 4px;
    }
  </style>
</head>
<body>

<div id="demo-container">
  <div id="controls-container">
    <div id="slider-group">
      <label for="slider-t">choose quadratic</label>
      <input type="range" id="slider-t" min="-5" max="5" step="0.01" value="0.5">
      <input type="number" id="input-t" min="-5" max="5" step="0.01" value="0.5">
      <button id="btn-reset" title="Reset">↻</button>
    </div>
    <div id="checkbox-group">
      <input type="checkbox" id="checkbox-family">
      <label for="checkbox-family">family</label>
    </div>
  </div>
  <div id="canvas-container"></div>
</div>

<script>
  let slider, inputBox, resetBtn, familyCheck;
  let t = 0.5;
  let familyVisible = false;

  let locators = [
    {x:-4, y: 1, dragging: false},
    {x:-2, y: 1.8, dragging: false},
    {x:1, y: -1, dragging: false},
    {x:4, y: -1, dragging: false}
  ];

  const colors = [
    '#e53935','#d81b60','#8e24aa','#5e35b1','#3f51b5',
    '#1e88e5','#00acc1','#00897b','#43a047','#689f38',
    '#fdd835','#ffb300','#fb8c00','#f4511e','#6d4c41'
  ];

  function setup() {
    let c = createCanvas(600, 500);
    c.parent('canvas-container');

    slider = select('#slider-t');
    inputBox = select('#input-t');
    resetBtn = select('#btn-reset');
    familyCheck = select('#checkbox-family');

    slider.input(() => {
      t = float(slider.value());
      inputBox.value(t.toFixed(2));
    });

    inputBox.input(() => {
      t = float(inputBox.value());
      slider.value(t);
    });

    resetBtn.mousePressed(reset);
    familyCheck.changed(() => {
      familyVisible = this.checked;
    });
  }

  function draw() {
    background(255);

    push();
    strokeWeight(1);
    stroke(200);
    line(width/2, 0, width/2, height);
    line(0, height/2, width, height/2);
    pop();

    drawAxes();

    let pts = locators.map(p => ({x: p.x, y: p.y}));
    let coeffs = solveCubicCoefficients(pts);
    let quadCoeffs = getOsculatingQuadraticCoeffs(t, coeffs);

    // Draw cubic when family not visible
    if (!familyVisible) {
      drawCubic(coeffs, '#3f51b5', 3);
    } else {
      drawFamily(pts);
      drawQuadratic(quadCoeffs, '#ff6600', 5);
    }

    // Draggable locators
    fill(255);
    stroke(0);
    strokeWeight(2);
    for (let i = 0; i < locators.length; i++) {
      let p = locators[i];
      let px = map(p.x, -6, 6, 0, width);
      let py = map(p.y, -5, 5, height, 0);
      if (p.dragging) {
        fill(220, 220, 255);
      } else {
        fill(255);
      }
      circle(px, py, 16);
      line(px-6, py, px+6, py);
      line(px, py-6, px, py+6);
    }
  }

  function drawAxes() {
    push();
    stroke(0);
    fill(0);
    textSize(10);
    strokeWeight(1);
    // x ticks
    for (let x = -6; x <= 6; x++) {
      let px = map(x, -6, 6, 0, width);
      text(x, px-5, height/2+15);
    }
    // y ticks
    for (let y = -5; y <= 5; y++) {
      let py = map(y, -5, 5, height, 0);
      text(y, width/2+8, py+4);
    }
    pop();
  }

  function solveCubicCoefficients(points) {
    let [p1,p2,p3,p4] = points;
    // Ax = y
    let A = [
      [p1.x**3, p1.x**2, p1.x, 1],
      [p2.x**3, p2.x**2, p2.x, 1],
      [p3.x**3, p3.x**2, p3.x, 1],
      [p4.x**3, p4.x**2, p4.x, 1]
    ];
    let y = [p1.y, p2.y, p3.y, p4.y];
    let a = A[0][0];
    let det = determinant(A);
    if (det === 0) return [0,0,1,0]; // fallback
    let b = y.map((_, i) => {
      let B = A.map((row, j) => row.slice());
      for (let k = 0; k < 4; k++) B[k][i] = y[k];
      return determinant(B) / det;
    });
    return b; // [a,b,c,d]
  }

  function determinant(m) {
    // 4x4 sarrus (expand minors)
    let [a,b,c,d] = m.map(r => r[0]);
    let e = m.map(r => r[1]);
    let f = m.map(r => r[2]);
    let g = m.map(r => r[3]);
    let M3 = [
      [m[1][1],m[1][2],m[1][3]],
      [m[2][1],m[2][2],m[2][3]],
      [m[3][1],m[3][2],m[3][3]]
    ];
    return a[0]*det3([
      [m[1][1],m[1][2],m[1][3]],
      [m[2][1],m[2][2],m[2][3]],
      [m[3][1],m[3][2],m[3][3]]
    ])
    - a[1]*det3([
      [m[0][1],m[0][2],m[0][3]],
      [m[2][1],m[2][2],m[2][3]],
      [m[3][1],m[3][2],m[3][3]]
    ])
    + a[2]*det3([
      [m[0][1],m[0][2],m[0][3]],
      [m[1][1],m[1][2],m[1][3]],
      [m[3][1],m[3][2],m[3][3]]
    ])
    - a[3]*det3([
      [m[0][1],m[0][2],m[0][3]],
      [m[1][1],m[1][2],m[1][3]],
      [m[2][1],m[2][2],m[2][3]]
    ]);
  }

  function det3(m) {
    let [a,b,c,d,e,f,g,h,i] = [...m[0], ...m[1], ...m[2]];
    return a*(e*i - f*h) - b*(d*i - f*g) + c*(d*h - e*g);
  }

  function getOsculatingQuadraticCoeffs(t, [a,b,c,d]) {
    let p = 3*a*t**2 + 2*b*t + c;
    let q = -3*a*t**2 - 2*b*t;
    let r = 3*a*t**3 + 2*b*t**2 + c*t + d - (a*t**3 + b*t**2 + c*t + d);
    return [a, q, r]; // coefficients for g(x) = p x² + q x + r
  }

  // Replace with actual quad coeffs matching tangent
  function correctedGetOsculatingQuadraticCoeffs(t, [a,b,c,d]) {
    let y = a*t**3 + b*t**2 + c*t + d;
    let dy = 3*a*t**2 + 2*b*t + c;
    let ydd = 6*a*t + 2*b;
    let p = 1;
    let q = dy - 2*ydd*t;
    let r = y - dy*t + ydd*t**2;
    return [ydd/2, dy - ydd*t, y - dy*t + ydd*t**2/2];
  }

  function drawCubic([a,b,c,d], col, sw) {
    stroke(col);
    strokeWeight(sw);
    noFill();
    beginShape();
    for (let px = 0; px <= width; px++) {
      let x = map(px, 0, width, -6, 6);
      let y = a*x**3 + b*x**2 + c*x + d;
      let py = map(y, -5, 5, height, 0);
      vertex(px, py);
    }
    endShape();
  }

  function drawQuadratic([p,q,r], col, sw) {
    let visible = false;
    for (let px = 0; px <= width; px++) {
      let x = map(px, 0, width, -6, 6);
      let y = p*x**2 + q*x + r;
      if (y >= -6 && y <= 6) visible = true;
    }
    if (!visible) return;

    stroke(col);
    strokeWeight(sw);
    noFill();
    beginShape();
    for (let px = 0; px <= width; px++) {
      let x = map(px, 0, width, -6, 6);
      let y = p*x**2 + q*x + r;
      let py = map(y, -5, 5, height, 0);
      vertex(px, py);
    }
    endShape();
  }

  function drawFamily(points) {
    let coeffs = solveCubicCoefficients(points);

    for (let i = 0; i < 40; i++) {
      let ti = -5 + (i/39)*10;
      let quad = correctedGetOsculatingQuadraticCoeffs(ti, coeffs);

      fill(red(colors[i%colors.length]), green(colors[i%colors.length]), blue(colors[i%colors.length]), 40);
      stroke(colors[i%colors.length]);
      strokeWeight(1);

      beginShape();
      for (let px = 0; px <= width; px++) {
        let x = map(px, 0, width, -6, 6);
        let y = quad[0]*x**2 + quad[1]*x + quad[2];
        let py = map(y, -5, 5, height, 0);
        vertex(px, py);
      }
      // Close with bottom edge
      vertex(width, height);
      vertex(0, height);
      endShape(CLOSE);
    }

    // Highlight current one
    let hiqh = correctedGetOsculatingQuadraticCoeffs(t, coeffs);
    drawQuadratic(hiqh, '#ff6600', 5);
  }

  function mousePressed() {
    for (let p of locators) {
      let px = map(p.x, -6, 6, 0, width);
      let py = map(p.y, -5, 5, height, 0);
      if (dist(mouseX, mouseY, px, py) < 20) {
        p.dragging = true;
        return;
      }
    }
  }

  function mouseDragged() {
    for (let p of locators) {
      if (p.dragging) {
        p.x = constrain(map(mouseX, 0, width, -6, 6), -6, 6);
        p.y = constrain(map(mouseY, height, 0, -5, 5), -5, 5);
        break;
      }
    }
  }

  function mouseReleased() {
    for (let p of locators) p.dragging = false;
  }

  function reset() {
    t = 0.5;
    slider.value(t);
    inputBox.value(t.toFixed(2));
    familyCheck.checked(false);
    familyVisible = false;
    locators = [
      {x:-4, y: 1, dragging: false},
      {x:-2, y: 1.8, dragging: false},
      {x:1, y: -1, dragging: false},
      {x:4, y: -1, dragging: false}
    ];
  }
</script>

</body>
</html>
