    <script>
        // Global variables
        let v0 = 211;
        let T = 270;
        let isCelsius = true;
        let k;
        let plotData = [];

        // Initialize p5.js sketch
        let p5Sketch = function(p) {
            let canvas;

            p.setup = function() {
                canvas = p.createCanvas(400, 400);
                canvas.parent('piston-container');
            };

            p.draw = function() {
                p.background(240);

                // Calculate current volume based on Charles's Law
                let T_K = isCelsius ? T + 273.15 : T;
                let V_current = (v0 / 273.15) * T_K;
                V_current = Math.max(0, Math.min(V_current, 2000));

                // Draw cylinder
                p.stroke(0, 100, 255);
                p.strokeWeight(3);
                p.line(100, 50, 100, 350);
                p.line(300, 50, 300, 350);
                p.line(100, 350, 300, 350);

                // Calculate piston position based on volume
                let pistonY = 350 - Math.max(0, Math.min(300, V_current / 6.67));

                // Draw gas
                let colorLow = p.color(128, 0, 128);
                let colorMid = p.color(255, 255, 0);
                let colorHigh = p.color(255, 0, 0);

                let t = p.constrain(T_K / 1000, 0, 1);
                let gasColor;
                if (t < 0.5) {
                    gasColor = p.lerpColor(colorLow, colorMid, t * 2);
                } else {
                    gasColor = p.lerpColor(colorMid, colorHigh, (t - 0.5) * 2);
                }

                p.fill(gasColor);
                p.noStroke();
                p.rect(100, pistonY, 200, 350 - pistonY);

                // Draw piston
                p.fill(150);
                p.stroke(100);
                p.strokeWeight(2);
                p.rect(90, pistonY - 10, 220, 10);
                p.rect(190, pistonY - 30, 20, 20);

                // Draw pressure arrows
                p.fill(255, 150, 0);
                p.noStroke();
                for (let x = 120; x < 280; x += 30) {
                    p.triangle(x, pistonY - 20, x - 5, pistonY - 30, x + 5, pistonY - 30);
                }

                // Display current values
                p.fill(0);
                p.textSize(12);
                p.text(`Volume: ${Math.round(V_current)}`, 50, 30);
                p.text(`Temperature: ${Math.round(isCelsius ? T : T_K)}${isCelsius ? '°C' : 'K'}`, 50, 45);
            };
        };

        new p5(p5Sketch);

        // Initialize plot
        function initPlot() {
            k = v0 / 273.15;
            updatePlot();
        }

        function updatePlot() {
            let T_K = isCelsius ? T + 273.15 : T;
            let V_current = (v0 / 273.15) * T_K;

            // Generate line data
            let T_values = [];
            let V_values = [];
            let T_min = isCelsius ? -273 : 0;
            let T_max = isCelsius ? 727 : 1000;
            let T_step = (T_max - T_min) / 100;

            for (let t = T_min; t <= T_max; t += T_step) {
                let temp = isCelsius ? t + 273.15 : t;
                T_values.push(t);
                V_values.push((v0 / 273.15) * temp);
            }

            let colorscale = [
                [0, 'rgb(128,0,128)'],
                [0.5, 'rgb(255,255,0)'],
                [1, 'rgb(255,0,0)']
            ];

            let lineTrace = {
                x: T_values,
                y: V_values,
                mode: 'lines',
                line: {
                    color: 'rgb(0, 100, 255)',
                    width: 2
                },
                name: 'Charles\'s Law'
            };

            let markerTrace = {
                x: [T],
                y: [V_current],
                mode: 'markers',
                marker: {
                    size: 12,
                    color: Math.round(T_K / 10) * 10,
                    colorscale: colorscale,
                    cmin: 0,
                    cmax: 1000
                },
                name: 'Current State'
            };

            let layout = {
                xaxis: {
                    title: isCelsius ? 'temperature °C' : 'temperature K',
                    range: [T_min, T_max]
                },
                yaxis: {
                    title: 'volume',
                    range: [0, 2000]
                },
                margin: { t: 40, r: 20, b: 40, l: 50 },
                showlegend: false
            };

            Plotly.react('plot-container', [lineTrace, markerTrace], layout);
        }

        // Event listeners
        document.getElementById('slider-v0').addEventListener('input', function(e) {
            v0 = parseInt(e.target.value);
            document.getElementById('v0-value').textContent = v0;
            initPlot();
        });

        document.getElementById('slider-T').addEventListener('input', function(e) {
            T = parseInt(e.target.value);
            let unit = isCelsius ? '°C' : 'K';
            document.getElementById('T-value').textContent = T + unit;
            updatePlot();
        });

        document.getElementById('radio-kelvin').addEventListener('change', function() {
            if (this.checked) {
                isCelsius = false;
                let slider = document.getElementById('slider-T');
                slider.min = 0;
                slider.max = 1000;
                slider.value = Math.round(T + 273.15);
                T = parseInt(slider.value);
                document.getElementById('T-value').textContent = T + 'K';
                updatePlot();
            }
        });

        document.getElementById('radio-celsius').addEventListener('change', function() {
            if (this.checked) {
                isCelsius = true;
                let slider = document.getElementById('slider-T');
                slider.min = -273;
                slider.max = 727;
                slider.value = Math.round(T - 273.15);
                T = parseInt(slider.value);
                document.getElementById('T-value').textContent = T + '°C';
                updatePlot();
            }
        });

        // Initialize
        initPlot();
    </script>
</body>
</html>
