    <script>
        let canvas;
        let centerX, centerY, radius;
        let angleA, angleB, angleC;
        let selectedVertex = 'C';
        let draggingVertex = null;

        window.addEventListener('DOMContentLoaded', () => {
            initializeControls();
        });

        function setup() {
            canvas = createCanvas(600, 600);
            canvas.parent('canvas-container');

            centerX = width / 2;
            centerY = height / 2;
            radius = 200;

            angleA = parseFloat(document.getElementById('slider-a').value);
            angleB = parseFloat(document.getElementById('slider-b').value);
            angleC = parseFloat(document.getElementById('slider-c').value);
        }

        function draw() {
            background(255);

            // Calculate positions
            let aX = centerX + radius * cos(radians(angleA));
            let aY = centerY + radius * sin(radians(angleA));
            let bX = centerX + radius * cos(radians(angleB));
            let bY = centerY + radius * sin(radians(angleB));
            let cX = centerX + radius * cos(radians(angleC));
            let cY = centerY + radius * sin(radians(angleC));

            // Draw colored sectors
            drawColoredSectors();

            // Draw circle
            fill(170, 204, 222);
            stroke(102, 102, 102);
            strokeWeight(2);
            ellipse(centerX, centerY, radius * 2, radius * 2);

            // Draw center
            fill(102, 102, 102);
            noStroke();
            ellipse(centerX, centerY, 8, 8);

            // Draw triangle
            fill(173, 216, 230, 127);
            stroke(102, 102, 102);
            strokeWeight(2);
            beginShape();
            vertex(aX, aY);
            vertex(bX, bY);
            vertex(cX, cY);
            endShape(CLOSE);

            // Draw vertices
            fill(0, 0, 139);
            noStroke();
            ellipse(aX, aY, 12, 12);
            ellipse(bX, bY, 12, 12);
            ellipse(cX, cY, 12, 12);

            // Draw highlighted angles if a vertex is selected
            if (selectedVertex !== 'None') {
                drawHighlightedAngles();
            }

            // Draw vertex labels
            textSize(20);
            textStyle(BOLD);
            fill(0, 0, 139);
            let labelOffset = 15;
            textAlign(CENTER, CENTER);

            let labelAX = centerX + (radius + labelOffset) * cos(radians(angleA));
            let labelAY = centerY + (radius + labelOffset) * sin(radians(angleA));
            text('A', labelAX, labelAY);

            let labelBX = centerX + (radius + labelOffset) * cos(radians(angleB));
            let labelBY = centerY + (radius + labelOffset) * sin(radians(angleB));
            text('B', labelBX, labelBY);

            let labelCX = centerX + (radius + labelOffset) * cos(radians(angleC));
            let labelCY = centerY + (radius + labelOffset) * sin(radians(angleC));
            text('C', labelCX, labelCY);

            updateInfoPanel();
        }

        function drawColoredSectors() {
            // Calculate arc lengths
            let arcAB = ((angleB - angleA + 360) % 360);
            let arcBC = ((angleC - angleB + 360) % 360);
            let arcCA = ((angleA - angleC + 360) % 360);

            // Draw colored sectors
            fill(224, 240, 224);
            noStroke();
            arc(centerX, centerY, radius * 2, radius * 2, radians(angleA), radians(angleA + arcAB));

            fill(245, 245, 213);
            arc(centerX, centerY, radius * 2, radius * 2, radians(angleB), radians(angleB + arcBC));

            fill(224, 232, 248);
            arc(centerX, centerY, radius * 2, radius * 2, radians(angleC), radians(angleC + arcCA));
        }

        function drawHighlightedAngles() {
            let arcAB = ((angleB - angleA + 360) % 360);
            let arcBC = ((angleC - angleB + 360) % 360);
            let arcCA = ((angleA - angleC + 360) % 360);

            stroke(0, 128, 0);
