            let aY = centerY + radius * sin(radians(angleA));
            let bX = centerX + radius * cos(radians(angleB));
            let bY = centerY + radius * sin(radians(angleB));
            let cX = centerX + radius * cos(radians(angleC));
            let cY = centerY + radius * sin(radians(angleC));

            let threshold = 15;

            if (dist(mouseX, mouseY, aX, aY) < threshold) {
                draggingVertex = 'A';
            } else if (dist(mouseX, mouseY, bX, bY) < threshold) {
                draggingVertex = 'B';
            } else if (dist(mouseX, mouseY, cX, cY) < threshold) {
                draggingVertex = 'C';
            }
        }

        function mouseDragged() {
            if (draggingVertex) {
                let angle = degrees(atan2(mouseY - centerY, mouseX - centerX));
                if (angle < 0) angle += 360;

                switch(draggingVertex) {
                    case 'A':
                        angleA = angle;
                        document.getElementById('slider-a').value = angle;
                        break;
                    case 'B':
                        angleB = angle;
                        document.getElementById('slider-b').value = angle;
                        break;
                    case 'C':
                        angleC = angle;
                        document.getElementById('slider-c').value = angle;
                        break;
                }
            }
        }

        function mouseReleased() {
            draggingVertex = null;
        }
    </script>
</body>
</html>
