<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>2D PDE Interactive Demo</title>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <style>
        body {
            margin: 0;
            font-family: sans-serif;
        }
        button {
            border: none;
            background: #4CAF50;
            color: white;
            width: 25px;
            font-size: 16px;
            cursor: pointer;
        }
        button:active {
            background: #45a049;
        }
        label {
            display: inline-block;
            width: 15px;
            font-weight: bold;
        }
        .control-row {
            margin-bottom: 15px;
            display: flex;
            align-items: center;
        }
        .value-display {
            width: 50px;
            text-align: right;
            margin-left: 5px;
        }
    </style>
</head>
<body>
    <div id="main-container" style="display: flex; font-family: sans-serif;">
        <div id="control-panel" style="width: 250px; padding: 20px;">
            <h3>Parameters</h3>

            <!-- τ -->
            <div class="control-row">
                <label>τ</label>
                <button id="btn-dec-tau">-</button>
                <input type="range" id="slider-tau" min="0.01" max="5" step="0.01" value="1.0" style="flex:1;">
                <button id="btn-inc-tau">+</button>
                <span id="value-tau" class="value-display">1.00</span>
            </div>

            <!-- φ -->
            <div class="control-row">
                <label>φ</label>
                <button id="btn-dec-phi">-</button>
                <input type="range" id="slider-phi" min="0.1" max="10" step="0.1" value="2.0" style="flex:1;">
                <button id="btn-inc-phi">+</button>
                <span id="value-phi" class="value-display">2.0</span>
            </div>

            <!-- β -->
            <div class="control-row">
                <label>β</label>
                <button id="btn-dec-beta">-</button>
                <input type="range" id="slider-beta" min="-0.5" max="0.5" step="0.01" value="0.2" style="flex:1;">
                <button id="btn-inc-beta">+</button>
                <span id="value-beta" class="value-display">0.20</span>
            </div>

            <!-- γ -->
            <div class="control-row">
                <label>γ</label>
                <button id="btn-dec-gamma">-</button>
                <input type="range" id="slider-gamma" min="0" max="40" step="1" value="20" style="flex:1;">
                <button id="btn-inc-gamma">+</button>
                <span id="value-gamma" class="value-display">20</span>
            </div>

            <!-- ξ -->
            <div class="control-row">
                <label>ξ</label>
                <button id="btn-dec-xi">-</button>
                <input type="range" id="slider-xi" min="0.001" max="1" step="0.001" value="0.4" style="flex:1;">
                <button id="btn-inc-xi">+</button>
                <span id="value-xi" class="value-display">0.400</span>
            </div>
        </div>

        <div id="visualization-panel" style="flex-grow: 1;">
            <div id="plot-concentration" style="width: 100%; height: 50vh;"></div>
            <div id="plot-temperature" style="width: 100%; height: 50vh;"></div>
        </div>
    </div>

<script>
//==============================================================================
// MODEL & SIMULATION
//==============================================================================
const N = 100;              // # spatial grid points
let ξ = [...Array(N+1).keys()].map(i => i/N);  // xi grid from 0 to 1
const dξ = 1/N;

// storage for current solution
let c = new Array(N+1);
let θ = new Array(N+1);

// helper: second-derivative using central difference (with ghosts for Neumann BC)
function laplacian(x) {
    const lap = new Array(N+1);
    for (let i = 1; i < N; i++) {
        lap[i] = (x[i+1] - 2*x[i] + x[i-1])/(dξ*dξ);
    }
    // Neumann BC: ∂x/∂ξ|ξ=0 = 0
    lap[0] = (-x[2] + 4*x[1] - 3*x[0])/(dξ*dξ);
    // Dirichlet BC: x|ξ=1 = fixed. Laplacian not needed at boundary; set 0 to avoid mutation
    lap[N] = 0;
    return lap;
}

function rhs(cArr, θArr, φ, β, γ) {
    const lapC = laplacian(cArr);
    const lapT = laplacian(θArr);
    const source = new Array(N+1);
    const dcdτ = new Array(N+1);
    const dθdτ = new Array(N+1);

    for (let i = 0; i <= N; i++) {
        const reac = φ*φ * cArr[i] * Math.exp(γ*(1 - 1/θArr[i]));
        dcdτ[i] = lapC[i] - reac;
        dθdτ[i] = lapT[i] + β * reac;
    }

    // Apply Dirichlet BC at ξ=1
    dcdτ[N] = 0;
    dθdτ[N] = 0;

    return [dcdτ, dθdτ];
}

function rk4_step(cOld, θOld, dt, φ, β, γ) {
    const k1c = rhs(cOld, θOld, φ, β, γ)[0];
    const k1θ = rhs(cOld, θOld, φ, β, γ)[1];

    let cMid = cOld.map((v,i)=>v+k1c[i]*dt/2);
    let θMid = θOld.map((v,i)=>v+k1θ[i]*dt/2);
    cMid[N] = 1; θMid[N] = 1; // restore BC
    const k2c = rhs(cMid, θMid, φ, β, γ)[0];
    const k2θ = rhs(cMid, θMid, φ, β, γ)[1];

    cMid = cOld.map((v,i)=>v+k2c[i]*dt/2);
    θMid = θOld.map((v,i)=>v+k2θ[i]*dt/2);
    cMid[N] = 1; θMid[N] = 1;
    const k3c = rhs(cMid, θMid, φ, β, γ)[0];
    const k3θ = rhs(cMid, θMid, φ, β, γ)[1];

    const c4 = cOld.map((v,i)=>v+k3c[i]*dt);
    const θ4 = θOld.map((v,i)=>v+k3θ[i]*dt);
    c4[N] = 1; θ4[N] = 1;
    const k4c = rhs(c4, θ4, φ, β, γ)[0];
    const k4θ = rhs(c4, θ4, φ, β, γ)[1];

    const cNew = cOld.map((v,i)=>v + dt/6*(k1c[i]+2*k2c[i]+2*k3c[i]+k4c[i]));
    const θNew = θOld.map((v,i)=>v + dt/6*(k1θ[i]+2*k2θ[i]+2*k3θ[i]+k4θ[i]));
    cNew[N] = 1; θNew[N] = 1;
    return [cNew, θNew];
}

function solve(φ, β, γ, τ_end, stepOut=0.01, dt=0.0005) {
    let t = 0;
    let cNow = new Array(N+1).fill(0);
    let θNow = new Array(N+1).fill(1);
    cNow[N] = 1; θNow[N] = 1;

    const snapshots = [];
    let nextOut = 0;
    while (t <= τ_end + 1e-8) {
        if (t >= nextOut-1e-8) {
            snapshots.push({t, c:cNow.slice(), θ:θNow.slice()});
            nextOut += stepOut;
        }
        [cNow, θNow] = rk4_step(cNow, θNow, dt, φ, β, γ);
        t += dt;
    }
    // ensure final exact endpoint
    if (snapshots.length === 0 || Math.abs(snapshots[snapshots.length-1].t - τ_end) > 1e-8) {
        snapshots.push({t:τ_end, c:cNow, θ:θNow});
    }
    return snapshots;
}

// Return only final profile
function finalProfile(φ, β, γ, τ) {
    const snaps = solve(φ, β, γ, τ);
    return {c: snaps[snaps.length-1].c, θ: snaps[snaps.length-1].θ};
}

//==============================================================================
// UI / STATE
//==============================================================================
let params = {
    τ: 1.0,
    φ: 2.0,
    β: 0.2,
    γ: 20,
    ξ: 0.4
};

let solution = {
    c: new Array(N+1).fill(0),
    θ: new Array(N+1).fill(1)
};

const sliderIds = ['tau','phi','beta','gamma','xi'];
const updateValueDisplay = (id,val)=> document.getElementById('value-'+id).textContent = val.toFixed((id==='xi'||id==='tau'||id==='beta')?3:1);

function runSimulation() {
    const {φ, β, γ, τ} = params;
    const res = finalProfile(φ, β, γ, τ);
    solution.c = res.c;
    solution.θ = res.θ;
    updatePlots(true);
}

function updateMarkerOnly() {
    updatePlots(false); // only marker lines & annotations update
}

function updatePlots(fullRewind) {
    const idx = Math.round(params.ξ*N);
    const cAtXi = solution.c[idx];
    const θAtXi = solution.θ[idx];

    // Plot 1: c
    const traceC = {x: ξ, y: solution.c, mode:'lines', line:{color:'#1f77b4'}, name:''};
    const markerLineC = {
      x:[params.ξ, params.ξ, 0],
      y:[0, cAtXi, cAtXi],
      mode:'lines',
      line:{color:'#333333', width:2},
      showlegend:false
    };
    const annotationsC = [{
        x:1, y:1, xanchor:'right', yanchor:'top', showarrow:false,
        bgcolor:'rgba(255,255,255,0.9)',
        bordercolor:'#ccc',
        text:`τ = ${params.τ.toFixed(2)}<br>ξ = ${params.ξ.toFixed(3)}<br>c = ${cAtXi.toFixed(5)}`,
        font:{size:12},
        xshift:-10, yshift:-10
    }];

    // Plot 2: θ
    const traceθ = {x: ξ, y: solution.θ, mode:'lines', line:{color:'#ff7f0e'}, name:''};
    const markerLineθ = {
      x:[params.ξ, params.ξ, 0],
      y:[null, θAtXi, θAtXi],
      mode:'lines',
      line:{color:'#333333', width:2},
      showlegend:false
    };
    const annotationsθ = [{
        x:1, y:1, xanchor:'right', yanchor:'top', showarrow:false,
        bgcolor:'rgba(255,255,255,0.9)',
        bordercolor:'#ccc',
        text:`τ = ${params.τ.toFixed(2)}<br>ξ = ${params.ξ.toFixed(3)}<br>θ = ${θAtXi.toFixed(5)}`,
        font:{size:12},
        xshift:-10, yshift:-10
    }];

    const layoutC = {
        xaxis:{title:'ξ', range:[0,1]},
        yaxis:{title:'c', range:[0,1.1]},
        margin:{l:40,r:80,t:30,b:40},
        annotations: annotationsC
    };
    const layoutθ = {
        xaxis:{title:'ξ', range:[0,1]},
        yaxis:{title:'θ', autorange:true},
        margin:{l:40,r:80,t:30,b:40},
        annotations: annotationsθ
    };

    updateTemperatureYMin();

    if (fullRewind) {
        Plotly.react('plot-concentration', [traceC, markerLineC], layoutC, {responsive:true});
        Plotly.react('plot-temperature', [traceθ, markerLineθ], layoutθ, {responsive:true});
    } else {
        Plotly.restyle('plot-concentration', {
            y:[traceC.y, markerLineC.y],
            x:[traceC.x, markerLineC.x]
        }, [0,1]);
        Plotly.relayout('plot-concentration', {annotations: annotationsC});

        Plotly.restyle('plot-temperature', {
            y:[traceθ.y, markerLineθ.y],
            x:[traceθ.x, markerLineθ.x]
        }, [0,1]);
        // need yaxis for new y min of marker line
        const yMin = typeof layoutθ.yaxis.range !== 'undefined' ? layoutθ.yaxis.range[0] : 0;
        Plotly.restyle('plot-temperature', {'y[0]': yMin}, [1], {xas:0, yas:1});
        Plotly.relayout('plot-temperature', {annotations: annotationsθ});
    }

    function updateTemperatureYMin() {
        const yDomainPlot = Plotly.d3.select('#plot-temperature')[0][0]._fullLayout;
        if (!yDomainPlot) return;
        const yMin = yDomainPlot.yaxis.range[0];
        markerLineθ.y[0] = yMin;
    }
}

//==============================================================================
// BINDINGS
//==============================================================================
sliderIds.forEach(id=>{
    const slider = document.getElementById('slider-'+id);
    const decBtn = document.getElementById('btn-dec-'+id);
    const incBtn = document.getElementById('btn-inc-'+id);
    function readVal() { return parseFloat(slider.value); }

    [decBtn, incBtn].forEach(btn=>{
        if (btn) {
            btn.addEventListener('click', ()=>{
                const step = parseFloat(slider.step);
                slider.value = Math.max(parseFloat(slider.min), Math.min(parseFloat(slider.max), btn.textContent==='+'?readVal()+step:readVal()-step));
                slider.dispatchEvent(new Event('input'));
            });
        }
    });

    slider.addEventListener('input', ()=>{
        const val = readVal();
        params[id] = val;
        updateValueDisplay(id, val);
        if (id==='xi') {
            updateMarkerOnly();
        } else {
            runSimulation(); // recalc from τ=0 to current τ
        }
    });
});

//==============================================================================
// INIT
//==============================================================================
window.addEventListener('load', ()=>{
    sliderIds.forEach(id=> updateValueDisplay(id, params[id]));
    runSimulation();
});
</script>
</body>
</html>
