<body>
    <div id="main-container">
        <header id="header-section">
            <h1>Richards Growth Curve</h1>
            <p>Interactive forest growth curve visualization using the Richards model</p>
        </header>

        <div id="control-panel">
            <div class="slider-wrapper">
                <label>max biomass \\(b_{max}\\) +</label>
                <input type="range" id="slider-bmax" min="1000" max="2500" step="1" value="1566">
                <span id="value-bmax">1566</span>
            </div>
            <div class="slider-wrapper">
                <label>initial biomass \\(b_0\\) +</label>
                <input type="range" id="slider-b0" min="1" max="100" step="0.1" value="30">
                <span id="value-b0">30</span>
            </div>
            <div class="slider-wrapper">
                <label>growth rate \\(r\\) +</label>
                <input type="range" id="slider-r" min="0.01" max="0.2" step="0.001" value="0.113">
                <span id="value-r">0.113</span>
            </div>
            <div class="slider-wrapper">
                <label>shape \\(\\beta\\) +</label>
                <input type="range" id="slider-beta" min="0.1" max="1.5" step="0.001" value="0.477">
                <span id="value-beta">0.477</span>
            </div>
        </div>

        <div id="visualization-area">
            <div id="plot-table-container">
                <div id="plot-container"></div>
                <div id="table-container">
                    <table>
                        <thead>
                            <tr>
                                <th>Age (years)</th>
                                <th>Volume (m³ ha⁻¹)</th>
                            </tr>
                        </thead>
                        <tbody id="data-table-body">
                            <tr><td>30</td><td>289</td></tr>
                            <tr><td>35</td><td>445</td></tr>
                            <tr><td>40</td><td>565</td></tr>
                            <tr><td>45</td><td>704</td></tr>
                            <tr><td>50</td><td>833</td></tr>
                            <tr><td>55</td><td>924</td></tr>
                        </tbody>
                    </table>
                </div>
            </div>
            <div id="results-display">
                <p>rejuvenation bias <em>u</em> = <span id="res-u">--</span></p>
                <p>site productivity <em>p</em> = <span id="res-p">--</span></p>
            </div>
        </div>
    </div>

    <script>
        const plotDiv = document.getElementById('plot-container');
        const staticAges = [30, 35, 40, 45, 50, 55];
        const staticVolumes = [289, 445, 565, 704, 833, 924];

        function getSlider(id){ return document.getElementById(id); }
        function getOutput(id){ return document.getElementById('value-'+id.slice(-3));} // "slider-bmax" -> "max"
        const sliders = {
            bmax: {slider:getSlider('slider-bmax'),output:getOutput('slider-bmax')},
            b0:   {slider:getSlider('slider-b0'),  output:getOutput('slider-b0')  },
            r:    {slider:getSlider('slider-r'),   output:getOutput('slider-r')   },
            beta: {slider:getSlider('slider-beta'),output:getOutput('slider-beta')}
        };

        const tRange = [];
        for(let t=15; t<=80; t+=0.5) tRange.push(t);

        const layout = {
            title:'forest growth curve',
            paper_bgcolor:'#FFFFFF',
            plot_bgcolor:'#FFFFF0',
            xaxis:{title:'stand age (years)', range:[15,80], gridcolor:'lightgrey', zeroline:false},
            yaxis: {title:{text:'<b>Richards curve vs 4/5 law</b><br>wood volume, m³ ha⁻¹', standoff:40},
                     range:[0,1600], gridcolor:'lightgrey', zeroline:false},
            showlegend:false,
            margin:{l:60,r:30,t:40,b:40}
        };

        const pointTrace = {
            x:staticAges,
            y:staticVolumes,
            mode:'markers',
            type:'scatter',
            name:'data',
            marker:{color:'blue', size:6}
        };

        function richards(t, b_max, b_0, r, beta){
            const M = Math.pow(b_max/b_0, beta) - 1;
            return b_max / Math.pow(1 + M * Math.exp(-r*t), 1/beta);
        }

        function update(){
            const b_max = parseFloat(sliders.bmax.slider.value);
            const b_0   = parseFloat(sliders.b0.slider.value);
            const r     = parseFloat(sliders.r.slider.value);
            const beta  = parseFloat(sliders.beta.slider.value);

            sliders.bmax.output.textContent = b_max;
            sliders.b0.output.textContent   = b_0.toFixed(1);
            sliders.r.output.textContent    = r.toFixed(3);
            sliders.beta.output.textContent = beta.toFixed(3);

            const yRich = tRange.map(t => richards(t, b_max, b_0, r, beta));

            // Inflection calculations
            const M = Math.pow(b_max/b_0, beta) - 1;
            const term = M * beta;
            if(term <= 0){
                document.getElementById('res-u').textContent = '--';
                document.getElementById('res-p').textContent = '--';
                Plotly.react(plotDiv,[pointTrace,
                  {x:[],y:[],type:'scatter',mode:'lines',line:{color:'orange'}},
                  {x:[],y:[],type:'scatter',mode:'lines',line:{color:'blue'}}],layout);
                return;
            }
            const t_i = Math.log(term)/r;
            const V_i = b_max * Math.pow((beta+1)/(2*beta), 1/beta);
            const V_prime_i = (r/beta)*V_i*(1 - Math.pow(V_i/b_max,beta));

            const u = t_i - (4/5)*V_i/V_prime_i;
            const p = V_i / Math.pow(t_i - u, 4/5);

            document.getElementById('res-u').textContent = u.toFixed(4);
            document.getElementById('res-p').textContent = p.toFixed(4);

            // Evaluate 4/5 law with domain protection
            const y45 = [];
            for(let t of tRange){
                if(t - u > 0){
                    y45.push(p * Math.pow(t - u, 4/5));
                } else {
                    y45.push(null);
                }
            }

            const richardsTrace = {
                x:tRange,
                y:yRich,
                mode:'lines',
                type:'scatter',
                name:'Richards',
                line:{color:'orange',width:2}
            };
            const y45Trace = {
                x:tRange,
                y:y45,
                mode:'lines',
                type:'scatter',
                name:'4/5 law',
                line:{color:'blue',width:2}
            };

            Plotly.react(plotDiv,[pointTrace, richardsTrace, y45Trace],layout);
        }

        // Attach listeners
        Object.values(sliders).forEach(s => s.slider.addEventListener('input', update));
        // Initial render
        update();
    </script>
</body>
</html>
