                <label for="slider-rotate-x">rotate x</label>
                <input type="range" id="slider-rotate-x" min="-90" max="90" value="30" step="1">
                <button id="btn-rotate-x-plus">+</button>
            </div>

            <div class="slider-container">
                <label for="slider-rotate-z">rotate z</label>
                <input type="range" id="slider-rotate-z" min="-180" max="180" value="0" step="1">
                <button id="btn-rotate-z-plus">+</button>
            </div>
