    <script>
        let scene, camera, renderer, coneGroup;
        let currentGeometry, segments = [];

        function init() {
            // Scene setup
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xffffff);

            // Camera setup
            const canvas = document.getElementById('main-canvas');
            const aspect = canvas.clientWidth / canvas.clientHeight;
            camera = new THREE.PerspectiveCamera(75, aspect, 0.1, 1000);
            camera.position.set(0, 0, 300);

            // Renderer setup
            renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(50, 50, 50);
            scene.add(directionalLight);

            // Cone group
            coneGroup = new THREE.Group();
            scene.add(coneGroup);

            // Resize handler
            window.addEventListener('resize', onWindowResize);

            // Initial draw
            redraw();
            animate();
        }

        function onWindowResize() {
            const canvas = document.getElementById('main-canvas');
            const aspect = canvas.clientWidth / canvas.clientHeight;
            camera.aspect = aspect;
            camera.updateProjectionMatrix();
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);
        }

        function createPolygonGeometry(n) {
            const vertices = [];
            for (let i = 0; i <= n; i++) {
                const angle = (i / n) * Math.PI * 2;
                vertices.push(
                    Math.cos(angle) * 10,
                    Math.sin(angle) * 10,
                    0
                );
            }

            const shape = new THREE.Shape();
            shape.moveTo(vertices[0], vertices[1]);
            for (let i = 3; i < vertices.length; i += 3) {
                shape.lineTo(vertices[i], vertices[i + 1]);
            }
            shape.closePath();

            return new THREE.ShapeGeometry(shape);
        }

        function redraw() {
            const basePolygon = parseInt(document.querySelector('input[name="base-polygon"]:checked').value);
            const numUnits = parseInt(document.getElementById('slider-units').value);
            const angleAdvance = parseFloat(document.getElementById('slider-angle').value);
            const conical = parseFloat(document.getElementById('slider-conical').value);
            const rotateX = parseFloat(document.getElementById('slider-rotate-x').value) * Math.PI / 180;
            const rotateZ = parseFloat(document.getElementById('slider-rotate-z').value) * Math.PI / 180;

            // Remove existing segments
            while (segments.length > 0) {
                const segment = segments.pop();
                coneGroup.remove(segment);
                segment.geometry.dispose();
                segment.material.dispose();
            }

            // Create new geometry
            currentGeometry = createPolygonGeometry(basePolygon);

            // Add segments
            const angleAdvanceRad = angleAdvance * Math.PI / 180;

            for (let i = 0; i < numUnits; i++) {
                const angle = i * angleAdvanceRad;
                const radius = 4 * Math.sqrt(i);
                const scale = 0.5 * Math.log(i + 2);

                const x = radius * Math.cos(angle);
                const y = radius * Math.sin(angle);
                const z = conical * radius;

                const material = new THREE.MeshStandardMaterial({
                    color: new THREE.Color().setHSL((angle / (2 * Math.PI)) % 1, 0.8, 0.6),
                    side: THREE.DoubleSide
                });

                const mesh = new THREE.Mesh(currentGeometry, material);
                mesh.position.set(x, y, z);
                mesh.scale.set(scale, scale, scale);
                mesh.rotation.z = angle;

                segments.push(mesh);
                coneGroup.add(mesh);
            }

            // Apply rotations
            coneGroup.rotation.x = rotateX;
            coneGroup.rotation.z = rotateZ;
        }

        function animate() {
            requestAnimationFrame(animate);
            renderer.render(scene, camera);
        }

        // Event listeners
        document.querySelectorAll('input[name="base-polygon"]').forEach(radio => {
            radio.addEventListener('change', redraw);
        });

        document.getElementById('slider-units').addEventListener('input', redraw);
        document.getElementById('slider-angle').addEventListener('input', redraw);
        document.getElementById('slider-conical').addEventListener('input', redraw);
        document.getElementById('slider-rotate-x').addEventListener('input', redraw);
        document.getElementById('slider-rotate-z').addEventListener('input', redraw);

        ['units', 'angle', 'conical', 'rotate-x', 'rotate-z'].forEach(param => {
            document.getElementById(`btn-${param}-plus`).addEventListener('click', function() {
                const slider = document.getElementById(`slider-${param}`);
                const max = parseFloat(slider.max);
                const step = parseFloat(slider.step);
                const newValue = Math.min(parseFloat(slider.value) + step, max);
                slider.value = newValue;
                redraw();
            });
        });

        // Initialize when DOM is loaded
        window.addEventListener('DOMContentLoaded', init);
    </script>
</body>
</html>
