    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r150/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.150.0/examples/js/controls/OrbitControls.js"></script>

    <script>
        // Global variables
        let scene, camera, renderer, controls;
        let atomsGroup, planesGroup;
        let currentLattice = 'SC';
        let currentH = 1, currentK = 1, currentL = 1;
        let currentOpacity = 0.8;

        // Initialize Three.js scene
        function initThree() {
            const container = document.getElementById('canvas-container');
            const width = container.clientWidth;
            const height = container.clientHeight;

            // Scene setup
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf5f5f5);

            // Camera setup
            camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
            camera.position.set(2, 2, 2);

            // Renderer setup
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(width, height);
            container.appendChild(renderer.domElement);

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.4);
            directionalLight.position.set(5, 5, 5);
            scene.add(directionalLight);

            // Groups for atoms and planes
            atomsGroup = new THREE.Group();
            planesGroup = new THREE.Group();
            scene.add(atomsGroup);
            scene.add(planesGroup);

            // Add unit cell wireframe
            addUnitCellWireframe();

            // Add coordinate axes
            addCoordinateAxes();

            // Handle window resize
            window.addEventListener('resize', onWindowResize);
        }

        function addUnitCellWireframe() {
            const geometry = new THREE.BoxGeometry(1, 1, 1);
            const edges = new THREE.EdgesGeometry(geometry);
            const material = new THREE.LineBasicMaterial({ color: 0x333333 });
            const wireframe = new THREE.LineSegments(edges, material);
            scene.add(wireframe);
        }

        function addCoordinateAxes() {
            const origin = new THREE.Vector3(0, 0, 0);
            const length = 0.8;

            // X-axis (red)
            const xArrow = new THREE.ArrowHelper(
                new THREE.Vector3(1, 0, 0),
                origin,
                length,
                0xff0000,
                0.1,
                0.05
            );
            scene.add(xArrow);

            // Y-axis (green)
            const yArrow = new THREE.ArrowHelper(
                new THREE.Vector3(0, 1, 0),
                origin,
                length,
                0x00ff00,
                0.1,
                0.05
            );
            scene.add(yArrow);

            // Z-axis (blue)
            const zArrow = new THREE.ArrowHelper(
                new THREE.Vector3(0, 0, 1),
                origin,
                length,
                0x0000ff,
                0.1,
                0.05
            );
            scene.add(zArrow);
        }

        function drawAtoms() {
            atomsGroup.clear();

            const atomGeometry = new THREE.SphereGeometry(0.05, 16, 16);
            const atomMaterial = new THREE.MeshPhongMaterial({ 
                color: 0x2a6496,
                shininess: 100,
                specular: 0x4a90e2
            });

            let positions = [];

            switch(currentLattice) {
                case 'SC':
                    positions = [
                        [-0.5, -0.5, -0.5], [-0.5, -0.5, 0.5],
                        [-0.5, 0.5, -0.5], [-0.5, 0.5, 0.5],
                        [0.5, -0.5, -0.5], [0.5, -0.5, 0.5],
                        [0.5, 0.5, -0.5], [0.5, 0.5, 0.5]
                    ];
                    break;

                case 'BCC':
                    positions = [
                        [-0.5, -0.5, -0.5], [-0.5, -0.5, 0.5],
                        [-0.5, 0.5, -0.5], [-0.5, 0.5, 0.5],
                        [0.5, -0.5, -0.5], [0.5, -0.5, 0.5],
                        [0.5, 0.5, -0.5], [0.5, 0.5, 0.5],
                        [0, 0, 0]
                    ];
                    break;

                case 'FCC':
                    positions = [
                        [-0.5, -0.5, -0.5], [-0.5, -0.5, 0.5],
                        [-0.5, 0.5, -0.5], [-0.5, 0.5, 0.5],
                        [0.5, -0.5, -0.5], [0.5, -0.5, 0.5],
                        [0.5, 0.5, -0.5], [0.5, 0.5, 0.5],
                        [0, 0, -0.5], [0, 0, 0.5],
                        [0, -0.5, 0], [0, 0.5, 0],
                        [-0.5, 0, 0], [0.5, 0, 0]
                    ];
                    break;
            }

            positions.forEach(pos => {
                const atom = new THREE.Mesh(atomGeometry, atomMaterial);
                atom.position.set(...pos);
                atomsGroup.add(atom);
            });
        }

        function gcd(a, b) {
            a = Math.abs(a);
            b = Math.abs(b);
            while (b !== 0) {
                const temp = b;
                b = a % b;
                a = temp;
            }
            return a;
        }

        function gcd3(a, b, c) {
            return gcd(gcd(a, b), c);
        }

        function drawPlanes() {
            planesGroup.clear();

            if (currentH === 0 && currentK === 0 && currentL === 0) return;

            // Get reduced indices
            const g = gcd3(currentH, currentK, currentL);
            const h = currentH / g;
            const k = currentK / g;
            const l = currentL / g;

            // Find range of n values
            const vertices = [
                [-0.5, -0.5, -0.5], [-0.5, -0.5, 0.5],
                [-0.5, 0.5, -0.5], [-0.5, 0.5, 0.5],
                [0.5, -0.5, -0.5], [0.5, -0.5, 0.5],
                [0.5, 0.5, -0.5], [0.5, 0.5, 0.5]
            ];

            let minVal = Infinity;
            let maxVal = -Infinity;

            vertices.forEach(v => {
                const val = h * v[0] + k * v[1] + l * v[2];
                minVal = Math.min(minVal, val);
                maxVal = Math.max(maxVal, val);
            });

            const nMin = Math.floor(minVal);
            const nMax = Math.ceil(maxVal);

            const planeMaterial = new THREE.MeshBasicMaterial({
                color: 0x4a90e2,
                transparent: true,
                opacity: currentOpacity,
                side: THREE.DoubleSide
            });

            for (let n = nMin; n <= nMax; n++) {
                const plane = createPlaneMesh(h, k, l, n);
                if (plane) {
                    plane.material = planeMaterial;
                    planesGroup.add(plane);
                }
            }
        }

        function createPlaneMesh(h, k, l, n) {
            const points = [];

            // Check all 12 edges of the cube
            const edges = [
                // Bottom face
                [[-0.5, -0.5, -0.5], [0.5, -0.5, -0.5]],
                [[0.5, -0.5, -0.5], [0.5, -0.5, 0.5]],
                [[0.5, -0.5, 0.5], [-0.5, -0.5, 0.5]],
                [[-0.5, -0.5, 0.5], [-0.5, -0.5, -0.5]],

                // Top face
                [[-0.5, 0.5, -0.5], [0.5, 0.5, -0.5]],
                [[0.5, 0.5, -0.5], [0.5, 0.5, 0.5]],
                [[0.5, 0.5, 0.5], [-0.5, 0.5, 0.5]],
                [[-0.5, 0.5, 0.5], [-0.5, 0.5, -0.5]],

                // Vertical edges
                [[-0.5, -0.5, -0.5], [-0.5, 0.5, -0.5]],
                [[0.5, -0.5, -0.5], [0.5, 0.5, -0.5]],
                [[0.5, -0.5, 0.5], [0.5, 0.5, 0.5]],
                [[-0.5, -0.5, 0.5], [-0.5, 0.5, 0.5]]
            ];

            edges.forEach(edge => {
                const [p1, p2] = edge;
                const t = (n - h * p1[0] - k * p1[1] - l * p1[2]) / 
                          (h * (p2[0] - p1[0]) + k * (p2[1] - p1[1]) + l * (p2[2] - p1[2]));

                if (t >= 0 && t <= 1) {
                    const point = [
                        p1[0] + t * (p2[0] - p1[0]),
                        p1[1] + t * (p2[1] - p1[1]),
                        p1[2] + t * (p2[2] - p1[2])
                    ];

                    // Check if point is within bounds
                    if (point.every(coord => Math.abs(coord) <= 0.5 + 1e-10)) {
                        points.push(new THREE.Vector3(...point));
                    }
                }
            });

            if (points.length < 3) return null;

            // Sort points to form a convex polygon
            const centroid = new THREE.Vector3();
            points.forEach(p => centroid.add(p));
            centroid.divideScalar(points.length);

            points.sort((a, b) => {
                const angleA = Math.atan2(a.y - centroid.y, a.x - centroid.x);
                const angleB = Math.atan2(b.y - centroid.y, b.x - centroid.x);
                return angleA - angleB;
            });

            // Create the shape
            const shape = new THREE.Shape();
            shape.moveTo(points[0].x, points[0].y);
            for (let i = 1; i < points.length; i++) {
                shape.lineTo(points[i].x, points[i].y);
            }
            shape.lineTo(points[0].x, points[0].y);

            const geometry = new THREE.ShapeGeometry(shape);
            geometry.rotateX(Math.PI / 2);

            return new THREE.Mesh(geometry);
        }

        function updateScene() {
            drawAtoms();
            drawPlanes();
        }

        function updateOpacity() {
            planesGroup.children.forEach(plane => {
                plane.material.opacity = currentOpacity;
            });
        }

        function onWindowResize() {
            const container = document.getElementById('canvas-container');
            camera.aspect = container.clientWidth / container.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(container.clientWidth, container.clientHeight);
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Event listeners
        document.getElementById('btn-sc').addEventListener('click', function() {
            currentLattice = 'SC';
            document.querySelectorAll('#lattice-controls button').forEach(btn => btn.classList.remove('active'));
            this.classList.add('active');
            updateScene();
        });

        document.getElementById('btn-bcc').addEventListener('click', function() {
            currentLattice = 'BCC';
            document.querySelectorAll('#lattice-controls button').forEach(btn => btn.classList.remove('active'));
            this.classList.add('active');
            updateScene();
        });

        document.getElementById('btn-fcc').addEventListener('click', function() {
            currentLattice = 'FCC';
            document.querySelectorAll('#lattice-controls button').forEach(btn => btn.classList.remove('active'));
            this.classList.add('active');
            updateScene();
        });

        document.getElementById('input-h').addEventListener('input', function() {
            currentH = parseInt(this.value) || 0;
            updateScene();
        });

        document.getElementById('input-k').addEventListener('input', function() {
            currentK = parseInt(this.value) || 0;
            updateScene();
        });

        document.getElementById('input-l').addEventListener('input', function() {
            currentL = parseInt(this.value) || 0;
            updateScene();
        });

        document.getElementById('slider-opacity').addEventListener('input', function() {
            currentOpacity = parseFloat(this.value);
            updateOpacity();
        });

        // Initialize
        initThree();
        updateScene();
        animate();
    </script>
</body>
