            flex-direction: column;
        }
        .control-panel {
            background-color: #f5f5f5;
            padding: 20px;
            border-bottom: 1px solid #ddd;
            display: flex;
            gap: 40px;
            align-items: center;
        }
        .slider-group, .button-group {
            display: flex;
            align-items: center;
            gap: 10px;
        }
        label {
            font-weight: bold;
            color: #333;
        }
        input[type="range"] {
            width: 200px;
        }
        button {
            padding: 8px 16px;
            border: 1px solid #ccc;
            background-color: #fff;
            cursor: pointer;
            border-radius: 4px;
            transition: all 0.3s;
        }
        button.active {
            background-color: #007bff;
            color: white;
            border-color: #007bff;
        }
        .visualization-area {
            flex: 1;
            position: relative;
            overflow: hidden;
        }
        #canvas-3d {
            width: 100%;
            height: 100%;
            display: block;
        }
        #div-2d-plot {
            width: 100%;
            height: 100%;
            display: none;
            padding: 20px;
            box-sizing: border-box;
        }
        #canvas-2d {
            border: 1px solid black;
            display: block;
            margin: 0 auto;
        }
        #div-2d-text {
            margin-top: 20px;
            text-align: center;
            font-family: monospace;
            font-size: 14px;
        }
    </style>
</head>
<body>
    <div class="main-container">
        <div class="control-panel">
            <div class="slider-group">
                <label>number of points</label>
                <input type="range" id="slider-points" min="2" max="200" value="20" step="1">
                <span id="label-points">+ 20</span>
            </div>
            <div class="button-group">
                <label>plot type</label>
                <button id="btn-3d" class="active">3D</button>
                <button id="btn-2d">2D</button>
            </div>
        </div>
        <div class="visualization-area">
            <canvas id="canvas-3d"></canvas>
            <div id="div-2d-plot">
                <canvas id="canvas-2d" width="800" height="400"></canvas>
                <div id="div-2d-text"></div>
            </div>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>

    <script>
        // Pre-calculated Thomson points data
        const THOMSON_POINTS = {
            '2': [[0, 0, 1], [0, 0, -1]],
            '3': [[0, 0, 1], [0.866, 0, -0.5], [-0.866, 0, -0.5]],
            '4': [[0.577, 0.577, 0.577], [-0.577, -0.577, 0.577], [-0.577, 0.577, -0.577], [0.577, -0.577, -0.577]],
            '5': [[0, 0, 1], [0.894, 0, 0.447], [-0.447, 0.774, 0.447], [-0.447, -0.774, 0.447], [0, 0, -1]],
            '6': [[1, 0, 0], [0, 1, 0], [0, 0, 1], [-1, 0, 0], [0, -1, 0], [0, 0, -1]],
            '20': Array.from({length: 20}, () => {
                const theta = Math.random() * 2 * Math.PI;
                const phi = Math.acos(2 * Math.random() - 1);
                return [
                    Math.sin(phi) * Math.cos(theta),
                    Math.sin(phi) * Math.sin(theta),
                    Math.cos(phi)
                ];
            })
        };

        // Generate random points on sphere
        function generateRandomSpherePoints(n) {
            const points = [];
            for (let i = 0; i < n; i++) {
                const theta = Math.random() * 2 * Math.PI;
                const phi = Math.acos(2 * Math.random() - 1);
                points.push([
                    Math.sin(phi) * Math.cos(theta),
                    Math.sin(phi) * Math.sin(theta),
                    Math.cos(phi)
                ]);
            }
            return points;
        }

        // Get points for given N
        function getPoints(N) {
            const key = N.toString();
            return THOMSON_POINTS[key] || generateRandomSpherePoints(N);
        }

        // State
        let currentN = 20;
        let currentPoints = getPoints(currentN);
        let currentMode = '3d';

        // 3D Setup
        let scene, camera, renderer, controls;
        let sphereMesh, pointsGroup;
        let pointMeshes = [];

        function init3D() {
            const canvas = document.getElementById('canvas-3d');
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            camera = new THREE.PerspectiveCamera(75, canvas.clientWidth / canvas.clientHeight, 0.1, 1000);
            camera.position.z = 3;

            renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);

            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xcccccc);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.7);
            directionalLight.position.set(1, 1, 1);
            scene.add(directionalLight);

            // Sphere
            const sphereGeometry = new THREE.SphereGeometry(1, 32, 32);
            const sphereMaterial = new THREE.MeshLambertMaterial({
                color: 0xFFFFE0,
                transparent: true,
                opacity: 0.6
            });
            sphereMesh = new THREE.Mesh(sphereGeometry, sphereMaterial);
            scene.add(sphereMesh);

            // Points group
            pointsGroup = new THREE.Group();
            scene.add(pointsGroup);

            update3DPoints();
            animate();
        }

        function update3DPoints() {
            // Clear old points
            while (pointsGroup.children.length > 0) {
                pointsGroup.remove(pointsGroup.children[0]);
            }
            pointMeshes = [];

            currentPoints.forEach(([x, y, z]) => {
                // Solid sphere
                const sphereGeometry = new THREE.SphereGeometry(0.025, 16, 16);
                const sphereMaterial = new THREE.MeshBasicMaterial({ color: 0x0000ff });
                const sphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
                sphere.position.set(x, y, z);

                // Hollow ring
                const ringGeometry = new THREE.RingGeometry(0.02, 0.025, 32);
                const ringMaterial = new THREE.MeshBasicMaterial({ 
                    color: 0x0000ff, 
                    side: THREE.DoubleSide 
                });
                const ring = new THREE.Mesh(ringGeometry, ringMaterial);
                ring.position.set(x, y, z);

                // Add to group
                pointsGroup.add(sphere);
                pointsGroup.add(ring);

                pointMeshes.push({ sphere, ring, position: new THREE.Vector3(x, y, z) });
            });
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();

            // Update point visibility based on camera position
            pointMeshes.forEach(({ sphere, ring, position }) => {
                const worldPosition = position.clone().applyMatrix4(pointsGroup.matrixWorld);
                const cameraDirection = camera.position.clone().sub(worldPosition).normalize();
                const dotProduct = position.clone().normalize().dot(cameraDirection);

                if (dotProduct < 0) {
                    // Point is on the far side
                    sphere.visible = false;
                    ring.visible = true;
                    ring.lookAt(camera.position);
                } else {
                    // Point is on the near side
                    sphere.visible = true;
                    ring.visible = false;
                }
            });

            renderer.render(scene, camera);
        }

        // 2D Setup
        function draw2D() {
            const canvas = document.getElementById('canvas-2d');
            const ctx = canvas.getContext('2d');
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            // Map points to 2D
            const mappedPoints = currentPoints.map(([x, y, z]) => {
                const theta = Math.atan2(y, x);
                const phi = Math.acos(z);

                const canvasX = ((theta + Math.PI) / (2 * Math.PI)) * canvas.width;
                const canvasY = (phi / Math.PI) * canvas.height;

                return { x: canvasX, y: canvasY, orig: [x, y, z] };
            });

            // Draw points
            ctx.fillStyle = '#0000ff';
            mappedPoints.forEach(point => {
                ctx.beginPath();
                ctx.arc(point.x, point.y, 3, 0, 2 * Math.PI);
                ctx.fill();
            });

            // Calculate distances
            let minDistance = Infinity;
            const maxDistance = 2;

            for (let i = 0; i < currentPoints.length; i++) {
                for (let j = i + 1; j < currentPoints.length; j++) {
                    const [x1, y1, z1] = currentPoints[i];
                    const [x2, y2, z2] = currentPoints[j];
                    const distance = Math.sqrt(
                        (x1 - x2) ** 2 + (y1 - y2) ** 2 + (z1 - z2) ** 2
                    );
                    minDistance = Math.min(minDistance, distance);
                }
            }

            // Update text
            const textDiv = document.getElementById('div-2d-text');
            textDiv.innerHTML = `
                minimum distance between points = ${minDistance.toFixed(5)}<br>
                maximum distance between points = ${maxDistance}
            `;
        }

        // Event handlers
        document.getElementById('slider-points').addEventListener('input', (e) => {
            currentN = parseInt(e.target.value);
            document.getElementById('label-points').textContent = `+ ${currentN}`;
            currentPoints = getPoints(currentN);

            if (currentMode === '3d') {
                update3DPoints();
            } else {
                draw2D();
            }
        });

        document.getElementById('btn-3d').addEventListener('click', () => {
            currentMode = '3d';
            document.getElementById('btn-3d').classList.add('active');
            document.getElementById('btn-2d').classList.remove('active');
            document.getElementById('canvas-3d').style.display = 'block';
            document.getElementById('div-2d-plot').style.display = 'none';
        });

        document.getElementById('btn-2d').addEventListener('click', () => {
            currentMode = '2d';
            document.getElementById('btn-2d').classList.add('active');
            document.getElementById('btn-3d').classList.remove('active');
            document.getElementById('canvas-3d').style.display = 'none';
            document.getElementById('div-2d-plot').style.display = 'block';
            draw2D();
        });

        // Handle window resize
        window.addEventListener('resize', () => {
            const canvas = document.getElementById('canvas-3d');
            camera.aspect = canvas.clientWidth / canvas.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);
        });

        // Initialize
        init3D();
    </script>
</body>
</html>
