            display: flex;
            flex-direction: column;
            gap: 20px;
        }
        canvas {
            border: 1px solid #ddd;
            display: block;
            margin: 0 auto;
        }
    </style>
</head>
<body>
    <div class="main-container">
        <div class="control-panel">
            <div class="slider-group">
                <label for="slider-angle">angle relative to ecliptic</label>
                <input type="range" id="slider-angle" min="0" max="60" step="0.01" value="23.45">
                <span id="value-angle">23.45</span>
            </div>
            <div class="slider-group">
                <label for="slider-longitude">longitude of periapsis</label>
                <input type="range" id="slider-longitude" min="0" max="360" step="0.01" value="102.95">
                <span id="value-longitude">102.95</span>
            </div>
            <div class="slider-group">
                <label for="slider-eccentricity">orbit eccentricity</label>
                <input type="range" id="slider-eccentricity" min="0" max="0.99" step="0.000001" value="0.016722">
                <span id="value-eccentricity">0.016722</span>
            </div>
        </div>

        <div class="visualization-area">
            <canvas id="orbit-canvas" width="400" height="300"></canvas>
            <canvas id="plot-canvas" width="600" height="400"></canvas>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script>
        // Get slider elements
        const sliderAngle = document.getElementById('slider-angle');
        const sliderLongitude = document.getElementById('slider-longitude');
        const sliderEccentricity = document.getElementById('slider-eccentricity');

        const valueAngle = document.getElementById('value-angle');
        const valueLongitude = document.getElementById('value-longitude');
        const valueEccentricity = document.getElementById('value-eccentricity');

        // Update display values
        function updateDisplayValues() {
            valueAngle.textContent = parseFloat(sliderAngle.value).toFixed(2);
            valueLongitude.textContent = parseFloat(sliderLongitude.value).toFixed(2);
            valueEccentricity.textContent = parseFloat(sliderEccentricity.value).toFixed(6);
        }

        // Orbit canvas
        const orbitCanvas = document.getElementById('orbit-canvas');
        const orbitCtx = orbitCanvas.getContext('2d');

        function drawOrbit() {
            const ctx = orbitCtx;
            const width = orbitCanvas.width;
            const height = orbitCanvas.height;

            // Clear canvas
            ctx.fillStyle = '#f0f0f0';
            ctx.fillRect(0, 0, width, height);

            const e = parseFloat(sliderEccentricity.value);
            const omega = parseFloat(sliderLongitude.value) * Math.PI / 180;

            const a = width * 0.4;
            const b = a * Math.sqrt(1 - e * e);

            ctx.save();
            ctx.translate(width / 2, height / 2);
            ctx.rotate(omega);

            ctx.strokeStyle = '#a0522d';
            ctx.lineWidth = 2;
            ctx.beginPath();
            ctx.ellipse(0, 0, a, b, 0, 0, 2 * Math.PI);
            ctx.stroke();

            // Draw periapsis marker
            ctx.fillStyle = '#a0522d';
            ctx.beginPath();
            ctx.arc(a * e, 0, 3, 0, 2 * Math.PI);
            ctx.fill();

            ctx.restore();
        }

        // Plot canvas
        const plotCanvas = document.getElementById('plot-canvas');
        const plotCtx = plotCanvas.getContext('2d');

        function solveKepler(M, e) {
            let E = M;
            for (let i = 0; i < 10; i++) {
                E = E - (E - e * Math.sin(E) - M) / (1 - e * Math.cos(E));
            }
            return E;
        }

        function drawPlot() {
            const ctx = plotCtx;
            const width = plotCanvas.width;
            const height = plotCanvas.height;

            const epsilon = parseFloat(sliderAngle.value) * Math.PI / 180;
            const omega = parseFloat(sliderLongitude.value) * Math.PI / 180;
            const e = parseFloat(sliderEccentricity.value);

            // Generate data points
            const points = [];
            let prevEOT = 0;

            for (let M = 0; M <= 2 * Math.PI; M += 0.01) {
                const E = solveKepler(M, e);
                const nu = 2 * Math.atan2(Math.sqrt(1 + e) * Math.sin(E / 2), Math.sqrt(1 - e) * Math.cos(E / 2));
                const lambda = nu + omega;
                const alpha = Math.atan2(Math.cos(epsilon) * Math.sin(lambda), Math.cos(lambda));

                let EOT_rad = M - alpha;

                // Phase unwrapping
                while (EOT_rad - prevEOT > Math.PI) EOT_rad -= 2 * Math.PI;
                while (EOT_rad - prevEOT < -Math.PI) EOT_rad += 2 * Math.PI;

                const EOT_minutes = EOT_rad * (720 / Math.PI);
                points.push({ x: M, y: EOT_minutes });
                prevEOT = EOT_rad;
            }

            // Find min and max y values
            const yMin = Math.min(...points.map(p => p.y));
            const yMax = Math.max(...points.map(p => p.y));
            const yRange = Math.max(Math.abs(yMin), Math.abs(yMax)) * 2.2;

            // Clear canvas
            ctx.fillStyle = 'white';
            ctx.fillRect(0, 0, width, height);

            // Set up coordinate system
            const margin = 50;
            const plotWidth = width - 2 * margin;
            const plotHeight = height - 2 * margin;

            // Draw axes
            ctx.strokeStyle = 'black';
            ctx.lineWidth = 1;
            ctx.beginPath();
            ctx.moveTo(margin, margin);
            ctx.lineTo(margin, height - margin);
            ctx.lineTo(width - margin, height - margin);
            ctx.stroke();

            // Draw grid and labels
            ctx.strokeStyle = '#e0e0e0';
            ctx.fillStyle = 'black';
            ctx.font = '12px Arial';
            ctx.textAlign = 'center';

            // X-axis ticks
            for (let i = 1; i <= 6; i++) {
                const x = margin + (i / 6) * plotWidth;
                ctx.beginPath();
                ctx.moveTo(x, margin);
                ctx.lineTo(x, height - margin);
                ctx.stroke();

                ctx.fillText(i.toString(), x, height - margin + 15);
            }

            // Y-axis ticks
            const yTicks = 5;
            for (let i = -yTicks; i <= yTicks; i++) {
                const y = height / 2 - (i / yTicks) * (plotHeight / 2);
                const value = (i / yTicks) * yRange / 2;

                ctx.beginPath();
                ctx.moveTo(margin, y);
                ctx.lineTo(width - margin, y);
                ctx.stroke();

                ctx.textAlign = 'right';
                ctx.fillText(value.toFixed(1), margin - 5, y + 4);
            }

            // Draw axes labels
            ctx.textAlign = 'center';
            ctx.font = '14px Arial';
            ctx.fillText('Mean Anomaly (radians)', width / 2, height - 10);

            ctx.save();
            ctx.translate(15, height / 2);
            ctx.rotate(-Math.PI / 2);
            ctx.fillText('minutes', 0, 0);
            ctx.restore();

            // Draw curve
            ctx.strokeStyle = '#ff8c00';
            ctx.lineWidth = 2;
            ctx.beginPath();

            for (let i = 0; i < points.length; i++) {
                const x = margin + (points[i].x / (2 * Math.PI)) * plotWidth;
                const y = height / 2 - (points[i].y / yRange) * plotHeight;

                if (i === 0) {
                    ctx.moveTo(x, y);
                } else {
                    ctx.lineTo(x, y);
                }
            }
            ctx.stroke();
        }

        // Event listeners
        [sliderAngle, sliderLongitude, sliderEccentricity].forEach(slider => {
            slider.addEventListener('input', () => {
                updateDisplayValues();
                drawOrbit();
                drawPlot();
            });
        });

        // Initial draw
        updateDisplayValues();
        drawOrbit();
        drawPlot();
    </script>
</body>
</html>
