<body>
  <main>
    <section id="control-panel">
      <h3>Controls</h3>
      <label>c <span id="c-value" class="param">4</span></label>
      <input id="slider-c" type="range" min="1" max="10" step="0.1" value="4"/>

      <label>h<sub>C</sub> <span id="h_C-value" class="param">1.9</span></label>
      <input id="slider-h_C" type="range" min="0.1" max="5" step="0.1" value="1.9"/>

      <label>δ = α - β <span id="delta-value" class="param">0.5</span></label>
      <input id="slider-delta" type="range" min="-1.5" max="1.5" step="0.01" value="0.5"/>

