        <label for="step3">3</label>
        <input id="step4" type="radio" name="steps" value="4"/>
        <label for="step4">4</label>
        <input id="step5" type="radio" name="steps" value="5" checked/>
        <label for="step5">5</label>
      </div>

      <label>
        <input id="check-verification" type="checkbox" checked/>
        verification
      </label>

      <label>plot range <span id="plot-range-value" class="param">1</span></label>
      <input id="slider-plot-range" type="range" min="0.5" max="2" step="0.01" value="1"/>
    </section>

    <section id="visualization-area"></section>
