  <script>
    const sliderC = document.getElementById('slider-c');
    const sliderHC = document.getElementById('slider-h_C');
    const sliderDelta = document.getElementById('slider-delta');
    const checkVerification = document.getElementById('check-verification');
    const sliderPlotRange = document.getElementById('slider-plot-range');

    let c = 4;
    let hC = 1.9;
    let delta = 0.5;
    let verification = true;
    let plotRange = 1;
    let step = '5';

    // Attach inputs
    sliderC.addEventListener('input', e=>{
      c = parseFloat(e.target.value);
      document.getElementById('c-value').textContent = c.toFixed(1);
      redrawScene();
    });
    sliderHC.addEventListener('input', e=>{
      hC = parseFloat(e.target.value);
      document.getElementById('h_C-value').textContent = hC.toFixed(1);
      redrawScene();
    });
    sliderDelta.addEventListener('input', e=>{
      delta = parseFloat(e.target.value);
      document.getElementById('delta-value').textContent = delta.toFixed(2);
      redrawScene();
    });
    checkVerification.addEventListener('change', e=>{
      verification = e.target.checked;
      redrawScene();
    });
    sliderPlotRange.addEventListener('input', e=>{
      plotRange = parseFloat(e.target.value);
      document.getElementById('plot-range-value').textContent = plotRange.toFixed(2);
      redrawScene();
    });

    document.querySelectorAll('input[name="steps"]').forEach(rb=>{
      rb.addEventListener('change', e=>{
        step = e.target.value;
        redrawScene();
      });
    });

    let p5Inst;
    new p5(sketch);

    function sketch(p){
      p.setup = function(){
        const {clientWidth, clientHeight} = document.getElementById('visualization-area');
        p.createCanvas(clientWidth, clientHeight);
        p.rectMode(p.CENTER);
        p.textAlign(p.CENTER, p.CENTER);
        p.noLoop();
      };

      p.draw = redrawScene;
    }

    function redrawScene(){
      if(!p5Inst) p5Inst = p5.instance;
      const p = p5Inst;
      p.background('#E0E0E0');
      p.clear();

      // Coordinate transform
      const center = {x: p.width/2, y: p.height*0.6};
      const scaleFactor = 50 * plotRange;
      p.translate(center.x, center.y);
      p.scale(1,-1);

      // Helper: Draw label with fixed y
      function label(pt, txt, offset=[0, -7]){
        p.push();
        p.scale(1, -1);
        p.textSize(14);
        p.fill(0);
        p.strokeWeight(0);
        p.text(txt, pt.x*scaleFactor + offset[0], -pt.y*scaleFactor + offset[1]);
        p.pop();
      }

      // Points and calculations
      const F = {x: 0, y: 0};
      const C = {x: 0, y: hC};
      const D = {x: c/2, y: 0};
      const K = {x: c, y: 0};

      // Solve M
      let yM;
      if(Math.abs(delta) < 1e-4){
        // Limit case δ→0, handle separately, avoid division by zero.
        yM = 0;
      }else{
        const tanD = Math.tan(delta);
        // Coefficients of quadratic: A*yM² + B*yM + C = 0
        const A = tanD;
        const B = c*c/(4*tanD*4*hC) - hC/2; // simplified from derivation
        const C_const = -c*t*c*(3/8)*tanD - c*c*c/4*tanD; // careful algebra from derivation
        // Real equation obtained in step derivation leads to:
        const a = Math.tan(delta) * (4*hC*hC + c*c);
        const b = -2*hC*c*c;
        const c2 = (c*c*hC)*(1/4 -1)*Math.tan(delta);
        // Re-derive quadratic via substitution
        let denom = 2*Math.tan(delta)*hC;
        if(Math.abs(denom) < 1e-6){
          // Special case to avoid division by zero
          yM = ( (c*c/4) - Math.tan(delta)*c*c/2 ) / (-2*hC);
        }else{
          // Standard quadratic derived after substitution
          const num = (Math.tan(delta)*c)/2;
          const newA = 1 + (num*2*Math.tan(delta)*c)/(2*hC);
          const newB = (-Math.tan(delta)*c*3*c)/(2*hC);
          // Actually, substituting properly into (ii) leads to solving for y_M
          // using:
          const rhs = (c/2)*Math.tan(delta);
          const t = 1/(2*hC);
          const quadA = 4*hC*Math.tan(delta);
          const quadB = -2*c*c;
          const quadC = c*c*hC*(0.25) + 2*c*c*hC*0.5 - 0.5*c*c*hC; // Simplified coefficient term

          // Correct quadratic via derivation: from (1) & (2), solve numerically for yM
          // Solving simplified quadratic from derivations:
          const aa = 4*Math.tan(delta)*hC;
          const bb = -2*c*c;
          const cc = c*c*hC - 0.5*(c*c*c)/(2*hC)*Math.tan(delta); 

          // The above derivation was complex; use numerical root solution
          // Based on the equations given in prompt simplified form:
          // Solve quadratic for yM: simplified via algebraic manipulation

          let aCoeff = 4 * Math.tan(delta) * hC;
          let bCoeff = -2 * c * c;
          let cCoeff = c * c * hC * (1 - 1/4) + 0.5*c*c*c*Math.tan(delta)/(2*hC); // re-simplified

          // Correct coefficients after substitution of xM from the perp bisector into the angle relation
          // The derived quadratic is:
          let Ay = 4*hC*Math.tan(delta);
          let By = -2*c*c;
          let Cy = c*c*hC + (c*c*c)*Math.tan(delta)/4 + 0;

          if(Math.abs(Ay) > 1e-6){
            const discriminant = By*By - 4*Ay*Cy;
            if(discriminant < 0){ yM = 0; } // fallback
            else{
              let root = Number.MAX_VALUE;
              // Prefer negative y from derivation
              const y1 = (-By + Math.sqrt(discriminant))/(2*Ay);
              const y2 = (-By - Math.sqrt(discriminant))/(2*Ay);
              yM = Math.min(y1,y2) < 0 ? Math.min(y1,y2) : Math.max(y1,y2);
            }
          }else{
            yM = -Cy/By;
          }
        }
      }

      // Calculate xM
      let xM;
      if(Math.abs(hC) < 1e-6){
        // Vertical perpendicular bisector at c/4
        xM = (c*c)/(2*c);
      }else{
        xM = (2*hC*yM - hC*hC + c*c/4)/c;
      }

      const M = {x: xM, y: yM};
      const R = Math.sqrt(Math.pow(M.x-C.x,2)+Math.pow(M.y-C.y,2));
      const xB = 2*xM - c/2;
      const B = {x: xB, y: 0};
      const A = {x: xB - c, y: 0};
      const E = {x: 2*D.x - B.x, y: 0};

      // Common drawing styles
      function drawPt(pt, name, color=[0,0,0], size=4){
        p.stroke(color);
        p.fill(color);
        p.ellipse(pt.x*scaleFactor, pt.y*scaleFactor, size, size);
        label(pt, name);
      }
      function drawLine(p1, p2, dashed=false, weight=1){
        p.strokeWeight(weight);
        p.stroke(0);
        if(dashed){
          p.drawingContext.setLineDash([5,5]);
        }else{
          p.drawingContext.setLineDash([]);
        }
        p.line(p1.x*scaleFactor, p1.y*scaleFactor, p2.x*scaleFactor, p2.y*scaleFactor);
        p.drawingContext.setLineDash([]);
      }
      function drawArc(center, radius, startAngle, endAngle){
        p.strokeWeight(1);
        p.stroke(0);
        p.noFill();
        let start = p.radians(startAngle);
        let end = p.radians(endAngle);
        p.arc(center.x*scaleFactor, center.y*scaleFactor, radius*2*scaleFactor, radius*2*scaleFactor, start, end);
      }

      // Step based rendering
      if(step >= '1'){
        // Line λ (x-axis)
        drawLine({x: -center.x/(scaleFactor)-1, y:0}, {x: center.x/(scaleFactor)+1, y:0});
        drawPt(F, 'F');
        drawPt(C, 'C');
        drawPt(D, 'D');
        drawLine(F,C,false,2);
      }
      if(step >= '2'){
        drawPt(K, 'K');
        drawLine(C,D,true);
        drawLine(D,K,true);
        drawPt(M,'M');
        drawLine(M,C,true);
        drawLine(M,D,true);
        drawLine(M,K,true);
        drawArc(M, R, 0, 360);
      }
      if(step >= '3'){
        drawPt(B, 'B');
      }
      if(step >= '4'){
        drawPt(A, 'A');
      }
      if(step >= '5'){
        drawLine(A,B,false,3);
        drawLine(B,C,false,3);
        drawLine(C,A,false,3);
      }
      if(verification){
        drawPt(E, 'E');
        drawLine(C,E,true);
        drawLine(K,B,true);
      }
    }
  </script>
</body>
</html>
