<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="slider-z">z (m)</label>
            <input type="range" id="slider-z" min="0" max="32" step="0.01" value="0">
            <span id="label-z">0.</span>
        </div>
        <div id="plots-container">
            <div id="plot-intensity"></div>
            <div id="plot-population"></div>
            <div id="plot-coherence"></div>
        </div>
    </div>

    <script>
        // Common time array
        const t_values = Array.from({length: 301}, (_, i) => i - 50); // -50 … 250 ns

        // --- PHYICS FORMULAS FOR DATA GENERATION ---
        function redIntensity(t) {
            if (t < -15 || t > 170) return 0;
            if (t > 15) return 1;
            return 0.5 * (1 + Math.tanh(t / 4));
        }

        function redPopulation(t) {
            if (t < -15) return 0;
            return 4.5 * (1 - Math.exp(-(t + 15) / 50));
        }

        function redCoherence(t) {
            if (t < -15) return 0;
            return 2.2 * (1 - Math.exp(-(t + 15) / 50));
        }

        function blueIntensity(t, z) {
            const t_delay = z / 0.3;
            const t_prime = t - t_delay;
            if (t_prime < -15 || t_prime > 170) return 0;
            const I_base = t_prime > 15 ? 1 : 0.5 * (1 + Math.tanh(t_prime / 4));
            const envelope = (t_prime > 0) ? Math.exp(-0.035 * z * t_prime / 32) : 1;
            return I_base * envelope;
        }

        function bluePopulation(t, z) {
            const t_delay = z / 0.3;
            const t_prime = t - t_delay;
            const scaling_factor = 1 - 0.45 * (z / 32);
            if (t_prime < -15 || t_prime > 170) return 0;
            return scaling_factor * 4.5 * (1 - Math.exp(-(t_prime + 15) / 50));
        }

        function blueCoherence(t, z) {
            const t_delay = z / 0.3;
            const t_prime = t - t_delay;
            const scaling_factor = 1 - 0.45 * (z / 32);
            if (t_prime < -15 || t_prime > 170) return 0;
            return scaling_factor * 2.2 * (1 - Math.exp(-(t_prime + 15) / 50));
        }

        // Initial data
        const I_red = t_values.map(redIntensity);
        const P_red = t_values.map(redPopulation);
        const C_red = t_values.map(redCoherence);

        const I_blue = t_values.map(t => blueIntensity(t, 0));
        const P_blue = t_values.map(t => bluePopulation(t, 0));
        const C_blue = t_values.map(t => blueCoherence(t, 0));

        // Plot layout templates
        const commonLayout = (ylabel, yrange, txt) => ({
            xaxis: { title: 'time (ns)', range: [-50, 250], zeroline: true },
            yaxis: { title: { text: ylabel, standoff: 10 }, range: yrange, zeroline: true },
            margin: { l: 100, r: 20, t: 40, b: 50 },
            showlegend: true,
            legend: { x: 0.75, y: 0.9 },
            shapes: [
                { type: 'line', x0: 0, y0: 0, x1: 0, y1: 1, yref: 'paper', line: { color: 'black', width: 2, dash: 'dash' } },
                { type: 'line', x0: 0, y0: 0, x1: 0, y1: 1, yref: 'paper', line: { color: 'black', width: 2, dash: 'dash' } }
            ],
            annotations: [
                { x: -45, y: yrange[1]*0.9, xref: 'x', yref: 'y', text: txt, showarrow: false, font: { size: 14 } },
                { x: 0, y: yrange[0]*0.7, xref: 'x', yref: 'y', text: 'pulse<br>midpoint', showarrow: false, xanchor: 'left', yanchor: 'bottom', font: { size: 12 } }
            ]
        });

        // Create 3 plots
        Plotly.newPlot('plot-intensity', [
            { x: t_values, y: I_red, name: 'z = 0 meters', line: { color: 'red' } },
            { x: t_values, y: I_blue, name: 'z = 0. meters', line: { color: 'blue', dash: 'dash' } }
        ], commonLayout('intensity (arb)', [-0.1, 1.1], '(a)'), {responsive: true});

        Plotly.newPlot('plot-population', [
            { x: t_values, y: P_red, name: 'z = 0 meters', line: { color: 'red' } },
            { x: t_values, y: P_blue, name: 'z = 0. meters', line: { color: 'blue', dash: 'dash' } }
        ], commonLayout('excited-state population × 10<sup>4</sup>', [-0.5, 5.5], '(b)'), {responsive: true});

        Plotly.newPlot('plot-coherence', [
            { x: t_values, y: C_red, name: 'z = 0 meters', line: { color: 'red' } },
            { x: t_values, y: C_blue, name: 'z = 0. meters', line: { color: 'blue', dash: 'dash' } }
        ], commonLayout('coherence × 10<sup>2</sup>', [-0.2, 2.4], '(c)'), {responsive: true});

        // Slider interaction
        const slider = document.getElementById('slider-z');
        const zLabel = document.getElementById('label-z');

        slider.addEventListener('input', function() {
            const z_val = parseFloat(this.value);
            zLabel.textContent = (z_val === Math.floor(z_val)) ? z_val + "." : z_val.toFixed(2);
            const tDelay = z_val / 0.3;

            // Update each plot
            ['intensity', 'population', 'coherence'].forEach((key, idx) => {
                let y_blue;
                const zName = "z = " + ((z_val === Math.floor(z_val)) ? z_val + "." : z_val.toFixed(2)) + " meters";
                if (key === 'intensity') y_blue = t_values.map(t => blueIntensity(t, z_val));
                else if (key === 'population') y_blue = t_values.map(t => bluePopulation(t, z_val));
                else y_blue = t_values.map(t => blueCoherence(t, z_val));

                Plotly.restyle('plot-' + key, { y: [y_blue], 'name[1]': zName }, [1]);
                const shapeUpdate = {};
                shapeUpdate['shapes[1].x0'] = tDelay;
                shapeUpdate['shapes[1].x1'] = tDelay;
                Plotly.relayout('plot-' + key, shapeUpdate);
            });
        });
    </script>
</body>
</html>
