</head>
<body>
    <!-- Main container -->
    <div id="demo-container">

      <!-- Control Panel Section -->
      <div id="control-panel">

        <!-- Heat Transfer Coefficient Slider -->
        <div>
          <label for="slider-ua">heat transfer coefficient (cal/(dm² K s))</label>
          <input type="range" id="slider-ua" min="0" max="20" step="0.1" value="0">
          <span id="ua-value">0</span>
        </div>

        <!-- Reverse Reaction Pre-exponential Factor Slider -->
        <div>
          <label for="slider-kr0">reverse reaction pre-exponential factor (1/s)</label>
          <input type="range" id="slider-kr0" min="0" max="10" step="0.1" value="0">
          <span id="kr0-value">0.0 x 10¹²</span>
        </div>

        <!-- Feed Temperature Slider -->
        <div>
          <label for="slider-tf">feed temperature (K)</label>
          <input type="range" id="slider-tf" min="250" max="350" step="1" value="265">
          <span id="tf-value">265</span>
        </div>

        <!-- Residence Time Slider -->
        <div>
          <label for="slider-tau">residence time (s)</label>
          <input type="range" id="slider-tau" min="100" max="1000" step="10" value="400">
          <span id="tau-value">400</span>
        </div>

      </div>

      <!-- Visualization Section -->
      <div id="plot-container"></div>
    </div>

    <script>
        // Constants
        const C_A0 = 10; // mmol/dm³
        const R = 1.987; // cal/mol K
        const E_f = 25000; // cal/mol
        const delta_H_rxn = -200000; // cal/mol
        const E_r = E_f - delta_H_rxn; // 225000 cal/mol
        const k_f0 = 1e15; // 1/s
        const rhoCp = 20000; // cal/(dm³ K)

        // Initialize plot
        const layout = {
            xaxis: {
                title: 'temperature (K)',
                range: [250, 400],
                gridcolor: '#ddd'
            },
            yaxis: {
                title: 'product concentration (mmol/dm³)',
                range: [0, 12],
                gridcolor: '#ddd'
            },
            annotations: [
                {
                    text: 'mass balance',
                    x: 360,
                    y: 8,
                    showarrow: false,
                    font: { color: '#008000' }
                },
                {
                    text: 'energy balance',
                    x: 280,
                    y: 2,
                    showarrow: false,
                    font: { color: '#0000FF' }
                }
            ],
            margin: { l: 60, r: 40, t: 40, b: 60 }
        };

        function calculateCurves() {
            // Read slider values
            const UA = parseFloat(document.getElementById('slider-ua').value);
            const kr0_slider = parseFloat(document.getElementById('slider-kr0').value);
            const T_f = parseFloat(document.getElementById('slider-tf').value);
            const tau = parseFloat(document.getElementById('slider-tau').value);

            // Update value displays
            document.getElementById('ua-value').textContent = UA.toFixed(1);
            document.getElementById('kr0-value').textContent = (kr0_slider / 10).toFixed(1) + ' x 10¹²';
            document.getElementById('tf-value').textContent = T_f;
            document.getElementById('tau-value').textContent = tau;

            // Generate temperature points
            const T_points = [];
            for (let T = 250; T <= 400; T += 1) {
                T_points.push(T);
            }

            // Calculate mass balance curve
            const massBalancePoints = T_points.map(T => {
                const k_f = k_f0 * Math.exp(-E_f / (R * T));
                const k_r = kr0_slider * 1e12 * Math.exp(-E_r / (R * T));
                const C_B = (tau * k_f * C_A0) / (1 + tau * (k_f + k_r));
                return C_B;
            });

            // Calculate energy balance line
            const energyBalancePoints = T_points.map(T => {
                const C_B = (rhoCp * (T - T_f) + UA * tau * (T - T_f)) / (-delta_H_rxn);
                return C_B;
            });

            // Create traces
            const massBalanceTrace = {
                x: T_points,
                y: massBalancePoints,
                mode: 'lines',
                line: { color: '#008000' },
                name: 'mass balance'
            };

            const energyBalanceTrace = {
                x: T_points,
                y: energyBalancePoints,
                mode: 'lines',
                line: { color: '#0000FF' },
                name: 'energy balance'
            };

            // Update annotations positions based on data
            layout.annotations[0].y = massBalancePoints[110] || 8; // Around T=360
            layout.annotations[1].y = energyBalancePoints[30] || 2; // Around T=280

            return [massBalanceTrace, energyBalanceTrace];
        }

        function updatePlot() {
            const data = calculateCurves();
            Plotly.react('plot-container', data, layout);
        }

        // Add event listeners to all sliders
        document.getElementById('slider-ua').addEventListener('input', updatePlot);
        document.getElementById('slider-kr0').addEventListener('input', updatePlot);
        document.getElementById('slider-tf').addEventListener('input', updatePlot);
        document.getElementById('slider-tau').addEventListener('input', updatePlot);

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
