        <input type="radio" name="ref-frame" id="radio-epitrochoid" value="epitrochoid" checked>
        <label for="radio-epitrochoid">epitrochoid</label>
        <input type="radio" name="ref-frame" id="radio-envelope" value="envelope">
        <label for="radio-envelope">envelope</label>
        <input type="radio" name="ref-frame" id="radio-fixed-centers" value="fixed centers">
        <label for="radio-fixed-centers">fixed centers</label>
      </div>
      <div>
        <span>envelope to show</span>
        <input type="radio" name="envelope-type" id="radio-inner" value="inner" checked>
        <label for="radio-inner">inner</label>
        <input type="radio" name="envelope-type" id="radio-outer" value="outer">
        <label for="radio-outer">outer</label>
        <input type="radio" name="envelope-type" id="radio-both" value="both">
        <label for="radio-both">both</label>
      </div>
      <div>
        <label for="slider-rotation">rotation angle</label>
        <input type="range" id="slider-rotation" min="0" max="6.28318" step="0.0001" value="0">
        <span id="label-rotation">0.00</span>
      </div>
    </div>

    <!-- Visualization -->
    <div id="canvas-container">
      <!-- p5 canvas -->
    </div>
  </div>

  <script>
    let kSlider, rotSlider;
    let kVal = 0.14;
    let phiVal = 0;
    let refFrame = 'epitrochoid';
    let envType = 'inner';
    let scaleFactor = 150;

    function setup() {
      const canvas = createCanvas(600, 600);
      canvas.parent('canvas-container');

      kSlider = select('#slider-eccentricity');
      rotSlider = select('#slider-rotation');

      // Update labels
      select('#label-eccentricity').html(kVal.toFixed(4));
      select('#label-rotation').html(phiVal.toFixed(2));

      // Events
      selectAll('input[type="range"]').forEach(input => {
        input.input(() => {
          kVal = parseFloat(kSlider.value());
          select('#label-eccentricity').html(kVal.toFixed(4));
          phiVal = parseFloat(rotSlider.value());
          select('#label-rotation').html(phiVal.toFixed(2));
        });
      });

      selectAll('input[name="ref-frame"]').forEach(radio => {
        radio.changed(() => {
          refFrame = radio.value();
          if (radio.checked) refFrame = radio.value();
        });
      });

      selectAll('input[name="envelope-type"]').forEach(radio => {
        radio.changed(() => {
          envType = radio.value();
          if (radio.checked) envType = radio.value();
        });
      });
    }

    function draw() {
      background(255);
      translate(width / 2, height / 2);
      scale(1, -1);

      drawAxes();

      // Plot curves
      drawEpitrochoid();
      drawEnvelopes();
    }

    function drawAxes() {
      stroke(150);
      strokeWeight(1);
      line(-width / 2, 0, width / 2, 0);
      line(0, -height / 2, 0, height / 2);

      // Ticks
      fill(150);
      noStroke();
      textAlign(CENTER, CENTER);
      const tick = 0.5;
      const maxR = 1.5;
      stroke(150);
      strokeWeight(1);
      for (let x = -maxR; x <= maxR; x += tick) {
        line(x * scaleFactor, -5, x * scaleFactor, 5);
        push();
        scale(1, -1);
        translate(x * scaleFactor, 15);
        noStroke();
        text(x.toFixed(1), 0, 0);
        pop();
      }
      for (let y = -maxR; y <= maxR; y += tick) {
        line(-5, y * scaleFactor, 5, y * scaleFactor);
        push();
        scale(1, -1);
        translate(15, -y * scaleFactor);
        noStroke();
        text(y.toFixed(1), 0, 0);
        pop();
      }
    }

    function getEpitrochoidPoints() {
      const points = [];
      const res = 200;
      for (let t = 0; t <= TWO_PI; t += TWO_PI / res) {
        let x = cos(t) - kVal * cos(3 * t);
        let y = sin(t) - kVal * sin(3 * t);
        points.push({ x, y });
      }
      return points;
    }

    function getEnvelopePoints(type) {
      const points = [];
      const res = 200;
      const R = 1;
      const e = kVal;

      for (let t = 0; t <= TWO_PI; t += TWO_PI / res) {
        let x, y;
        if (type === 'inner') {
          x = 0.5 * R * cos(t) + e * cos(2 * t);
          y = 0.5 * R * sin(t) - e * sin(2 * t);
        } else {
          x = 0.5 * R * cos(t) - e * cos(2 * t);
          y = 0.5 * R * sin(t) + e * sin(2 * t);
        }
        points.push({ x, y });
      }
      return points;
    }

    function drawEpitrochoid() {
      const pts = getEpitrochoidPoints();
      let transform = true;
      if (refFrame === 'envelope') {
        push();
        const C = { x: kVal * cos(2 * phiVal), y: kVal * sin(2 * phiVal) };
        const theta = -2 * phiVal / 3;
        translate(-C.x * scaleFactor, -C.y * scaleFactor);
        rotate(-theta);
      } else if (refFrame === 'fixed centers') {
        push();
      } else {
        push();
      }

      stroke(50, 100, 200);
      strokeWeight(2);
      noFill();
      beginShape();
      pts.forEach(p => vertex(p.x * scaleFactor, p.y * scaleFactor));
      endShape(CLOSE);
      pop();
    }

    function drawEnvelopes() {
      const showInner = envType === 'inner' || envType === 'both';
      const showOuter = envType === 'outer' || envType === 'both';
      const phiCopy = phiVal;

      const drawEnvelopeInternal = (type, color) => {
        const pts = getEnvelopePoints(type);
        stroke(color);
        strokeWeight(2);
        noFill();
        beginShape();
        pts.forEach(p => {
          let x = p.x, y = p.y;
          push();

          if (refFrame === 'epitrochoid') {
            const C = { x: kVal * cos(2 * phiCopy), y: kVal * sin(2 * phiCopy) };
            const theta = -2 * phiCopy / 3;
            translate(C.x * scaleFactor, C.y * scaleFactor);
            rotate(theta);
          } else if (refFrame === 'fixed centers') {
            const C = { x: kVal * cos(2 * phiCopy), y: kVal * sin(2 * phiCopy) };
            const theta = -2 * phiCopy / 3;
            translate(C.x * scaleFactor, C.y * scaleFactor);
            rotate(theta);
          }

          vertex(x * scaleFactor, y * scaleFactor);
          pop();
        });
        endShape(CLOSE);
      };

      push();
      if (showInner) drawEnvelopeInternal('inner', color(230, 150, 0));
      if (showOuter) drawEnvelopeInternal('outer', color(230, 150, 0));
      pop();
    }
  </script>
</body>
</html>
