            </div>

            <hr>

            <div class="slider-group">
                <label for="slider-gamma-sat">\(\Gamma_{sat}\) (mg/m²)</label>
                <input type="range" id="slider-gamma-sat" min="0.1" max="10" step="0.1" value="1.5">
                <span class="value-display" id="gamma-sat-value-display">1.5</span>
            </div>

            <div class="slider-group">
                <label for="slider-r">r (nm)</label>
                <input type="range" id="slider-r" min="50" max="1000" step="10" value="300">
                <span class="value-display" id="r-value-display">300</span>
            </div>

            <div class="slider-group">
                <label for="slider-rpe">r_{pe} (nm)</label>
                <input type="range" id="slider-rpe" min="5" max="50" step="1" value="20">
                <span class="value-display" id="rpe-value-display">20</span>
            </div>

            <div class="slider-group">
                <label for="slider-mw">M_W (kDa)</label>
                <input type="range" id="slider-mw" min="10" max="1000" step="1" value="166">
                <span class="value-display" id="mw-value-display">166</span>
            </div>

            <hr>

            <div class="slider-group">
                <label for="slider-phi-axis">\(\phi\) axis max.</label>
                <input type="range" id="slider-phi-axis" min="0.01" max="0.5" step="0.01" value="0.05">
                <span class="value-display" id="phi-axis-value-display">0.05</span>
            </div>

            <div class="slider-group">
                <label for="slider-c-axis">C axis max.</label>
                <input type="range" id="slider-c-axis" min="1.0" max="5.0" step="0.1" value="2.0">
                <span class="value-display" id="c-axis-value-display">2.0</span>
            </div>
        </div>

        <div id="vis-area">
            <div id="info-display">
                <p id="formula-display">
                    <span style="color:blue;">y₁(ϕ)</span>, 
                    <span style="color:red;">y₂(ϕ)</span>, and 
                    <span style="color:green;">y₃(ϕ)</span>
                </p>
                <p id="phimax-display">ϕ_max = </p>
                <p id="range-display">Stability range: </p>
            </div>
            <div id="plot-container"></div>
        </div>
    </div>

    <script>
        // Constants
        const C_UNIT_CONVERSION = 1000;
        const K_DEPLETION = 0.137;

        // Get all slider elements
        const sliders = {
            phi: document.getElementById('slider-phi'),
            gammaSat: document.getElementById('slider-gamma-sat'),
            r: document.getElementById('slider-r'),
            rpe: document.getElementById('slider-rpe'),
            mw: document.getElementById('slider-mw'),
            phiAxis: document.getElementById('slider-phi-axis'),
            cAxis: document.getElementById('slider-c-axis')
        };

        // Get all display elements
        const displays = {
            phi: document.getElementById('phi-value-display'),
            gammaSat: document.getElementById('gamma-sat-value-display'),
            r: document.getElementById('r-value-display'),
            rpe: document.getElementById('rpe-value-display'),
            mw: document.getElementById('mw-value-display'),
            phiAxis: document.getElementById('phi-axis-value-display'),
            cAxis: document.getElementById('c-axis-value-display'),
            phimax: document.getElementById('phimax-display'),
            range: document.getElementById('range-display')
        };

        // Function definitions
        function y1(phi, gammaSat, r) {
            return (3 * gammaSat / r) * C_UNIT_CONVERSION * phi;
        }

        function y2(phi, gammaSat, r) {
            return y1(phi, gammaSat, r) / (1 - phi);
        }

        function y3(phi, gammaSat, r, mw, rpe) {
            return ((3 * gammaSat / r) * C_UNIT_CONVERSION) * phi + (K_DEPLETION * mw / (rpe ** 3)) / phi;
        }

        function calculatePhiMax(gammaSat, r, mw, rpe) {
            const A = (3 * gammaSat / r) * C_UNIT_CONVERSION;
            const B = K_DEPLETION * mw / (rpe ** 3);
            return Math.sqrt(B / A);
        }

        // Generate plot data
        function generatePlotData() {
            const gammaSat = parseFloat(sliders.gammaSat.value);
            const r = parseFloat(sliders.r.value);
            const rpe = parseFloat(sliders.rpe.value);
            const mw = parseFloat(sliders.mw.value);
            const phiAxisMax = parseFloat(sliders.phiAxis.value);
            const cAxisMax = parseFloat(sliders.cAxis.value);
            const currentPhi = parseFloat(sliders.phi.value);

            const phiMax = calculatePhiMax(gammaSat, r, mw, rpe);
            const phiValues = [];
            const y1Values = [];
            const y2Values = [];
            const y3Values = [];

            const steps = 100;
            for (let i = 0; i <= steps; i++) {
                const phi = (i / steps) * phiAxisMax;
                phiValues.push(phi);
                y1Values.push(y1(phi, gammaSat, r));
                y2Values.push(y2(phi, gammaSat, r));
                y3Values.push(y3(phi, gammaSat, r, mw, rpe));
            }

            // Create shaded region data
            const shadedPhi = [];
            const shadedY2 = [];
            const shadedY3 = [];
            for (let i = 0; i <= steps; i++) {
                const phi = (i / steps) * Math.min(phiMax, phiAxisMax);
                shadedPhi.push(phi);
                shadedY2.push(y2(phi, gammaSat, r));
                shadedY3.push(y3(phi, gammaSat, r, mw, rpe));
            }

            // Current marker values
            const currentY1 = y1(currentPhi, gammaSat, r);
            const currentY2 = y2(currentPhi, gammaSat, r);
            const currentY3 = y3(currentPhi, gammaSat, r, mw, rpe);

            return {
                phiValues,
                y1Values,
                y2Values,
                y3Values,
                shadedPhi,
                shadedY2,
                shadedY3,
                currentPhi,
                currentY1,
                currentY2,
                currentY3,
                phiMax,
                phiAxisMax,
                cAxisMax
            };
        }

        // Update plot
        function updatePlot() {
            const data = generatePlotData();

            const traces = [
                {
                    x: data.phiValues,
                    y: data.y1Values,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'blue' },
                    name: 'y1'
                },
                {
                    x: data.phiValues,
                    y: data.y2Values,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'red' },
                    name: 'y2'
                },
                {
                    x: data.phiValues,
                    y: data.y3Values,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'green' },
                    name: 'y3'
                },
                {
                    x: data.shadedPhi,
                    y: data.shadedY2,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'transparent' },
                    showlegend: false,
                    hoverinfo: 'skip'
                },
                {
                    x: data.shadedPhi,
                    y: data.shadedY3,
                    type: 'scatter',
                    mode: 'lines',
                    fill: 'tonexty',
                    fillcolor: 'rgba(173, 216, 230, 0.5)',
                    line: { color: 'transparent' },
                    showlegend: false,
                    hoverinfo: 'skip'
                },
                {
                    x: [data.currentPhi, data.currentPhi, data.currentPhi],
                    y: [data.currentY1, data.currentY2, data.currentY3],
                    type: 'scatter',
                    mode: 'markers',
                    marker: { color: ['blue', 'red', 'green'], size: 8 },
                    showlegend: false
                }
            ];

            const layout = {
                xaxis: {
                    title: 'ϕ (volume fraction)',
                    range: [0, data.phiAxisMax]
                },
                yaxis: {
                    title: 'C (kg/m³)',
                    range: [0, data.cAxisMax]
                },
                shapes: [
                    {
                        type: 'line',
                        x0: data.currentPhi,
                        x1: data.currentPhi,
                        y0: 0,
                        y1: data.cAxisMax,
                        line: { color: 'grey', dash: 'dash' }
                    }
                ],
                showlegend: false,
                margin: { t: 20, r: 20, b: 40, l: 50 }
            };

            Plotly.react('plot-container', traces, layout);

            // Update info display
            displays.phimax.textContent = `ϕ_max = ${data.phiMax.toFixed(4)}`;
            displays.range.textContent = `Stability range: [${data.currentY2.toFixed(2)}, ${data.currentY3.toFixed(2)}] kg/m³`;
        }

        // Update slider displays
        function updateSliderDisplays() {
            displays.phi.textContent = sliders.phi.value;
            displays.gammaSat.textContent = sliders.gammaSat.value;
            displays.r.textContent = sliders.r.value;
            displays.rpe.textContent = sliders.rpe.value;
            displays.mw.textContent = sliders.mw.value;
            displays.phiAxis.textContent = sliders.phiAxis.value;
            displays.cAxis.textContent = sliders.cAxis.value;
        }

        // Event listeners
        Object.values(sliders).forEach(slider => {
            slider.addEventListener('input', () => {
                updateSliderDisplays();
                if (slider === sliders.phi) {
                    // Only update markers and range
                    const data = generatePlotData();
                    Plotly.restyle('plot-container', {
                        'x': [[data.currentPhi, data.currentPhi, data.currentPhi]],
                        'y': [[data.currentY1, data.currentY2, data.currentY3]]
                    }, [5]);
                    Plotly.relayout('plot-container', {
                        'shapes[0].x0': data.currentPhi,
                        'shapes[0].x1': data.currentPhi
                    });
                    displays.range.textContent = `Stability range: [${data.currentY2.toFixed(2)}, ${data.currentY3.toFixed(2)}] kg/m³`;
                } else if (slider === sliders.phiAxis || slider === sliders.cAxis) {
                    // Update axis ranges
                    const data = generatePlotData();
                    sliders.phi.max = data.phiAxisMax;
                    Plotly.relayout('plot-container', {
                        'xaxis.range': [0, data.phiAxisMax],
                        'yaxis.range': [0, data.cAxisMax],
                        'shapes[0].y1': data.cAxisMax
                    });
                } else {
                    // Full update for parameter changes
                    updatePlot();
                }
            });
        });

        // Initialize
        updateSliderDisplays();
        updatePlot();
    </script>
</body>
</html>
