        <input type="range" id="slider-v1" min="1" max="20" step="0.1" value="10">
        <span id="label-v1" class="label-display">10.0 m/s</span>

        <h4>Object 2</h4>
        <label for="slider-m2">mass (kg)</label>
        <input type="range" id="slider-m2" min="1" max="10" step="0.01" value="3">
        <span id="label-m2" class="label-display">3.00 kg</span>

        <label for="slider-v2">speed (m/s)</label>
        <input type="range" id="slider-v2" min="1" max="20" step="0.1" value="5">
        <span id="label-v2" class="label-display">5.0 m/s</span>

        <label for="joystick-container">observer velocity (m/s)</label>
        <div class="label-display">
            X: <span id="label-v0x">1.1</span> | Y: <span id="label-v0y">1.1</span>
        </div>
        <div id="joystick-container">
            <div id="joystick-knob"></div>
        </div>
    </div>

    <div id="canvas-container"></div>
</div>

<script>
// Global SLIDER values (live)
let timeSlider, m1Slider, v1Slider, m2Slider, v2Slider;

// Joystick values (float)
let v0x = 1.1;
let v0y = 1.1;

// Joystick references
let joystickContainer, joystickKnob;

// p5.js canvas dimensions
const canvasW = 600;
const canvasH = 400;
const centerX = () => canvasW / 2;
const centerY = () => canvasH / 2;

function setupJoystick() {
    joystickContainer = document.getElementById('joystick-container');
    joystickKnob = document.getElementById('joystick-knob');

    function updateKnob(clientX, clientY) {
        const rect = joystickContainer.getBoundingClientRect();
        const radius = rect.width / 2;
        const centerX = rect.left + radius;
        const centerY = rect.top + radius;

        let dx = (clientX - centerX);
        let dy = (clientY - centerY);
        const mag = Math.sqrt(dx*dx + dy*dy);
        const maxDist = radius - joystickKnob.offsetWidth / 2;

        if (mag > maxDist) {
            dx = (dx / mag) * maxDist;
            dy = (dy / mag) * maxDist;
        }

        joystickKnob.style.left = (radius + dx - joystickKnob.offsetWidth / 2) + 'px';
        joystickKnob.style.top = (radius + dy - joystickKnob.offsetHeight / 2) + 'px';

        // Map from pixels (-maxDist ... +maxDist) to default values (-5 ... 5)
        const scale = 5.0 / maxDist;
        v0x = dx * scale;
        v0y = -dy * scale; // y-axis is inverted in screen space

        // Clamp
        v0x = Math.max(-5, Math.min(5, v0x));
        v0y = Math.max(-5, Math.min(5, v0y));

        // Update label displays
        document.getElementById('label-v0x').textContent = v0x.toFixed(1);
        document.getElementById('label-v0y').textContent = v0y.toFixed(1);
    }

    // Mouse events
    joystickContainer.addEventListener('mousedown', e => {
        updateKnob(e.clientX, e.clientY);
        window.addEventListener('mousemove', move);
        window.addEventListener('mouseup', up);
        joystickKnob.style.cursor = 'grabbing';
    });
    function move(e) {
        updateKnob(e.clientX, e.clientY);
    }
    function up() {
        window.removeEventListener('mousemove', move);
        window.removeEventListener('mouseup', up);
        joystickKnob.style.cursor = 'grab';
    }

    // Touch events
    joystickContainer.addEventListener('touchstart', e => {
        e.preventDefault();
        updateKnob(e.touches[0].clientX, e.touches[0].clientY);
        window.addEventListener('touchmove', touchMove);
        window.addEventListener('touchend', touchEnd);
    });
    function touchMove(e) {
        if (e.touches.length > 0) {
            updateKnob(e.touches[0].clientX, e.touches[0].clientY);
        }
    }
    function touchEnd() {
        window.removeEventListener('touchmove', touchMove);
        window.removeEventListener('touchend', touchEnd);
    }
}

function setupSliders() {
    timeSlider = document.getElementById('slider-time');
    m1Slider = document.getElementById('slider-m1');
    v1Slider = document.getElementById('slider-v1');
    m2Slider = document.getElementById('slider-m2');
    v2Slider = document.getElementById('slider-v2');

    [timeSlider, m1Slider, v1Slider, m2Slider, v2Slider]
        .forEach(slider => slider.addEventListener('input', updateLabels));
}

function updateLabels() {
    document.getElementById('label-time').textContent = parseFloat(timeSlider.value).toFixed(1) + ' s';
    document.getElementById('label-m1').textContent = parseFloat(m1Slider.value).toFixed(2) + ' kg';
    document.getElementById('label-v1').textContent = parseFloat(v1Slider.value).toFixed(1) + ' m/s';
    document.getElementById('label-m2').textContent = parseFloat(m2Slider.value).toFixed(2) + ' kg';
    document.getElementById('label-v2').textContent = parseFloat(v2Slider.value).toFixed(1) + ' m/s';
}

function getSliderValues() {
    return {
        t: parseFloat(timeSlider.value),
        m1: parseFloat(m1Slider.value),
        v1cm: parseFloat(v1Slider.value),
        m2: parseFloat(m2Slider.value),
        // v2 cm only for display, actual cm velocity is calculated by formula
        v0x, v0y
    };
}

function sketch(p) {
    p.setup = () => {
        const canvasDiv = document.getElementById('canvas-container');
        p.createCanvas(canvasDiv.offsetWidth, canvasDiv.offsetHeight);
    };

    p.draw = () => {
        p.background(255);

        const {t, m1, v1cm, m2, v0x, v0y} = getSliderValues();

        // CM Frame velocities set up by scheme
        const u1_cm = p.createVector(v1cm, 0);
        const s2_cm = (m1/m2) * v1cm;
        const u2_cm = p.createVector(-s2_cm, 0);
        const v0 = p.createVector(v0x, v0y);

        // After collision final velocities (rotation)
        const v1_cm = p.constructor.Vector.fromAngle(-p.PI / 4).mult(u1_cm.mag());
        const v2_cm = p.constructor.Vector.fromAngle(-p.PI / 4).mult(u2_cm.mag()).rotate(p.PI);

        // Choose pre-collision or post-collision vector
        const vel1 = (t < 0) ? u1_cm : v1_cm;
        const vel2 = (t < 0) ? u2_cm : v2_cm;

        // Observer frame positions at time t
        const scale = 20; // pixels per meter
        const ox = centerX();
        const oy = centerY();
        const pos1 = p.createVector(
            ox + (vel1.x * t + v0x * t) * scale,
            oy + (vel1.y * t + v0y * t) * scale
        );
        const pos2 = p.createVector(
            ox + (vel2.x * t + v0x * t) * scale,
            oy + (vel2.y * t + v0y * t) * scale
        );

        // Radii proportional to sqrt mass
        const r1 = p.sqrt(m1) * 4;
        const r2 = p.sqrt(m2) * 4;

        // Draw trajectories
        p.push();
        p.drawingContext.setLineDash([5, 5]);
        // Future
        p.stroke(255,0,0,150); p.line(pos1.x, pos1.y, ox + (t + 20)*scale*(vel1.x + v0x)/scale, oy + (t + 20)*scale*(vel1.y + v0y)/scale);
        p.stroke(0,0,255,150); p.line(pos2.x, pos2.y, ox + (t + 20)*scale*(vel2.x + v0x)/scale, oy + (t + 20)*scale*(vel2.y + v0y)/scale);
        // Past
        p.stroke(255,0,0,150); p.line(pos1.x, pos1.y, ox + (t - 20)*scale*(vel1.x + v0x)/scale, oy + (t - 20)*scale*(vel1.y + v0y)/scale);
        p.stroke(0,0,255,150); p.line(pos2.x, pos2.y, ox + (t - 20)*scale*(vel2.x + v0x)/scale, oy + (t - 20)*scale*(vel2.y + v0y)/scale);
        p.drawingContext.setLineDash([]);
        p.pop();

        // Draw particles
        p.fill(255,0,0); p.noStroke(); p.circle(pos1.x, pos1.y, r1 * 2);
        p.fill(0,0,255); p.noStroke(); p.circle(pos2.x, pos2.y, r2 * 2);

        // Draw observer arrow negating v0
        const arrowStart = p.createVector(ox + 100, oy + 100);
        const arrowEnd = p.constructor.Vector.add(arrowStart, p.constructor.Vector.mult(v0, -8));
        p.stroke(0); p.strokeWeight(2); p.line(arrowStart.x, arrowStart.y, arrowEnd.x, arrowEnd.y);
        const angle = arrowStart.copy().sub(arrowEnd).heading();
        p.fill(0);
        p.triangle(
            arrowEnd.x, arrowEnd.y,
            arrowEnd.x + 8 * p.cos(angle + p.PI / 6 + p.PI), arrowEnd.y + 8 * p.sin(angle + p.PI / 6 + p.PI),
            arrowEnd.x + 8 * p.cos(angle - p.PI / 6 + p.PI), arrowEnd.y + 8 * p.sin(angle - p.PI / 6 + p.PI)
        );

        // Display static metrics (pre-collision t = -1s)
        const pre_vel1 = u1_cm.copy().add(v0);
        const pre_vel2 = u2_cm.copy().add(v0);
        const v0Mag = v0.mag();
        const v1Mag = pre_vel1.mag();
        const v2Mag = pre_vel2.mag();
        const E = 0.5 * m1 * v1Mag ** 2 + 0.5 * m2 * v2Mag ** 2;

        p.fill(0);
        p.textSize(14);
        p.textAlign(p.LEFT, p.TOP);
        p.text(
`Observer:
v\u2080 = ${v0Mag.toFixed(2)} m/s
v\u2081 = ${v1Mag.toFixed(2)} m/s
v\u2082 = ${v2Mag.toFixed(2)} m/s
Energy = ${E.toExponential(2)} J`,
            ox - 220, oy - 180);
    };

    p.windowResized = () => {
        const canvasDiv = document.getElementById('canvas-container');
        p.resizeCanvas(canvasDiv.offsetWidth, canvasDiv.offsetHeight);
    };
}

// Setup DOM interactions and p5
window.addEventListener('DOMContentLoaded', () => {
    setupSliders();
    setupJoystick();
    new p5(sketch, 'canvas-container');
});
</script>
</body>
</html>
```
