<body>

<div id="demo-container">
    <!-- Control Panel -->
    <div id="control-panel">
        <label for="shift-slider">shift</label>
        <input type="range" id="shift-slider" min="0" max="25" step="1" value="12">

        <input type="text" id="text-input" list="phrase-list" value="abcd" placeholder="Enter text to encrypt">
        <datalist id="phrase-list">
            <option value="abcd"></option>
            <option value="test phrase"></option>
            <option value="to be or not to be"></option>
            <option value="the quick brown fox jumped over the lazy dog"></option>
        </datalist>
    </div>

    <!-- Display Area -->
    <div id="display-area">
        <!-- Visualization Section -->
        <div id="alphabet-visualization">
            <div id="plaintext-alphabet" class="alphabet-row"></div>
            <div id="ciphertext-alphabet" class="alphabet-row"></div>
        </div>

        <!-- Key Section -->
        <div id="keys">
            <p id="encryption-key-display">encryption key: 12</p>
            <p id="decryption-key-display">decryption key: 14</p>
        </div>

        <!-- Text Output Section -->
        <div id="text-output">
            <p id="plaintext-output"><span class="plain-label">plaintext :</span> abcd</p>
            <p id="ciphertext-output"><span class="cipher-label">ciphertext:</span> MNOP</p>
        </div>
    </div>
</div>

<script>
    // Alphabet reference
    const alphabet = 'abcdefghijklmnopqrstuvwxyz';

    // Main update function
    function updateCipher(){
        const shiftValue = parseInt(document.getElementById('shift-slider').value,10);
        const text       = document.getElementById('text-input').value;

        // Update alphabet visualization
        renderAlphabets(shiftValue);

        // Update key displays
        document.getElementById('encryption-key-display').textContent = 'encryption key: ' + shiftValue;
        const decryptKey = (26 - shiftValue) % 26;
        document.getElementById('decryption-key-display').textContent = 'decryption key: ' + decryptKey;

        // Update text outputs
        document.getElementById('plaintext-output').innerHTML  = '<span class="plain-label">plaintext :</span> ' + text;
        const cipherText = encrypt(text, shiftValue);
        document.getElementById('ciphertext-output').innerHTML = '<span class="cipher-label">ciphertext:</span> ' + cipherText;
    }

    // Render plaintext & ciphertext alphabets
    function renderAlphabets(shift){
        const plainDiv  = document.getElementById('plaintext-alphabet');
        const cipherDiv = document.getElementById('ciphertext-alphabet');
        // Clear previous
        plainDiv.innerHTML = '';
        cipherDiv.innerHTML = '';

        for(let i=0;i<26;i++){
            // Plaintext letter
            const pSpan = document.createElement('span');
            pSpan.textContent = alphabet[i];
            plainDiv.appendChild(pSpan);

            // Cipher letter with shift, uppercase
            const shiftedIndex = (i + shift) % 26;
            const cSpan = document.createElement('span');
            cSpan.textContent = alphabet[shiftedIndex].toUpperCase();
            cipherDiv.appendChild(cSpan);
        }
    }

    // Caesar cipher encryption
    function encrypt(text, shift){
        let result = '';
        for(const ch of text){
            const lower = ch.toLowerCase();
            if(alphabet.includes(lower)){
                const index = alphabet.indexOf(lower);
                const cipherIndex = (index + shift) % 26;
                result += alphabet[cipherIndex].toUpperCase();
            }else{
                // Keep non-letter characters unchanged
                result += ch;
            }
        }
        return result;
    }

    // Event listeners
    document.getElementById('shift-slider').addEventListener('input', updateCipher);
    document.getElementById('text-input').addEventListener('input', updateCipher);

    // Initial render
    document.addEventListener('DOMContentLoaded', updateCipher);
</script>

</body>
</html>
