    }
    #coordination-buttons button{
        padding:6px 12px;
        border:1px solid #666;
        background:#e0e0e0;
        cursor:pointer;
        border-radius:4px;
        transition:background .2s, color .2s;
    }
    #coordination-buttons button.active{
        background:#666;
