<body>

<!-- ========== CONTROL PANEL ========== -->
<div id="control-panel">
  <label for="slider-angle">slope θ</label>
  <input
    type="range"
    id="slider-angle"
    min="10"
    max="80"
    step="1"
    value="30"
  >
  <div id="animation-controls">
    <p style="margin:5px 0;">release block</p>
    <button id="btn-play">▶</button>
    <button id="btn-pause">||</button>
    <button id="btn-reset">|<</button>
  </div>
</div>

<!-- ========== VISUALIZATION AREA ========== -->
<div id="visualization-area">
  <p id="info-display">
    slope = \( \theta \) = 30°; acceleration = \( g \sin(\theta) \) = 4.905 m/s²
  </p>
  <!-- Placeholder canvas – will be replaced by p5 -->
  <canvas id="p5-canvas" width="800" height="600"></canvas>
</div>

<!-- ========== MAIN SCRIPT ========== -->
<script>
/* ---------------- GLOBAL STATE ---------------- */
const g      = 9.81;         // m/s²
let angleDeg = 30;           // initial angle
let angleRad = angleDeg * Math.PI / 180;

let isPlaying   = false;
let startTime   = null;      // ms since epoch
let sPixels     = 0;         // displacement along ramp in pixels
const rampL_px  = 400;       // total ramp length (pixels)
const rampL_m   = 5;         // same length in metres (for conversion)
const pxPerM    = rampL_px / rampL_m;

const blockSize = 40;        // px (length of one edge)
let p5canvas;                // will hold the real p5 canvas element

/* ------------------- UI HOOKS ------------------ */
const slider    = document.getElementById('slider-angle');
const btnPlay   = document.getElementById('btn-play');
const btnPause  = document.getElementById('btn-pause');
const btnReset  = document.getElementById('btn-reset');
const infoDisp  = document.getElementById('info-display');

/* ------------   MATHJAX UPDATE  ---------------- */
function updateInfoDisplay(){
  const a = g * Math.sin(angleRad);
  infoDisp.innerHTML =
    `slope = \\( \\theta \\) = ${angleDeg}°; `+
    `acceleration = \\( g \\sin(\\theta) \\) = ${a.toFixed(3)} \\text{ m/s}^2`;
  MathJax.typesetPromise();
}

/* --------------  ANIMATION LOGIC  -------------- */
function resetAnimation(){
  isPlaying = false;
  startTime = null;
  sPixels   = 0;
}

/* --------------  EVENT LISTENERS  -------------- */
slider.addEventListener('input', () => {
  angleDeg = parseInt(slider.value,10);
  angleRad = angleDeg * Math.PI / 180;
  resetAnimation();
  updateInfoDisplay();
});

btnPlay.addEventListener('click', () => {
  if(!isPlaying){
    if(startTime === null){            // new run
      startTime = performance.now();
    }else{                             // resume after pause
      // shift startTime so that elapsed stays consistent
      startTime = performance.now() - elapsedDuringPause;
    }
    isPlaying = true;
  }
});

let elapsedDuringPause = 0;

btnPause.addEventListener('click', () => {
  if(isPlaying){
    elapsedDuringPause = performance.now() - startTime;
    isPlaying = false;
  }
});

btnReset.addEventListener('click', () => {
  resetAnimation();
});

/* ------------------  P5 SKETCH  ---------------- */
function sketch(p){
  // Geometry helpers
  const origin = p.createVector(100, 500); // bottom-left point of the ramp

  p.setup = function(){
    // Remove placeholder and create real canvas
    const placeholder = document.getElementById('p5-canvas');
    p5canvas = p.createCanvas(placeholder.width, placeholder.height);
    p5canvas.id('p5-canvas'); // keep the same ID
    placeholder.replaceWith(p5canvas.elt);
    p.frameRate(60);
  };

  function drawArrow(x1,y1,x2,y2,col,label){
    p.push();
    p.stroke(col);
    p.fill(col);
    p.strokeWeight(2);
    p.line(x1,y1,x2,y2);
    // arrow head
    const headSize = 8;
    const angle = Math.atan2(y2-y1,x2-x1);
    p.push();
      p.translate(x2,y2);
      p.rotate(angle);
      p.triangle(0,0, -headSize,-headSize/2, -headSize,headSize/2);
    p.pop();
    // label
    p.noStroke();
    p.fill(col);
    p.textSize(14);
    p.text(label, x2+5, y2+5);
    p.pop();
  }

  function drawScene(){
    p.background(255);

    /* ---------- Ramp Geometry ---------- */
    const dirDown = p.createVector(Math.cos(angleRad), Math.sin(angleRad)); // along +x,+y but y axis downward
    const dirUp   = p5.Vector.mult(dirDown, -1);
    const top     = p5.Vector.add(origin, p.createVector(rampL_px*Math.cos(angleRad), -rampL_px*Math.sin(angleRad)));

    // Draw inclined plane
    p.push();
      p.stroke('yellow');
      p.strokeWeight(2);
      p.fill('lightblue');
      p.triangle(origin.x, origin.y,
                 top.x,    top.y,
                 origin.x + rampL_px, origin.y);
    p.pop();

    // Draw angle marker at bottom-left
    p.push();
      p.noFill();
      p.stroke(0);
      p.arc(origin.x, origin.y, 60, 60, -angleRad, 0); // small arc
      p.noStroke();
      p.fill(0);
      p.textSize(16);
      p.text('θ', origin.x + 25*Math.cos(-angleRad/2),
                  origin.y - 25*Math.sin(-angleRad/2));
    p.pop();

    /* ---------- Block Position ---------- */
    const blockCenter = p5.Vector.add(top, p5.Vector.mult(dirDown, sPixels));
    const perp = p.createVector(-dirDown.y, dirDown.x); // perpendicular for rectangle drawing

    p.push();
      p.fill('darkgrey');
      p.stroke(0);
      p.rectMode(p.CENTER);
      p.translate(blockCenter.x, blockCenter.y);
      p.rotate(-angleRad);
      p.rect(0,0, blockSize, blockSize);
    p.pop();

    /* ---------- Coordinate Axes ---------- */
    p.push();
      const axisLength = 80;
      // x-axis (along slope)
      drawArrow(origin.x, origin.y,
                origin.x + axisLength*Math.cos(angleRad),
                origin.y - axisLength*Math.sin(angleRad),
                'black','x');
      // y-axis (perpendicular)
      drawArrow(origin.x, origin.y,
                origin.x - axisLength*Math.sin(angleRad),
                origin.y - axisLength*Math.cos(angleRad),
                'black','y');
    p.pop();

    /* ---------- Force Vectors ---------- */
    const mg       = 1 * g; // assuming m=1kg
    const scaleF   = 8;     // px per newton (visual scaling)
    const center   = blockCenter;
    const gravEnd  = p.createVector(center.x, center.y + mg*scaleF);
    const normEnd  = p5.Vector.add(center,
                      p.createVector(-mg*Math.cos(angleRad)*scaleF*Math.sin(angleRad),
                                     -mg*Math.cos(angleRad)*scaleF*Math.cos(angleRad)));
    const netEnd   = p5.Vector.add(center,
                      p.createVector(mg*Math.sin(angleRad)*scaleF*Math.cos(angleRad),
                                     -mg*Math.sin(angleRad)*scaleF*Math.sin(angleRad)));

    drawArrow(center.x, center.y, gravEnd.x, gravEnd.y, 'red', 'F_grav');
    drawArrow(center.x, center.y, normEnd.x, normEnd.y, 'deepskyblue', 'normal');
    drawArrow(center.x, center.y, netEnd.x,  netEnd.y,  'purple', 'F_net');

    /* ---------- Decomposition (dashed) ---------- */
    p.push();
      p.stroke(0);
      p.strokeWeight(1);
      p.drawingContext.setLineDash([6,4]);
      // perpendicular component projection
      p.line(gravEnd.x, gravEnd.y, normEnd.x, normEnd.y);
      // parallel component projection
      p.line(normEnd.x, normEnd.y, center.x + (gravEnd.x - normEnd.x), center.y);
      p.drawingContext.setLineDash([]);
      // labels
      p.noStroke();
      p.fill(0);
      p.textSize(12);
      p.text('mg cos(θ)', (center.x+normEnd.x)/2 - 10, (center.y+normEnd.y)/2 - 5);
      p.text('mg sin(θ)', (center.x+netEnd.x)/2 + 5,  (center.y+netEnd.y)/2 - 5);
    p.pop();
  }

  p.draw = function(){
    drawScene();

    if(isPlaying){
      const tSec = (performance.now() - startTime)/1000;
      const a    = g * Math.sin(angleRad);   // m/s²
      const s_m  = 0.5 * a * tSec * tSec;    // metres
      sPixels    = s_m * pxPerM;

      // stop at bottom
      if(sPixels > rampL_px - blockSize/2){
        sPixels   = rampL_px - blockSize/2;
        isPlaying = false;
      }
    }
  };
}

new p5(sketch);
</script>
