/* ===================== HELPER FUNCTIONS ===================== */
const asciiToBin = str =>
    str.split('').map(ch => ch.charCodeAt(0).toString(2).padStart(8,'0')).join('');

const padRight = (bin,lenMultiple) =>{
    while(bin.length % lenMultiple !==0) bin+='0';
    return bin;
};

const splitEvery = (str,size) => {
    const arr=[];
    for(let i=0;i<str.length;i+=size) arr.push(str.slice(i,i+size));
    return arr;
};

const permute = (input,table) => table.map(pos => input[pos-1]).join('');

const xorBin = (a,b)=> a.split('').map((bit,i)=> bit===b[i] ? '0':'1').join('');

const leftShift = (bits,shift)=> bits.slice(shift)+bits.slice(0,shift);

const bin6To4ViaSbox = (sixBits,boxIndex) =>{
    const row = parseInt(sixBits[0]+sixBits[5],2);
    const col = parseInt(sixBits.slice(1,5),2);
    const val = SBOX[boxIndex][row*16+col];
    return val.toString(2).padStart(4,'0');
};

const bin64ToHex16 = bin=>{
    let hex='';
    for(let i=0;i<16;i++){
        hex+=parseInt(bin.slice(i*4,i*4+4),2).toString(16);
