/* ===================== DES ONE BLOCK ===================== */
function desEncryptBlock(block64,subkeys,rounds){
    let permuted = permute(block64,IP);
    let L = permuted.slice(0,32);
    let R = permuted.slice(32,64);
    for(let round=0; round<rounds; round++){
        const E_R = permute(R,E); //48 bits
        const xorRes = xorBin(E_R,subkeys[round]);
