        let sboxOutput='';
        for(let i=0;i<8;i++){
            const sixBits = xorRes.slice(i*6,i*6+6);
            sboxOutput += bin6To4ViaSbox(sixBits,i);
        }
        // P permutation
        const fResult = permute(sboxOutput,P); //32 bits
        const newR = xorBin(L,fResult);
        L = R;
        R = newR;
    }
    // combine (note swap) and final permutation
    const preoutput = R+L;
    const ciphertext = permute(preoutput,FP);
    return ciphertext; //64-bit bin
