    const messageBin = padRight(asciiToBin(message),64);
    const blocks = splitEvery(messageBin,64);

    let keyBin = asciiToBin(keyInput);
    if(keyBin.length>64) keyBin = keyBin.slice(0,64);
    keyBin = padRight(keyBin,64);

    const allSubkeys = generateSubkeys(keyBin);

