    for(let s=2; s<=steps; s+=2){
        let cipherHexTotal='';
        for(const block of blocks){
            const ciphertextBin = desEncryptBlock(block,allSubkeys,s);
            cipherHexTotal += bin64ToHex16(ciphertextBin);
        }
        const line = s.toString().padStart(2,' ') + '    ' + cipherHexTotal;
        resultsElem.textContent += line + '\n';
    }
}

