<script>
/*
=====================================================
        GLOBAL STATE & HELPER FUNCTIONS
=====================================================
*/
const state = {
    currentAlgorithm : 'shellsort',
    numElements      : 10,
    sortHistory      : [],
    currentStep      : 1,
    isPlaying        : false,
    playInterval     : null
};

// Fisher–Yates shuffle
function shuffle(array){
    for(let i=array.length-1;i>0;i--){
        const j = Math.floor(Math.random()*(i+1));
        [array[i],array[j]]=[array[j],array[i]];
    }
    return array;
}

function isSorted(arr){
    for(let i=1;i<arr.length;i++){
        if(arr[i-1]>arr[i]) return false;
    }
    return true;
}

function clone(arr){return arr.slice();}

/*
=====================================================
          SORTING ALGORITHMS (with history)
=====================================================
*/

function shellSort(arr){
    const history=[clone(arr)];
    let n = arr.length;
    for(let gap=Math.floor(n/2); gap>0; gap=Math.floor(gap/2)){
        for(let i=gap; i<n; i++){
            let temp=arr[i];
            let j;
            for(j=i; j>=gap && arr[j-gap]>temp; j-=gap){
                arr[j]=arr[j-gap];
                history.push(clone(arr));
            }
            arr[j]=temp;
            history.push(clone(arr));
        }
    }
    return history;
}

function bubbleSort(arr){
    const history=[clone(arr)];
    let n=arr.length, swapped=true;
    while(swapped){
        swapped=false;
        for(let i=1;i<n;i++){
            if(arr[i-1]>arr[i]){
                [arr[i-1],arr[i]]=[arr[i],arr[i-1]];
                swapped=true;
                history.push(clone(arr));
            }
        }
        n--;
    }
    return history;
}

function insertionSort(arr){
    const history=[clone(arr)];
    for(let i=1;i<arr.length;i++){
        let key=arr[i], j=i-1;
        while(j>=0 && arr[j]>key){
            arr[j+1]=arr[j];
            j--;
            history.push(clone(arr));
        }
        arr[j+1]=key;
        history.push(clone(arr));
    }
    return history;
}

function cocktailSort(arr){
    const history=[clone(arr)];
    let start=0, end=arr.length-1, swapped=true;
    while(swapped){
        swapped=false;
        for(let i=start;i<end;i++){
            if(arr[i]>arr[i+1]){
                [arr[i],arr[i+1]]=[arr[i+1],arr[i]];
                swapped=true;
                history.push(clone(arr));
            }
        }
        if(!swapped) break;
        swapped=false;
        end--;
        for(let i=end;i>start;i--){
            if(arr[i-1]>arr[i]){
                [arr[i-1],arr[i]]=[arr[i],arr[i-1]];
                swapped=true;
                history.push(clone(arr));
            }
        }
        start++;
    }
    return history;
}

function bogoSort(arr){
    const history=[clone(arr)];
    const attemptLimit=10000; // safeguard
    let attempts=0;
    while(!isSorted(arr) && attempts<attemptLimit){
        shuffle(arr);
        history.push(clone(arr));
        attempts++;
    }
    return history;
}

function computeSortHistory(arr,algo){
    switch(algo){
        case 'shellsort':     return shellSort(arr);
        case 'bubblesort':    return bubbleSort(arr);
        case 'insertionsort': return insertionSort(arr);
        case 'cocktail':      return cocktailSort(arr);
        case 'bogosort':      return bogoSort(arr);
        default:              return shellSort(arr);
    }
}

/*
=====================================================
                UI EVENT HANDLERS
=====================================================
*/

document.addEventListener('DOMContentLoaded', ()=>{

    // cache DOM
    const sliderElements   = document.getElementById('slider-elements');
    const elementsValue    = document.getElementById('elements-value');
    const sliderStep       = document.getElementById('slider-step');
    const stepValue        = document.getElementById('step-value');
    const btnPlayPause     = document.getElementById('btn-play-pause');
    const btnStepBackward  = document.getElementById('btn-step-backward');
    const btnStepForward   = document.getElementById('btn-step-forward');
    const btnResetStep     = document.getElementById('btn-reset-step');
    const btnRandomize     = document.getElementById('btn-randomize');
    const sortButtons      = document.querySelectorAll('.sort-btn');

    // ----- slider-elements
    sliderElements.addEventListener('input',()=>{
        elementsValue.textContent = sliderElements.value;
    });

    // ----- RANDOMIZE logic
    function randomizeData(){
        // Stop animation
        stopPlaying();

        state.numElements = parseInt(sliderElements.value);
        // prepare array
        let arr = Array.from({length:state.numElements},(_,i)=>i+1);
        shuffle(arr);
        // compute history
        state.sortHistory = computeSortHistory(clone(arr),state.currentAlgorithm);
        // reset step slider
        sliderStep.max = state.sortHistory.length;
        sliderStep.value = 1;
        stepValue.textContent = 1;
        state.currentStep = 1;
        // redraw
        redraw();
    }

    btnRandomize.addEventListener('click', randomizeData);

    // ----- SORT BUTTONS
    sortButtons.forEach(btn=>{
        btn.addEventListener('click', ()=>{
            // set active class
            sortButtons.forEach(b=>b.classList.remove('active'));
            btn.classList.add('active');
            // algorithm
            state.currentAlgorithm = btn.id.replace('btn-','');
            // bogosort special handling
            if(state.currentAlgorithm === 'bogosort'){
                sliderElements.value = 7;
                sliderElements.disabled = true;
                elementsValue.textContent = 7;
            }else{
                sliderElements.disabled = false;
            }
            // regenerate
            randomizeData();
        });
    });

    // ----- STEP SLIDER
    sliderStep.addEventListener('input',()=>{
        state.currentStep = parseInt(sliderStep.value);
        stepValue.textContent = state.currentStep;
        stopPlaying();
        redraw();
    });

    // ----- PLAY / PAUSE
    function startPlaying(){
        if(state.isPlaying) return;
        state.isPlaying = true;
        btnPlayPause.textContent = '❚❚';
        state.playInterval = setInterval(()=>{
            if(state.currentStep < state.sortHistory.length){
                state.currentStep++;
                sliderStep.value = state.currentStep;
                stepValue.textContent = state.currentStep;
                redraw();
            }else{
                stopPlaying();
            }
        },200);
    }
    function stopPlaying(){
        if(state.playInterval) clearInterval(state.playInterval);
        state.isPlaying = false;
        btnPlayPause.textContent = '▶';
    }

    btnPlayPause.addEventListener('click',()=>{
        if(state.isPlaying){
            stopPlaying();
        }else{
            startPlaying();
        }
    });

    // ----- STEP BACKWARD
    btnStepBackward.addEventListener('click',()=>{
        if(state.currentStep>1){
            state.currentStep--;
            sliderStep.value = state.currentStep;
            stepValue.textContent = state.currentStep;
            redraw();
        }
    });
    // ----- STEP FORWARD
    btnStepForward.addEventListener('click',()=>{
        if(state.currentStep<state.sortHistory.length){
            state.currentStep++;
            sliderStep.value = state.currentStep;
            stepValue.textContent = state.currentStep;
            redraw();
        }
    });
    // ----- RESET STEP
    btnResetStep.addEventListener('click',()=>{
        state.currentStep=1;
        sliderStep.value = 1;
        stepValue.textContent = 1;
        redraw();
    });

    // INITIALIZE
    randomizeData();
});

/*
=====================================================
                  p5.js SKETCH
=====================================================
*/
const margin = {top:20,right:20,bottom:40,left:40};
let canvasW,canvasH;

function setup(){
    // remove placeholder canvas to avoid duplicate IDs
    const placeholder = document.getElementById('bar-chart-canvas');
    placeholder.parentNode.removeChild(placeholder);

    canvasW = window.innerWidth  * 0.95;
    canvasH = (window.innerHeight - 120) * 0.95;

    const cnv = createCanvas(canvasW, canvasH);
    cnv.parent('visualization-area');
    cnv.id('bar-chart-canvas');

    colorMode(HSB,360,100,100);
    noLoop();
}

function windowResized(){
    canvasW = window.innerWidth  * 0.95;
    canvasH = (window.innerHeight - 120) * 0.95;
    resizeCanvas(canvasW,canvasH);
    redraw();
}

function draw(){
    background(255);

    if(state.sortHistory.length===0) return;
    const arr = state.sortHistory[state.currentStep-1];

    const chartW = width  - margin.left - margin.right;
    const chartH = height - margin.top  - margin.bottom;
    const barWidth = chartW / arr.length;

    // Draw Y axis
    stroke(0);
    line(margin.left, margin.top, margin.left, margin.top+chartH);
    const axisMax = Math.max(state.numElements,20);
    const tickCount = 5;
    textAlign(RIGHT,CENTER);
    fill(0);
    noStroke();
    for(let i=0;i<=tickCount;i++){
        const yVal = (axisMax/tickCount)*i;
        const yPos = map(i,0,tickCount, margin.top+chartH, margin.top);
        stroke(200);
        line(margin.left-5, yPos, margin.left+chartW, yPos);
        noStroke();
        text(yVal.toFixed(0), margin.left-8, yPos);
    }

    // Draw Bars
    for(let i=0;i<arr.length;i++){
        const val = arr[i];
        const h = map(val,1,state.numElements,0,chartH);
        const x = margin.left + i*barWidth;
        const y = margin.top + chartH - h;

        const hue = map(val,1,state.numElements,240,0);
        fill(hue,80,90);
        stroke(0);
        rect(x, y, barWidth*0.9, h);
    }
}
</script>
</body>
</html>
