    <title>Runner Intersection Demo</title>
    <!-- CDN for Plotly.js -->
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <style>
        :root{
            font-family: Arial, Helvetica, sans-serif;
            --blue: rgb(70,130,180);
            --orange: rgb(255,165,0);
            --green: rgb(34,139,34);
