            margin:0 0 8px 0;
            font-weight:normal;
        }
        #plot-container{
            flex:1 1 auto;
            min-height:0; /* important for flex children */
        }
    </style>
</head>
<body>
<div id="demo-container">

    <!-- Control Panel Section -->
    <div id="control-panel">
        <button id="btn-reset">+</button>

        <div class="slider-row">
            <label for="slider-x1">x₁</label>
            <input type="range" id="slider-x1"
                   min="-1000" max="1000" step="1" value="500">
            <span id="value-x1"></span>
        </div>

        <div class="slider-row">
            <label for="slider-v1">v₁</label>
            <input type="range" id="slider-v1"
                   min="-15" max="15" step="0.01" value="-10">
            <span id="value-v1"></span>
        </div>

        <div class="slider-row">
            <label for="slider-x2">x₂</label>
            <input type="range" id="slider-x2"
                   min="-1000" max="1000" step="1" value="100">
            <span id="value-x2"></span>
        </div>

        <div class="slider-row">
            <label for="slider-v2">v₂</label>
            <input type="range" id="slider-v2"
                   min="-15" max="15" step="0.01" value="5">
            <span id="value-v2"></span>
        </div>
    </div>

    <!-- Visualization Section -->
    <div id="visualization-area">
        <h2 id="result-text"></h2>
        <div id="plot-container"></div>
    </div>

</div>

<script>
/* ---------------- State ---------------- */
const defaults = {
    x1: 500,
    v1: -10,
    x2: 100,
    v2: 5
};

const sliders = {
    x1: document.getElementById('slider-x1'),
    v1: document.getElementById('slider-v1'),
    x2: document.getElementById('slider-x2'),
    v2: document.getElementById('slider-v2')
};

const values = {
    x1: document.getElementById('value-x1'),
    v1: document.getElementById('value-v1'),
    x2: document.getElementById('value-x2'),
    v2: document.getElementById('value-v2')
};

const resultText = document.getElementById('result-text');
const plotContainer = document.getElementById('plot-container');
const resetBtn = document.getElementById('btn-reset');

/* ---------------- Utilities ---------------- */
function format(num, decimals=2){
    return (+num).toFixed(decimals);
}

function getState(){
    return {
        x1: +sliders.x1.value,
        v1: +sliders.v1.value,
        x2: +sliders.x2.value,
        v2: +sliders.v2.value
    };
}

function updateValueDisplays(){
    values.x1.textContent = sliders.x1.value;
    values.v1.textContent = format(sliders.v1.value);
    values.x2.textContent = sliders.x2.value;
    values.v2.textContent = format(sliders.v2.value);
}

/* ---------------- Calculation ---------------- */
function calculateIntersection({x1,v1,x2,v2}){
    const EPS = 1e-12;
    if(Math.abs(v2 - v1) < EPS){
        return null; // Parallel
    }
    const T = (x1 - x2)/(v2 - v1);
    if(T < 0 || T > 1000){
        return null; // Outside domain
    }
    const X = x1 + v1*T;
    return {T, X};
}

/* ---------------- Plotting ---------------- */
function buildTraces(state, intersection){
    const tMax = 1000;
    const tRange = [0, tMax];

    const trace1 = {
        x: tRange,
        y: [state.x1, state.x1 + state.v1 * tMax],
        mode: 'lines',
        name: 'Runner 1',
        line: {color: 'rgb(70,130,180)', width:2}
    };

    const trace2 = {
        x: tRange,
        y: [state.x2, state.x2 + state.v2 * tMax],
        mode: 'lines',
        name: 'Runner 2',
        line: {color: 'rgb(255,165,0)', width:2}
    };

    const traces = [trace1, trace2];

    if(intersection){
        traces.push({
            x: [intersection.T],
            y: [intersection.X],
            mode: 'markers',
            name: 'Intersection',
            marker:{
                color:'rgb(34,139,34)',
                size:10,
                line:{color:'rgb(0,100,0)', width:1}
            }
        });
    }
    return traces;
}

function plot(state){
    const intersection = calculateIntersection(state);
    const traces = buildTraces(state, intersection);

    const layout = {
        xaxis:{
            title:'t',
            range:[0,1000],
            gridcolor:'var(--light-gray)'
        },
        yaxis:{
            title:'distance',
            autorange:true,
            zerolinecolor:'#888',
            gridcolor:'var(--light-gray)'
        },
        showlegend:false,
        margin:{t:20, r:20, b:40, l:60},
        hovermode:'closest'
    };

    Plotly.react(plotContainer, traces, layout, {displayModeBar:false});

    // Update text
    if(intersection){
        resultText.textContent = `T = ${format(intersection.T)} s | X = ${format(intersection.X)} m`;
    }else{
        resultText.textContent = "The runners' paths never intersect";
    }
}

/* ---------------- Event Handling ---------------- */
function onSliderInput(){
    updateValueDisplays();
    plot(getState());
}

Object.values(sliders).forEach(slider=>{
    slider.addEventListener('input', onSliderInput);
});

resetBtn.addEventListener('click', ()=>{
    sliders.x1.value = defaults.x1;
    sliders.v1.value = defaults.v1;
    sliders.x2.value = defaults.x2;
    sliders.v2.value = defaults.v2;
    onSliderInput();
});

/* ---------------- Initialization ---------------- */
window.addEventListener('DOMContentLoaded', ()=>{
    updateValueDisplays();
    plot(getState());
});
</script>
</body>
</html>
