<body>
<div id="main-container">

    <!-- Control Panel -->
    <div id="control-panel">
        <label for="slider-phase">phosphorus cycle</label>
        <input type="range" id="slider-phase" min="0" max="8" step="1" value="0">
        <span id="slider-value">0</span>
        <button id="btn-reset">↻ Reset</button>
    </div>

    <!-- Visualization -->
    <div id="canvas-container"></div>

</div>

<script>
/* ---------- DOM elements ---------- */
const slider = document.getElementById('slider-phase');
const sliderValueSpan = document.getElementById('slider-value');
const btnReset = document.getElementById('btn-reset');

/* ---------- Slider & button handlers ---------- */
slider.addEventListener('input', () => {
    sliderValueSpan.textContent = slider.value;
});
btnReset.addEventListener('click', () => {
    slider.value = 0;
    sliderValueSpan.textContent = '0';
});

/* ---------- p5.js sketch ---------- */
let labels = [];   // will hold label bounding boxes each frame

function setup(){
    const canvas = createCanvas(600, 500);
    canvas.parent('canvas-container');
    textFont('Arial');
}

function draw(){
    background(202,233,255);  // sky
    labels = [];              // reset label registry each frame
    const phase = int(slider.value);

    /* ----- draw ground ----- */
    noStroke();
    fill('#9B7653');
    rect(0, height/2, width, height/2);

    drawSun();
    drawCloudsAndRain();
    drawRocks();
    drawWater();
    drawPlants();
    drawAnimal();

    /* ----- dynamic processes controlled by slider ----- */
    stroke(0);
    strokeWeight(2);
    fill(0);
    textSize(14);

    if(phase >= 1){
        drawArrow(120,140, 200,300);         // Erosion
        drawProcessLabel('Erosion', 140,210, descriptions.Erosion);
    }
    if(phase >= 2){
        drawArrow(200,330, 100,420);         // Runoff
        drawProcessLabel('Runoff', 140,380, descriptions.Runoff);
    }
    if(phase >= 3){
        drawArrow(100,420, 300,420);         // Soil (horizontal)
        drawProcessLabel('Soil', 190,410, descriptions.Soil);
    }
    if(phase >= 4){
        drawArrow(230,380, 120,440);         // Leaching
        drawProcessLabel('Leaching', 150,420, descriptions.Leaching);
    }
    if(phase >= 5){
        drawArrow(320,420, 450,350);         // Plant uptake
        drawProcessLabel('Plant uptake', 380,390, descriptions['Plant uptake']);
    }
    if(phase >= 6){
        drawArrow(450,340, 300,330);         // Animals eat plants
        drawProcessLabel('Animals eat plants', 360,320, descriptions['Animals eat plants']);
    }
    if(phase >= 7){
        drawArrow(300,340, 320,420);         // Decomposition
        drawProcessLabel('Decomposition', 310,380, descriptions.Decomposition);
    }
    if(phase >= 8){
        drawCurvedArrow();                   // Geologic uplift
        drawProcessLabel('Geologic uplift', 140,300, descriptions['Geologic uplift']);
    }

    /* ----- hover tooltips ----- */
    handleHover();
}

/* ---------- Drawing helper functions ---------- */
function drawSun(){
    noStroke();
    fill('#FFDD00');
    ellipse(520,80,80,80);
    stroke('#FFDD00');
    strokeWeight(3);
    for(let a=0;a<TWO_PI;a+=PI/6){
        const x1 = 520 + cos(a)*50;
        const y1 = 80 + sin(a)*50;
        const x2 = 520 + cos(a)*70;
        const y2 = 80 + sin(a)*70;
        line(x1,y1,x2,y2);
    }
}
function drawCloudsAndRain(){
    noStroke();
    fill('#E0E0E0');
    ellipse(110,80,120,60);
    ellipse(160,80,120,60);
    ellipse(130,60,120,60);

    // label
    fill('#505050');
    textSize(14);
    textAlign(CENTER,CENTER);
    text('Clouds',140,75);
    registerLabel(140-25,75-10,50,20, descriptions.Clouds);

    // rain lines
    stroke('#0000FF');
    strokeWeight(2);
    for(let i=0;i<6;i++){
        const x=90+i*20;
        line(x,100, x-10,130);
    }
}
function drawRocks(){
    noStroke();
    fill('#5A5A5A');
    ellipse(200,330,90,60);
    ellipse(240,330,90,60);
    ellipse(220,300,90,60);
    fill(0);
    textAlign(CENTER,CENTER);
    text('Rocks',220,350);
    registerLabel(220-25,350-10,50,20, descriptions.Rocks);
}
function drawWater(){
    noStroke();
    fill(0,0,255,150);
    ellipse(80,450,160,60);
    ellipse(140,430,160,60);
    fill('#00008B');
    textAlign(CENTER,CENTER);
    text('Water',110,445);
    registerLabel(110-25,445-10,50,20, descriptions.Water);
}
function drawPlants(){
    noStroke();
    fill('#90EE90');
    triangle(450,370, 440,340, 460,340);
    triangle(470,370, 460,340, 480,340);
    triangle(430,370, 420,340, 440,340);
    fill('#006400');
    textAlign(LEFT, CENTER);
    text('Plants',485,355);
    registerLabel(485,355-10,50,20, descriptions.Plants);
}
function drawAnimal(){
    // body
    stroke(0);
    fill('#FFFFFF');
    rect(270,300,60,40,5);
    // spots
    fill(0);
    ellipse(285,315,15,10);
    ellipse(305,330,18,12);
    // head
    fill('#FFFFFF');
    rect(330,315,25,20,3);
    // eye
    fill('#0000FF');
    ellipse(345,325,5,5);
    // legs
    fill(0);
    rect(280,340,6,20);
    rect(300,340,6,20);
    rect(320,340,6,20);
    rect(340,340,6,20);
    // label
    fill(0);
    textAlign(CENTER,CENTER);
    text('Animals',300,370);
    registerLabel(300-30,370-10,60,20, descriptions.Animals);
}
function drawArrow(x1,y1,x2,y2){
    stroke(0);
    strokeWeight(2);
    line(x1,y1,x2,y2);

    // arrow head
    const angle = atan2(y1 - y2, x1 - x2);
    const headSize = 10;
    push();
    translate(x2,y2);
    rotate(angle - HALF_PI);
    fill(0);
    noStroke();
    triangle(0,0, -headSize/2,-headSize, headSize/2,-headSize);
    pop();
}
function drawCurvedArrow(){
    noFill();
    stroke(0);
    strokeWeight(2);
    // simple quadratic curve
    beginShape();
    vertex(80,450);
    quadraticVertex(150,380, 200,300);
    endShape();
    // arrow head at end
    const x2 = 200, y2=300;
    const x1 = 150, y1=380;
    const angle = atan2(y1 - y2, x1 - x2);
    const headSize = 10;
    push();
    translate(x2,y2);
    rotate(angle - HALF_PI);
    fill(0);
    noStroke();
    triangle(0,0, -headSize/2,-headSize, headSize/2,-headSize);
    pop();
}
function drawProcessLabel(txt, x, y, desc){
    fill(0);
    textAlign(CENTER,CENTER);
    text(txt, x, y);
    const w = textWidth(txt);
    registerLabel(x - w/2, y - 10, w, 20, desc);
}
/* ---------- tooltip handling ---------- */
function registerLabel(x,y,w,h,desc){
    labels.push({x, y, w, h, desc});
}
function handleHover(){
    for(const lb of labels){
        if(mouseX >= lb.x && mouseX <= lb.x + lb.w &&
           mouseY >= lb.y && mouseY <= lb.y + lb.h){
            drawTooltip(lb.desc, mouseX, mouseY);
            break; // only show first found
        }
    }
}
function drawTooltip(txt, mx, my){
    textSize(12);
    const padding = 6;
    const tw = textWidth(txt);
    const th = 16;
    let tx = mx + 12;
    let ty = my + 12;
    if(tx + tw + padding*2 > width) tx = mx - tw - padding*2 - 12;
    if(ty + th + padding*2 > height) ty = my - th - padding*2 - 12;

    noStroke();
    fill(255,230);
    rect(tx, ty, tw + padding*2, th + padding*2,5);
    fill(0);
    textAlign(LEFT,TOP);
    text(txt, tx + padding, ty + padding);
}

/* ---------- descriptions for tooltips ---------- */
const descriptions = {
    'Clouds': 'Water vapor condensed in the atmosphere.',
    'Rocks': 'A primary reservoir of phosphorus in the form of phosphate.',
    'Animals': 'Obtain phosphorus by eating plants or other animals.',
    'Plants': 'Absorb phosphate from the soil to build organic molecules.',
    'Water': 'Phosphorus is carried into water bodies by runoff and leaching.',
