        <div id="example-controls">
            <span>example</span>
            <button id="btn-example-1" class="btn active">1</button>
            <button id="btn-example-2" class="btn">2</button>
        </div>

        <!-- Step controls -->
        <div id="step-controls">
            <span>algorithm step</span>
            <!-- Step buttons 1 … 11 -->
            <button id="btn-step-1"  class="btn active">1</button>
            <button id="btn-step-2"  class="btn">2</button>
            <button id="btn-step-3"  class="btn">3</button>
            <button id="btn-step-4"  class="btn">4</button>
            <button id="btn-step-5"  class="btn">5</button>
            <button id="btn-step-6"  class="btn">6</button>
            <button id="btn-step-7"  class="btn">7</button>
            <button id="btn-step-8"  class="btn">8</button>
            <button id="btn-step-9"  class="btn">9</button>
            <button id="btn-step-10" class="btn">10</button>
            <button id="btn-step-11" class="btn">11</button>
        </div>
    </div>

    <!-- ---------- MAIN CONTENT (DESCRIPTION + CANVAS) ---------- -->
    <div id="main-content">
        <p id="description-text">A 28-vertex graph with a 13-edge maximal matching.</p>
        <div id="canvas-container">
            <!-- p5.js canvas is injected here -->
        </div>
    </div>
</div>

<script>
/* ------------------ DEMO DATA ------------------ */
const demoData = {};

// Build Example 1 programmatically: 28 vertices on a circle
(() => {
    const verts = [];
    const edges = [];
    const cx = 300, cy = 225, r = 200;
    const V = 28;

    for(let i = 0; i < V; i++){
        const angle = 2*Math.PI*i/V - Math.PI/2;   // start at the top
        verts.push({x: cx + r*Math.cos(angle), y: cy + r*Math.sin(angle)});
    }
    // simple ring edges
    for(let i = 0; i < V; i++){
        edges.push({u:i, v:(i+1)%V});
    }

    // helper lists of matching edges
    const matching13 = [];
    for(let i=0;i<26;i+=2) matching13.push(i); // even indices 0..24  => 13 edges
    const matching14 = [...matching13, 26];    // add edge (26,27)

    const steps = [
        {description:"A 28-vertex graph with a 13-edge maximal matching.",
         matching:matching13,
         isFinal:false,
         blossom:null},

        {description:"Start search from an exposed vertex.", matching:matching13, isFinal:false, blossom:null},
        {description:"Grow an alternating tree from the exposed vertex.", matching:matching13, isFinal:false, blossom:null},
        {description:"Continue expanding the tree.", matching:matching13, isFinal:false, blossom:null},
        {description:"Even/odd layers identified.", matching:matching13, isFinal:false, blossom:null},
        {description:"A blossom is encountered.", matching:matching13, isFinal:false, blossom:null},
        {description:"Blossom edges inspected.", matching:matching13, isFinal:false, blossom:null},
        {description:"Blossom about to shrink.", matching:matching13, isFinal:false, blossom:null},
        {description:"Blossom shrunk into a super-vertex.", matching:matching13, isFinal:false, blossom:null},

        {description:"Augmenting path found in original graph; augment the matching.",
         matching:matching13,
         isFinal:false,
         blossom:{ vertices:[0,2,4,6,8,10,12], topVertex:0}
        },

        {description:"Matching augmented by one edge; maximum matching has 14 edges.",
         matching:matching14,
         isFinal:true,
         blossom:null}
    ];

    // pad to 11 steps (indices 0..10). We already have 11 items.
    demoData["1"] = {vertices:verts, edges:edges, steps:steps};
})();

/* Example 2 – smaller 12-vertex ring just to have a second example */
(() => {
    const verts = [];
    const edges = [];
    const cx = 300, cy = 225, r = 180;
    const V = 12;
    for(let i=0;i<V;i++){
        const angle = 2*Math.PI*i/V - Math.PI/2;
        verts.push({x: cx + r*Math.cos(angle), y: cy + r*Math.sin(angle)});
    }
    for(let i=0;i<V;i++){
        edges.push({u:i, v:(i+1)%V});
    }

    const match5 = [0,2,4,6,8];
    const match6 = [...match5,10];

    const steps=[];
    steps.push({description:"A 12-vertex graph with a 5-edge maximal matching.",
                matching:match5, isFinal:false, blossom:null});
    steps.push({description:"Select an exposed vertex.", matching:match5, isFinal:false, blossom:null});
    steps.push({description:"Grow alternating tree.", matching:match5, isFinal:false, blossom:null});
    steps.push({description:"Blossom detected.", matching:match5, isFinal:false, blossom:null});
    steps.push({description:"Blossom shrunk.", matching:match5, isFinal:false, blossom:null});
    steps.push({description:"Augmenting path located.", matching:match5, isFinal:false, blossom:null});
    steps.push({description:"Matching augmented to size 6.", matching:match6, isFinal:true,  blossom:null});
    // ensure we have 11 items
    while(steps.length<11){
        steps.push(steps[steps.length-1]);  // duplicate last
    }
    demoData["2"] = {vertices:verts, edges:edges, steps:steps};
})();

/* ------------------ APPLICATION STATE ------------------ */
let currentExample = "1";   // "1" or "2"
let currentStep    = 0;     // 0-based index into steps[]

/* ------------ D.O.M. HELPER FUNCTIONS ------------- */
function deselectGroup(selector){
    document.querySelectorAll(selector).forEach(btn=>btn.classList.remove('active'));
}

function setExample(exampleId){
    if(currentExample === exampleId) return;

    // update button highlight
    deselectGroup('#example-controls .btn');
    document.getElementById('btn-example-'+exampleId).classList.add('active');

    currentExample = exampleId;
    // reset step to 0
    setStep(0, true);   // true = coming from example change
}

function setStep(stepIndex, fromExampleChange=false){
    if(!fromExampleChange && currentStep === stepIndex) return;

    deselectGroup('#step-controls .btn');
    document.getElementById('btn-step-'+(stepIndex+1)).classList.add('active');

    currentStep = stepIndex;
    // update description
    const desc = demoData[currentExample].steps[currentStep].description;
    document.getElementById('description-text').textContent = desc;

    // redraw canvas
    p5Instance.redraw();
}

/* ------------------ REGISTER BUTTON EVENTS ------------------ */
document.getElementById('btn-example-1').addEventListener('click', ()=>setExample("1"));
document.getElementById('btn-example-2').addEventListener('click', ()=>setExample("2"));

for(let i=1;i<=11;i++){
    const idx = i-1;
    document.getElementById('btn-step-'+i)
            .addEventListener('click', ()=>setStep(idx));
}

/* ---------------------- P5.JS SKETCH ---------------------- */
let p5Instance;
const sketch = (p) => {
    p.setup = () =>{
        const canvas = p.createCanvas(600,450);
        canvas.parent('canvas-container');
        p.noLoop();   // we draw only on demand
    };

    p.draw = () =>{
        p.background(255);

        const graph   = demoData[currentExample];
        const step    = graph.steps[currentStep];

        // draw unmatched edges
        p.stroke(200);
        p.strokeWeight(2);
        graph.edges.forEach((e, idx)=>{
            if(step.matching.includes(idx)) return;
            const u = graph.vertices[e.u];
            const v = graph.vertices[e.v];
            p.line(u.x,u.y, v.x,v.y);
        });

        // draw matched edges
        p.stroke(step.isFinal? '#4d4dff' : '#ff4d4d');
        p.strokeWeight(6);
        step.matching.forEach(idx=>{
            const e = graph.edges[idx];
            const u = graph.vertices[e.u];
            const v = graph.vertices[e.v];
            p.line(u.x,u.y, v.x,v.y);
        });

        // draw blossom if present
        if(step.blossom){
            const b = step.blossom;
            p.fill(0,0,255,76);  // ~30% opacity
            p.stroke(0);
            p.strokeWeight(1);
            p.beginShape();
            b.vertices.forEach(vid=>{
                const v = graph.vertices[vid];
                p.vertex(v.x,v.y);
            });
            p.endShape(p.CLOSE);

            // super-vertex
            const tv = graph.vertices[b.topVertex];
            p.fill('#ffff00');
            p.stroke(0);
            p.strokeWeight(1.5);
            p.circle(tv.x, tv.y, 16);
        }

        // draw vertices
        p.noStroke();
        p.fill(0);
        graph.vertices.forEach(v=>{
            p.circle(v.x, v.y, 8);
        });
    };
};

p5Instance = new p5(sketch);

/* --------------- INITIAL STATE (already correct via HTML) --------------- */
// currentExample and currentStep already initialized
// just ensure p5 draws the first frame:
p5Instance.redraw();
</script>
</body>
</html>
