    <!-- Control Panel -->
    <div id="control-panel">
        <!-- Cylinder Radius Slider -->
        <div>
            <label for="slider-cylinder-radius">cylinder radius
                <span id="label-cylinder-radius" class="value-label"></span>
            </label>
            <input type="range" id="slider-cylinder-radius"
                   min="0.5" max="3.0" step="0.05" value="2.1">
        </div>

        <!-- Opacity Slider -->
        <div>
            <label for="slider-opacity">opacity
                <span id="label-opacity" class="value-label"></span>
            </label>
            <input type="range" id="slider-opacity"
                   min="0.0" max="1.0" step="0.01" value="0.95">
        </div>

        <!-- Drill Radius Slider -->
        <div>
            <label for="slider-drill-radius">drill radius
                <span id="label-drill-radius" class="value-label"></span>
            </label>
            <input type="range" id="slider-drill-radius"
                   min="0.1" max="2.5" step="0.05" value="1.35">
        </div>

