    <div id="visualization-container">
        <canvas id="main-canvas"></canvas>
    </div>
</div>

<!-- Three-JS / OrbitControls / CSG  -->
<script type="module">
import * as THREE from 'https://unpkg.com/three@0.158.0/build/three.module.js';
import { OrbitControls } from 'https://unpkg.com/three@0.158.0/examples/jsm/controls/OrbitControls.js';
import { CSG } from 'https://unpkg.com/three-bvh-csg@0.0.16/dist/index.module.js';

/* -------------------------------------------------------------------- */
/*  Slider / checkbox wiring                                            */
/* -------------------------------------------------------------------- */
const sliders = [
    { slider: 'slider-cylinder-radius', label: 'label-cylinder-radius' },
    { slider: 'slider-opacity',         label: 'label-opacity'         },
    { slider: 'slider-drill-radius',    label: 'label-drill-radius'    },
    { slider: 'slider-inclination',     label: 'label-inclination'     },
    { slider: 'slider-rotation',        label: 'label-rotation'        },
    { slider: 'slider-offset',          label: 'label-offset'          }
];

function updateSliderLabel(id) {
    const el  = document.getElementById(id);
    const lbl = document.getElementById(id.replace('slider', 'label'));
    lbl.textContent = Number(el.value).toFixed(2);
}

sliders.forEach(({slider}) => updateSliderLabel(slider));

/* -------------------------------------------------------------------- */
/*  Three.js scene setup                                                */
/* -------------------------------------------------------------------- */
const canvas  = document.getElementById('main-canvas');
const scene   = new THREE.Scene();
scene.background = new THREE.Color(0xe0e0e0);

const camera  = new THREE.PerspectiveCamera(45, 2, 0.1, 100);
camera.position.set(10, 8, 10);

const renderer = new THREE.WebGLRenderer({ canvas, antialias: true });
resizeRenderer();  // initial size

const controls = new OrbitControls(camera, renderer.domElement);
controls.enableDamping = true;

/*  Lighting  */
scene.add(new THREE.AmbientLight(0xffffff, 0.6));
const dirLight = new THREE.DirectionalLight(0xffffff, 0.9);
dirLight.position.set(5, 10, 7);
scene.add(dirLight);

/* Holder for dynamically generated meshes */
let dynamicGroup = null;

/* -------------------------------------------------------------------- */
/*  Geometry / scene regeneration                                       */
/* -------------------------------------------------------------------- */
function updateScene() {

    /* Remove previous build */
    if (dynamicGroup) {
        scene.remove(dynamicGroup);
        // dispose geometries/materials
        dynamicGroup.traverse(obj => {
            if (obj.geometry) obj.geometry.dispose();
            if (obj.material) {
                if (Array.isArray(obj.material)) obj.material.forEach(m => m.dispose());
                else obj.material.dispose();
            }
        });
    }
    dynamicGroup = new THREE.Group();
    scene.add(dynamicGroup);

    /* Read GUI values */
    const R          = +document.getElementById('slider-cylinder-radius').value; // main cylinder radius
    const opacity    = +document.getElementById('slider-opacity').value;
    const r          = +document.getElementById('slider-drill-radius').value;    // drill radius
    const incl       = +document.getElementById('slider-inclination').value;
    const rot        = +document.getElementById('slider-rotation').value;
    const offset     = +document.getElementById('slider-offset').value;
    const boundaryOnly = document.getElementById('checkbox-boundary-only').checked;

    /* -------------------------------------------------- */
    /*  Main cylinder (vertical)                          */
    /* -------------------------------------------------- */
    const mainGeo  = new THREE.CylinderGeometry(R, R, 8, 128, 1);
    const mainMat  = new THREE.MeshStandardMaterial({
        color: 0xD2B48C,
        transparent: true,
        opacity: boundaryOnly ? 0.15 : opacity,
        roughness: 0.35
    });
    const mainMesh = new THREE.Mesh(mainGeo, mainMat);

    /* -------------------------------------------------- */
    /*  Drill cylinder                                    */
    /* -------------------------------------------------- */
    const drillGeo = new THREE.CylinderGeometry(r, r, 12, 64, 1);
    const drillMat = new THREE.MeshStandardMaterial({ color: 0x888888, opacity: 0.4, transparent: true });
    const drill    = new THREE.Mesh(drillGeo, drillMat);

    // Orient & position the drill
    drill.rotation.z = Math.PI / 2;       // make it horizontal along X
    drill.rotation.x += incl;             // inclination
    drill.rotation.y += rot;              // yaw
    drill.position.y  = offset;           // vertical offset

    /* -------------------------------------------------- */
    /*  CSG operation                                     */
    /* -------------------------------------------------- */
    let resultMesh;
    try {
        resultMesh = CSG.subtract(mainMesh.clone(), drill.clone());
        resultMesh.material = new THREE.MeshStandardMaterial({
            color: 0xD2B48C,
            transparent: true,
            opacity: opacity,
            roughness: 0.35
        });
    } catch (e) {
        console.error('CSG failed:', e);
        // fall back to showing original meshes if CSG fails
        resultMesh = mainMesh.clone();
    }

    if (!boundaryOnly) dynamicGroup.add(resultMesh);
    else                dynamicGroup.add(mainMesh);     // ghosted guide

    /* -------------------------------------------------- */
    /*  Boundary curve extraction (approx.)               */
    /* -------------------------------------------------- */
    const curves = computeBoundaryCurves(drill, R);
    if (curves.front.length > 2) {
        addTube(curves.front, 0xFFA500);         // orange
    }
    if (curves.back.length > 2) {
        addTube(curves.back, 0x4169E1);          // royal blue
    }
}

/* ------------- Helpers for boundary curve --------------------------- */
function computeBoundaryCurves(drillMesh, mainRadius) {

    // clone & bake drill vertices into world space
    const geo = drillMesh.geometry.clone();
    geo.applyMatrix4(drillMesh.matrixWorld);

    const posAttr  = geo.getAttribute('position');
    const frontPts = [];
    const backPts  = [];
    const v = new THREE.Vector3();
    const EPS = 0.02;          // tolerance for being "on" surface

    for (let i = 0; i < posAttr.count; i++) {
        v.fromBufferAttribute(posAttr, i);
        const radial = Math.hypot(v.x, v.z);
        if (Math.abs(radial - mainRadius) < EPS) {
            (v.x >= 0 ? frontPts : backPts).push(v.clone());
        }
    }

    // Sort points around the vertical axis so the tube follows an orderly path
    const byAngle = (a, b) => Math.atan2(a.z, a.x) - Math.atan2(b.z, b.x);
    frontPts.sort(byAngle);  backPts.sort(byAngle);

    return { front: frontPts, back: backPts };
}

function addTube(points, color) {
    const curve = new THREE.CatmullRomCurve3(points, true);
    const tube  = new THREE.TubeGeometry(curve, 512, 0.05, 8, true);
    const mat   = new THREE.MeshStandardMaterial({ color });
    const mesh  = new THREE.Mesh(tube, mat);
    dynamicGroup.add(mesh);
}

/* -------------------------------------------------------------------- */
/*  Event wiring                                                        */
/* -------------------------------------------------------------------- */
sliders.forEach(({slider}) => {
    const s = document.getElementById(slider);
    s.addEventListener('input', () => {
        updateSliderLabel(slider);
        updateScene();
    });
});
document.getElementById('checkbox-boundary-only')
        .addEventListener('change', updateScene);

/* -------------------------------------------------------------------- */
/*  Resize handling                                                     */
/* -------------------------------------------------------------------- */
window.addEventListener('resize', resizeRenderer);
function resizeRenderer() {
    const cont      = document.getElementById('visualization-container');
    const width     = cont.clientWidth;
    const height    = cont.clientHeight;
    renderer.setSize(width, height, false);
    camera.aspect = width / height;
    camera.updateProjectionMatrix();
}

/* -------------------------------------------------------------------- */
/*  Animation loop                                                      */
/* -------------------------------------------------------------------- */
function animate() {
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene, camera);
}

/* -------------------------------------------------------------------- */
/*  Kick-off                                                            */
/* -------------------------------------------------------------------- */
updateScene();
animate();

</script>
</body>
</html>
