            <div id="canvas-brightness-parent"></div>
            <div id="arrow-brightness" class="arrow">↑</div>
        </div>
        <div class="bar-wrapper">
            <div id="canvas-result-parent"></div>
        </div>
    </div>
</main>

<script>
window.addEventListener("DOMContentLoaded", ()=>{

    /* ---------- CONSTANTS ---------- */
    const BAR_HEIGHT = 40;
    const CORNER_RADIUS = 10;

    /* ---------- DOM ELEMENTS ---------- */
    const sliderHue = document.getElementById("slider-hue");
    const sliderSat = document.getElementById("slider-saturation");
    const sliderBri = document.getElementById("slider-brightness");

    const valueHue   = document.getElementById("value-hue");
    const valueSat   = document.getElementById("value-saturation");
    const valueBri   = document.getElementById("value-brightness");

    const arrowHue = document.getElementById("arrow-hue");
    const arrowSat = document.getElementById("arrow-saturation");
    const arrowBri = document.getElementById("arrow-brightness");

    const btnReset = document.getElementById("btn-reset");

    /* ---------- P5 SKETCHES ---------- */
    // Hue bar (static)
    let hueSketch = new p5( sketch => {
        sketch.setup = ()=>{
            let width = sketch.selectParentWidth();
            let cnv = sketch.createCanvas(width, BAR_HEIGHT);
            cnv.parent("canvas-hue-parent");
            sketch.colorMode(sketch.HSB,1,1,1);
            sketch.noLoop();
        };
        sketch.draw = ()=>{
            let w = sketch.width;
            let h = sketch.height;
            sketch.noStroke();
            for(let x=0;x<w;x++){
                let hu = x/w;
                sketch.stroke(hu,1,1);
                sketch.line(x,0,x,h);
            }
            // rounded mask effect
            sketch.noFill();
            sketch.strokeWeight(0);
            sketch.rect(0,0,w,h,CORNER_RADIUS);
        };
        // Helper to get parent's width (after DOM ready)
        sketch.selectParentWidth = ()=>{
            let parent = document.getElementById("canvas-hue-parent");
            return parent.clientWidth || 360;
        }
    });

    // Saturation bar (dynamic)
    let satSketch = new p5( sketch=>{
        sketch.setup = ()=>{
            let width = sketch.selectParentWidth();
            let cnv = sketch.createCanvas(width, BAR_HEIGHT);
            cnv.parent("canvas-saturation-parent");
            sketch.colorMode(sketch.HSB,1,1,1);
            sketch.noLoop();
            sketch.redraw();
        };
        sketch.draw = ()=>{
            let w = sketch.width;
            let h = sketch.height;
            let H = parseFloat(sliderHue.value);
            let B = parseFloat(sliderBri.value);
            for(let x=0;x<w;x++){
                let s = x/w;
                sketch.stroke(H,s,B);
                sketch.line(x,0,x,h);
            }
            sketch.noFill();
            sketch.strokeWeight(0);
            sketch.rect(0,0,w,h,CORNER_RADIUS);
        };
        sketch.selectParentWidth = ()=>{
            let parent = document.getElementById("canvas-saturation-parent");
            return parent.clientWidth || 360;
        }
    });

    // Brightness bar (dynamic)
    let briSketch = new p5( sketch=>{
        sketch.setup = ()=>{
            let width = sketch.selectParentWidth();
            let cnv = sketch.createCanvas(width, BAR_HEIGHT);
            cnv.parent("canvas-brightness-parent");
            sketch.colorMode(sketch.HSB,1,1,1);
            sketch.noLoop();
            sketch.redraw();
        };
        sketch.draw = ()=>{
            let w = sketch.width;
            let h = sketch.height;
            let H = parseFloat(sliderHue.value);
            let S = parseFloat(sliderSat.value);
            for(let x=0;x<w;x++){
                let b = x/w;
                sketch.stroke(H,S,b);
                sketch.line(x,0,x,h);
            }
            sketch.noFill();
            sketch.strokeWeight(0);
            sketch.rect(0,0,w,h,CORNER_RADIUS);
        };
        sketch.selectParentWidth = ()=>{
            let parent = document.getElementById("canvas-brightness-parent");
            return parent.clientWidth || 360;
        }
    });

    // Result bar (dynamic solid)
    let resultSketch = new p5( sketch=>{
        sketch.setup = ()=>{
            let width = sketch.selectParentWidth();
            let cnv = sketch.createCanvas(width, BAR_HEIGHT);
            cnv.parent("canvas-result-parent");
            sketch.colorMode(sketch.HSB,1,1,1);
            sketch.noLoop();
            sketch.redraw();
        };
        sketch.draw = ()=>{
            let w = sketch.width;
            let h = sketch.height;
            let H = parseFloat(sliderHue.value);
            let S = parseFloat(sliderSat.value);
            let B = parseFloat(sliderBri.value);
            sketch.noStroke();
            sketch.fill(H,S,B);
            sketch.rect(0,0,w,h,CORNER_RADIUS);
        };
        sketch.selectParentWidth = ()=>{
            let parent = document.getElementById("canvas-result-parent");
            return parent.clientWidth || 360;
        }
    });

    /* ---------- UTILITY FUNCTIONS ---------- */
    function updateVisuals(){
        const H = parseFloat(sliderHue.value);
        const S = parseFloat(sliderSat.value);
        const B = parseFloat(sliderBri.value);

        // update numeric displays (round to 3 decimals)
        valueHue.textContent = H.toFixed(3);
        valueSat.textContent = S.toFixed(3);
        valueBri.textContent = B.toFixed(3);

        // redraw necessary bars
        satSketch.redraw();
        briSketch.redraw();
        resultSketch.redraw();

        // reposition arrows
        positionArrow(arrowHue , H , hueSketch);
        positionArrow(arrowSat , S , satSketch);
        positionArrow(arrowBri , B , briSketch);
    }

    function positionArrow(arrowElem, value0to1, sketchInstance){
        let canvasWidth = sketchInstance.width;
        let arrowWidth  = arrowElem.offsetWidth || 16;
        let leftPx = value0to1 * canvasWidth - arrowWidth/2;
        arrowElem.style.left = `${leftPx}px`;
    }

    /* ---------- EVENT LISTENERS ---------- */
    sliderHue.addEventListener("input", updateVisuals);
    sliderSat.addEventListener("input", updateVisuals);
    sliderBri.addEventListener("input", updateVisuals);

    btnReset.addEventListener("click", ()=>{
        sliderHue.value = 0;
        sliderSat.value = 0.5;
        sliderBri.value = 1;

        updateVisuals();
    });

    /* ---------- INITIAL RENDER ---------- */
    // wait a tiny bit for p5 canvases to finish sizing
    setTimeout(()=>{ updateVisuals(); },100);

});
</script>
</body>
</html>
