        min-height:0;       /* important for flexbox + Plotly */
    }
</style>
</head>
<body>
<div id="app">
    <!-- CONTROL PANEL -->
    <div id="controls-container">
        <!-- α -->
        <div class="control-row">
            <label for="slider-alpha">α</label>
            <input type="range" id="slider-alpha" min="0.01" max="0.2" step="0.0001" value="0.05">
            <span id="value-alpha">0.0500</span>
        </div>
        <!-- β -->
        <div class="control-row">
            <label for="slider-beta">β</label>
            <input type="range" id="slider-beta" min="1" max="20" step="0.01" value="5">
            <span id="value-beta">5.00</span>
        </div>
        <!-- γ -->
        <div class="control-row">
            <label for="slider-gamma">γ</label>
            <input type="range" id="slider-gamma" min="100" max="2000" step="1" value="500">
            <span id="value-gamma">500</span>
        </div>
    </div>

    <!-- PLOT AREA -->
    <div id="plot-container"></div>
</div>

<script>
/* ------------------------------
   Helper: 4th-order Runge–Kutta
--------------------------------*/
function solveODE(alpha, beta, gamma){
    const h = 0.05;
    const tMax = 20;
    const steps = Math.round(tMax / h);
    const t = new Array(steps+1);
    const dT = new Array(steps+1);
    const C  = new Array(steps+1);

    // initial conditions
    t[0]=0;
    dT[0]=0;   // ΔT(0)
    C[0]=0;    // C_X(0)

    function derivatives(T, CX){
        const expTerm = Math.exp( T / (1 + T/alpha) );
        const dTdt = gamma * CX * expTerm - beta * T;
        const dCdt = 1 - CX * expTerm;
        return [dTdt, dCdt];
    }

    for(let i=0;i<steps;i++){
        const currT = t[i];
        const currY1 = dT[i];
        const currY2 = C[i];

        const [k1y1, k1y2] = derivatives(currY1, currY2);

        const [k2y1, k2y2] = derivatives(
            currY1 + 0.5*h*k1y1,
            currY2 + 0.5*h*k1y2
        );

        const [k3y1, k3y2] = derivatives(
            currY1 + 0.5*h*k2y1,
            currY2 + 0.5*h*k2y2
        );

        const [k4y1, k4y2] = derivatives(
            currY1 + h*k3y1,
            currY2 + h*k3y2
        );

        const nextY1 = currY1 + (h/6)*(k1y1 + 2*k2y1 + 2*k3y1 + k4y1);
        const nextY2 = currY2 + (h/6)*(k1y2 + 2*k2y2 + 2*k3y2 + k4y2);

        t[i+1]   = currT + h;
        dT[i+1]  = nextY1;
        C[i+1]   = nextY2;
    }

    return {t, dT, C};
}

/* ------------------------------
   Plotting
--------------------------------*/
function makePlot(alpha, beta, gamma){
    const {t, dT, C} = solveODE(alpha, beta, gamma);
    const scaledC = C.map(c=>c*1000);

    const trace1 = {
        x: t,
        y: dT,
        mode:'lines',
        line:{color:'orange', width:2}
    };

    const trace2 = {
        x: t,
        y: scaledC,
        mode:'lines',
        line:{color:'steelblue', width:2}
    };

    const layout = {
        xaxis:{range:[0,20], zeroline:true},
        yaxis:{range:[0,100], zeroline:true},
        showlegend:false,
        margin:{l:50,r:20,b:30,t:20},
        plot_bgcolor:'#f0f0f0',
        paper_bgcolor:'white',
        annotations:[
            {
                text:'ΔT',
                x:-0.05, xref:'paper',
                y:100,   yref:'y',
                xanchor:'right', yanchor:'top',
                showarrow:false,
                font:{color:'purple',size:12}
            },
            {
                text:'1000 C<sub>X</sub>',
                x:-0.05, xref:'paper',
                y:95,    yref:'y',
                xanchor:'right', yanchor:'top',
                showarrow:false,
                font:{color:'purple',size:12}
            }
        ]
    };

    Plotly.react('plot-container',[trace1,trace2],layout,{responsive:true});
}

/* ------------------------------
   Slider Handling
--------------------------------*/
const sliderAlpha = document.getElementById('slider-alpha');
const sliderBeta  = document.getElementById('slider-beta');
const sliderGamma = document.getElementById('slider-gamma');

const valueAlpha  = document.getElementById('value-alpha');
const valueBeta   = document.getElementById('value-beta');
const valueGamma  = document.getElementById('value-gamma');

function updateDisplays(){
    valueAlpha.textContent = Number(sliderAlpha.value).toFixed(4);
    valueBeta.textContent  = Number(sliderBeta.value).toFixed(2);
    valueGamma.textContent = Number(sliderGamma.value).toFixed(0);
}

function handleInput(){
    updateDisplays();
    makePlot(
        Number(sliderAlpha.value),
        Number(sliderBeta.value),
        Number(sliderGamma.value)
    );
}

sliderAlpha.addEventListener('input',handleInput);
sliderBeta .addEventListener('input',handleInput);
sliderGamma.addEventListener('input',handleInput);

/* ------------------------------
   Initial Draw
--------------------------------*/
window.addEventListener('load', ()=>{
    updateDisplays();
    handleInput();    // draws first plot
});
</script>
</body>
</html>
